import {createStore} from 'redux';

const initialState = {
    activeModel: "",
    activeSystem: ""
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "HIGHLIGHT":
            return Object.assign({}, state, {activeSystem: action.system})
        default:
            return state;
    }
}

const store = createStore(reducer);

export default store;
