import React, { Component } from "react";
import { Collapse, Modal, ModalBody, Input, Label } from "reactstrap";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import subsystem from "../../../_assets/images/subsystem.png";
import services from "../../../_assets/images/services.png";
import reference from "../../../_assets/images/reference.png";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { connect } from 'react-redux';
import { fetchModel } from '../../../store/model/modelActions'
import { fetchSystemDetils } from '../../../store/system/systemActions'

function collect(props) {
    if (props.itemEntity)
        return { item:props.item, itemName: props.itemName, itemId: props.itemId, itemEntity: props.itemEntity };
    else
        return { item:props.item, itemName: props.itemName, itemId: props.itemId };
}

class Systems extends Component {
    constructor(props) {
        super(props);

        this.addUiToggle = this.addUiToggle.bind(this);
        this.resetAddUiToggle = this.resetAddUiToggle.bind(this);
        this.systemMenuToggle = this.systemMenuToggle.bind(this);
        this.flowsMenuToggle = this.flowsMenuToggle.bind(this);
        this.systemToggle = this.systemToggle.bind(this);
        this.flowsToggle = this.flowsToggle.bind(this);
        this.subSystemToggle = this.subSystemToggle.bind(this);
        this.systemSelected = this.systemSelected.bind(this);
        this.close = this.close.bind(this);
        this.modalToggle = this.modalToggle.bind(this);
        this.state = {
            modal: false,
            flowsCollapse: true,
            systemsCollapse: true,
            collapseSystem: false,
            collapseSubSystem: false,
            currentSystemIndex: "",
            currentSubSystemIndex: "",
            currentFlowIndex: "",
            systemData: { system: "" },
            showsystemDetail: false,
            showflowDetail: false,
            systemDetails: {},
            flowDetails: {},
            system: [],
            flow: [],
            searchString: "",
            ids: [],
            search: false,
            item:[],
            itemId: '',
            itemName: '',
            itemValue: '',
            itemType: '',
            itemEntity: '',
            systemAdd: false,
            flowAdd: false,
            systemId: '',
            servicesList: '',
            serviceId: '',
            testData: '',
        };

    }

   //manage add more system/flow text box to ui
    addUiToggle(event, type) {
        if (type === 'system') {
            this.setState(state => ({ systemAdd: !state.systemAdd, itemEntity: 'system' }));
        } else {
            this.setState(state => ({ flowAdd: !state.flowAdd, itemEntity: 'flow' }));
        }

    }

    //setting entity while add more systems/flows
    resetAddUiToggle(event, type) {
        if (type === 'system') {
            this.setState(state => ({ itemEntity: 'system' }));
        } else {
            this.setState(state => ({ itemEntity: 'flow' }));
        }

    }

    //setting system id on click
    systemSelected(systemId) {
        this.props.systemSelected(systemId);
    }

    close(menu, type) {
        this.props.close(menu, type);
    }

    systemMenuToggle() {
        this.setState(state => ({ systemsCollapse: !state.systemsCollapse }));
    }
    flowsMenuToggle() {
        this.setState(state => ({ flowsCollapse: !state.flowsCollapse }));
    }

    flowsToggle(flowId) {

        if (
            this.state.currentFlowIndex === flowId ||
            (!this.state.showflowDetail && this.state.search)
        ) {
            this.setState({ search: false })
            this.setState({ currentFlowIndex: 0 })
        } else {
            this.setState({ currentFlowIndex: flowId });

        }
        this.props.onFlowSelect(flowId)
    }

    //system listing toggle
    systemToggle(systemId) {
        console.log(this.props.details)
        if (
            this.state.currentSystemIndex === systemId ||
            (!this.state.showsystemDetail && this.state.search)
        ) {
            this.setState({ search: false })
            this.setState({ currentSystemIndex: 0 });
            //this.systemSelected(systemId);
        } else {
            this.setState({ currentSystemIndex: systemId });
            //this.systemSelected(systemId);
        }
        this.props.onHighlightClick(this.props.activeModel, systemId);
    }

    //sub system listing toggle
    subSystemToggle(systemId) {
        this.setState({ collapseSubSystem: !this.state.collapseSubSystem });
        if (this.state.currentSubSystemIndex === systemId) {
            this.setState({ currentSubSystemIndex: 0 })
            this.systemSelected(systemId);

        } else {
            this.setState({ currentSubSystemIndex: systemId });
            this.systemSelected(systemId);
        }
    }

    //create entity 
    createEntity(event) {
        console.log(this.props.details);
        let value = event.target.value;
        if (event.key === 'Enter') {
            event.target.value = '';
            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity}`,
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    return fetch(
                        process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity}/` + responseJson.id,
                        {
                            method: "PATCH",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({ name: value })
                        }
                    )
                    .then(response => this.props.getSystemAndFlows(this.props.activeModel))
                });
        }
    }


    //create service
    createService(event) {
        let value = event.target.value;
        if ((event.key === 'Enter' || event.type === 'blur') && value) {
            event.target.value = '';
            this.setState(prevState => ({
                modal: !prevState.modal
            }));

            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/system/${this.state.itemId}/service`,
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    return fetch(
                        process.env.REACT_APP_API + `${this.props.activeModel}/service/` + responseJson.id,

                        {
                            method: "PATCH",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({ name: value })
                        }
                    )
                        .then(response => {
                            this.props.getSystemAndFlows(this.props.activeModel)
                            this.props.updateSystemDetails(this.props.activeModel, this.state.itemId)
                        }

                         )
                });
        }
    }

    //create service
    createReference(event) {
        let value = event.target.value;
        if ((event.key === 'Enter' || event.type === 'blur') && value && this.state.serviceId) {
            event.target.value = '';
            this.setState(prevState => ({
                modal: !prevState.modal
            }));

            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/system/${this.state.itemId}/reference/${this.state.serviceId}`,
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify({ name: value })
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    return fetch(
                        process.env.REACT_APP_API + `${this.props.activeModel}/reference/` + responseJson.id,

                        {
                            method: "PATCH",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({ name: value })
                        }
                    )
                    .then(response => 
                        this.props.getSystemAndFlows(this.props.activeModel)
                    )
                });
        }
    }


    //create entity 
    createSubSyetem(event) {
        let value = event.target.value;
        if ((event.key === 'Enter' || event.type === 'blur') && value) {
            event.target.value = '';
            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity}/${this.state.itemId}/${this.state.itemEntity}`,
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    return fetch(
                        process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity}/` + responseJson.id,
                        {
                            method: "PATCH",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({ name: value })
                        }
                    )
                    .then(response => 
                        this.props.getSystemAndFlows(this.props.activeModel)
                    )
                });
        }
    }

    //Delete entity
    deleteEntity() {
        //console.log(this.state.item.services);
        if(this.state.itemEntity === 'system' && (this.state.item.services.length > 0 || this.state.item.references.length > 0))  {
            this.setState(prevState => ({
                modal: !prevState.modal
            }));
            this.state.itemType = 'deleteFailed';
            this.setState({modal: true })
        } else {
            return fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity === 'flow' ? 'flow' : 'modeled'}/${this.state.itemId}`,
                {
                    method: "DELETE",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => {
                    this.setState(prevState => ({
                        modal: !prevState.modal
                    }));
                    this.props.getSystemAndFlows(this.props.activeModel)
                });
        }
   }

    //update item value for edit
    renameHandleChange = (e) => {
        this.setState({ itemName: e.target.value });
    }

    //Update entity
    updateEntity(event) {
        let value = event.target.value;
        if ((event.key === 'Enter' || event.type === 'blur') && this.state.modal && value) { 
            event.target.value = '';
            this.setState(prevState => ({
                modal: !prevState.modal
            }));
            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/${this.state.itemEntity}/` + this.state.itemId,
                {
                    method: "PATCH",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify({ name: value })
                }
            )
            .then(response => {
                this.props.getSystemAndFlows(this.props.activeModel)
                this.props.updateSystemDetails(this.props.activeModel, this.state.itemId)
            }
         )
        }

    }

    //control right click modal
    modalToggle(e, data) {
        //console.log(data)
        this.setState(prevState => ({
            modal: !prevState.modal
        }));

        if (data) {
            console.log(data)
            this.setState({
                item: data.item,
                itemId: data.itemId,
                itemName: data.itemName,
                itemType: data.itemType,
                itemEntity: data.itemEntity
            })

        }
    }

    //get systems and services list for adding new reference
    getSystems(event) {
        let index = event.target.selectedIndex;
        let id = event.target[index].id
        //this.setState({ systemId: id })
        let parents = this.props.model.systems;
        let parent = parents.filter(parent => parent.name === event.target.value);
        if(parent)
        this.setState({ servicesList: parent[0].services });
    }

    serviceChangeHandle(event) {
        this.setState({ serviceId: event.target.value })
    }


    subSystem(item) {
        if (item) {
            return fetch(
                process.env.REACT_APP_API + "/model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/system1/" + item,
                {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    this.setState({ testData: responseJson });
                })
        }

    }

