function mapDispatchToProps(dispatch) {
    return {
        onHighlightClick: (modelId, systemId) => {
            const action = { type: "HIGHLIGHT", system: systemId };
            dispatch(fetchSystemDetils(modelId, systemId));
            dispatch(action);
        },
        onFlowSelect: (flowId) => {

            const action = { type: "FLOW_SELECT", flowId: flowId };
            dispatch(action);
        },
        getSystemAndFlows: (modelId) => {
            const action = { type: "SELECTMODEL", model: modelId };
            dispatch(fetchModel(modelId));
            dispatch(action);
          },
          updateSystemDetails: (modelId, systemId) => {
            dispatch(fetchSystemDetils(modelId, systemId));
          }
    }
}

function mapStateToProps(state){
    return{
      activeModel: state.model.modelUri,
      activeDomain: state.model.domain,
      model: state.model.items,
      details: state.model.details
    }
  }

export default connect(mapStateToProps, mapDispatchToProps)(Systems);
