import React, { Component } from "react";
import { Col } from "reactstrap";
import photo from "../../_assets/images/photo.jpg";
import Organisation from "../../pages/dashboard/browsers/Organisation";
import Systems from "../../pages/dashboard/browsers/Systems";
import Tagger from "../../pages/dashboard/browsers/Tagger";
import OrganisationDetails from "../../pages/dashboard/browsers/OrganisationDetails";

export default class Header extends Component {
  constructor() {
    super();

    this.state = {
      organisationVisible: false,
      systemsVisible: false,
      taggerVisible: false,
      organisationDetailsVisible: false
    };

    this.organisationhandleClick = this.organisationhandleClick.bind(this);
    this.organisationhandleOutsideClick = this.organisationhandleOutsideClick.bind(
      this
    );
    this.systemshandleClick = this.systemshandleClick.bind(this);
    this.systemshandleOutsideClick = this.systemshandleOutsideClick.bind(this);
    this.taggerhandleClick = this.taggerhandleClick.bind(this);
    this.taggerhandleOutsideClick = this.taggerhandleOutsideClick.bind(this);
    this.organisationDetailshandleClick = this.organisationDetailshandleClick.bind(this);
    this.organisationDetailshandleOutsideClick = this.organisationDetailshandleOutsideClick.bind(this);
  }
  handleClick() {
    if (!this.state.organisationVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }
    if (!this.state.systemsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }
    if (!this.state.taggerVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }
    if (!this.state.organisationDetailsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      organisationVisible: !prevState.organisationVisible
    }));
    this.setState(prevState => ({
      systemsVisible: !prevState.systemsVisible
    }));
    this.setState(prevState => ({
      taggerVisible: !prevState.taggerVisible
    }));
    this.setState(prevState => ({
      organisationDetailsVisible: !prevState.organisationDetailsVisible
    }));
  }
  organisationhandleClick() {
    if (!this.state.organisationVisible) {
      //attach/remove event handler
      document.addEventListener("click", this.organisationhandleClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.organisationhandleClick,
        false
      );
    }

    this.setState(prevState => ({
      organisationVisible: !prevState.organisationVisible
    }));
  }
  systemshandleClick() {
    if (!this.state.systemsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.systemshandleOutsideClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.systemshandleOutsideClick,
        false
      );
    }

    this.setState(prevState => ({
      systemsVisible: !prevState.systemsVisible
    }));
  }

  taggerhandleClick() {
    if (!this.state.taggerVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.taggerhandleOutsideClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.taggerhandleOutsideClick,
        false
      );
    }

    this.setState(prevState => ({
      taggerVisible: !prevState.taggerVisible
    }));
  }
  organisationDetailshandleClick() {
    if (!this.state.organisationDetailsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.organisationDetailshandleOutsideClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.organisationDetailshandleOutsideClick,
        false
      );
    }

    this.setState(prevState => ({
      organisationDetailsVisible: !prevState.taggerVisible
    }));
  }
  organisationhandleOutsideClick(e) {
    // ignore clicks on the component itself
    if (this.node.contains(e.target)) {
      return;
    }

    this.organisationhandleClick();
  }
  systemshandleOutsideClick(e) {
    // ignore clicks on the component itself
    if (this.node.contains(e.target)) {
      return;
    }

    this.systemshandleClick();
  }
  taggerhandleOutsideClick(e) {
    // ignore clicks on the component itself
    if (this.node.contains(e.target)) {
      return;
    }

    this.taggerhandleClick();
  }
  render() {
    return (
      <div
        className="dashboard-header"
        ref={node => {
          this.node = node;
        }}
      >
        <div className="d-flex text-white">
          <Col md="3">
            <ul className="first-menu">
              <li>
                <button onClick={this.organisationhandleClick}>
                  <span className="icon icon-Users menu-icon" />
                </button>
              </li>
              <li>
                <button onClick={this.systemshandleClick}>
                  <span className="icon icon-EmptyBox menu-icon" />
                </button>
              </li>
            </ul>
          </Col>
          <div className="col-md-4">
            <ul className="second-menu">
              <li>
                <button onClick={this.taggerhandleClick}>
                  <span className="icon icon-Floppy menu-icon" />
                </button>
              </li>
              <li>
                <button>
                  <span className="icon icon-User menu-icon" />
                </button>
              </li>
              <li>
                <button>
                  <span className="icon icon-FileBox menu-icon" />
                </button>
              </li>
              <li>
                <button>
                  <span className="icon icon-Share menu-icon" />
                </button>
              </li>
            </ul>
          </div>
          <div className="col-md-1">
            <ul className="third-menu">
              <li>
                <button>
                  <span className="icon icon-WorldWide menu-icon" />
                </button>
              </li>
            </ul>
          </div>
          <div className="col-md-4">
            <ul className="fourth-menu">
              <li>
                <button>
                  <img src={photo} alt="starlify" className="photo-icon" />
                </button>
              </li>
              <li className="user-avatar">
                Entiros <i class="fas fa-chevron-down m-l-15" />
              </li>
              <li>
                <button>
                  <span className="icon icon-Search menu-icon" />
                </button>
              </li>
            </ul>
          </div>
        </div>
        <div className="content d-flex">
          {this.state.organisationVisible && (
            <div className="organisationVisible col-md-4">
              <Organisation />
            </div>
          )}
          {this.state.systemsVisible && (
            <div className="systemVisible col-md-4">
              <Systems />
            </div>
          )}
          {this.state.taggerVisible && (
            <div className="taggerVisible col-md-4">
              <Tagger />
            </div>
          )}
        </div>
      </div>
    );
  }
}
