import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import {toggleFullScreen} from './_helpers/shortkeys'

import Home from './pages/home/Home';
import Dashboard from './pages/dashboard/Dashboard';
import './_assets/icons/strokegap/icons.css';
import './_assets/icons/fontawsome/css/all.min.css';
import './_assets/sass/main.scss';

class App extends Component {
  constructor(){
    super();
    this.forceUpdateHandler = this.forceUpdateHandler.bind(this);
  }

  forceUpdateHandler(){
    this.forceUpdate();
  }

  componentDidMount(){
    document.addEventListener("keydown", this.handleKeyDown.bind(this))
  }

  handleKeyDown(e) {
    if (e.which === 70) {
      toggleFullScreen();
    }
    if (e.which === 67) {
      window.location.reload(true);
    }
  }

  render() {
    return (
      <div className="App">
        <Router basename = { process.env.REACT_APP_BASE || '' } >
          <Switch>
            <Route exact path="/home" component={Home} />
            <Route exact path="/" component={Dashboard} />
          </Switch>
        </Router>
      </div>
    )
  }
}

export default App;
