import React, {useState, useEffect} from 'react';
import useBooleanState from "../hooks/useBooleanState";
import LocalStorage from "../lib/utilities/storage/LocalStorage";
const UserContext = React.createContext();

function UserProvider(props) {
    let storedUser = LocalStorage.getItem('user', {});
    let isAuthenticated = LocalStorage.getItem('is-authenticated', false);
    let [user, setUser] = useState(storedUser);
    let [authenticated, setAuthenticated] = useBooleanState(isAuthenticated);
    useEffect(() => {
        LocalStorage.setItem('is-authenticated', authenticated);
    }, [authenticated]);
    return <UserContext.Provider
        value={{
            state: {
                fetching: false,
                authenticated,
                user,
            },
            setUser,
            setAuthenticated
        }}
    >
        {props.children}
    </UserContext.Provider>;
}
export {UserContext, UserProvider};
