import {
	FETCH_MODEL_BEGIN,
	FETCH_MODEL_SUCCESS,
	FETCH_MODEL_FAILURE
} from "./modelActions";

const initialState = {
	domain: "",
	modelUri: "",
	activeSystem: "",
	items: [],
	loading: false,
	error: null,
	status: null,
	type: null
};

export default function modelReducer( state = initialState, action ){

	switch (action.type) {
		case "SELECTDOMAIN":
			return{
				...state,
				domain: action.domain,
				type: action.type
			};
		case "SELECTMODEL":
			return{
				...state,
				modelUri: action.model,
				type: action.type
			};
		case "HIGHLIGHT":
			return{
				...state,
				activeSystem: action.system,
				type: action.type
			};

		case FETCH_MODEL_BEGIN:
			return {
				...state,
				loading: true,
				error: null,
				type: action.type
			};

		case FETCH_MODEL_SUCCESS:
			return {
				...state,
				loading: false,
				items: action.payload.model,
				status: "success",
				type: action.type
			};

		case FETCH_MODEL_FAILURE:
			return {
				...state,
				loading: false,
				error: action.payload.error,
				items: [],
				type: action.type
			};

		default:
			return state;
	}
}
