function convertData(data = [], id = 'id', parent = null) {
    let nodes = [];
    let links = [];

    if (data.length > 0) {
        console.log('convertData', data);
        let traverseData = [...data];

        traverseData.forEach(row => {
            let newRow = {...row};
            let {subSystems = []} = newRow;
            delete newRow.subSystems;
            // sub systems
            let {nodes: newNodes = [], links: newLinks = []} = convertData(subSystems, id, newRow);
            links = links.concat(newLinks);
            nodes = nodes.concat(newNodes);
            // references

            nodes.push({
                ...newRow
            });
            if (parent) {
                links.push({
                    source: parent[id],
                    target: newRow[id],
                });
            }
        });
    }

    return {
        nodes,
        links
    };
}
