import {useState} from "react";

export default function useBooleanState(initState = true) {
    let [boolean, setBoolean] = useState(initState ? true : false);
    const setBooleanState = bool => setBoolean(bool ? true : false);
    const toggleBoolean = () => setBoolean(boolean => !boolean);
    return [boolean, setBooleanState, toggleBoolean];
}
