import React, {useState, useEffect, useContext} from 'react';
import LocalStorage from "../lib/utilities/storage/LocalStorage";
import fetchJSONDataFromURL from "../lib/utilities/fetchJSONDataFromURL";
import {DomainContext} from "./Domain";
const ModelContext = React.createContext();

function ModelProvider(props) {
    let storedModel = LocalStorage.getItem('model', {});
    let [model, setModel] = useState(storedModel);
    let domainContext = useContext(DomainContext);
    useEffect(() => {
        if (!domainContext.state) {
            setModel(undefined);
        }
    }, [0, domainContext.state]);
    useEffect(() => {
        LocalStorage.setItem('model', model);
    }, [0, model]);
    return <ModelContext.Provider
        value={{
            state: model,
            setModel,
        }}
    >
        {props.children}
    </ModelContext.Provider>;
}
export {ModelContext, ModelProvider};
