                                                                            <img
                                                                                src={reference}
                                                                                alt="starlify"
                                                                                className="m-r-15 m-b-5 "
                                                                            />
                                                                            {referenceData.name
                                                                                ? referenceData.name
                                                                                : "Empty Reference Name"}
                                                                        </p>
                                                                    );
                                                                })
                                                                : ""}
                                                            {item.subSystems
                                                                ? item.subSystems.map((subSystems, i) => {
                                                                    return (
                                                                        <div>
                                                                            <p
                                                                                className={`text-grey click ${this.state.currentSubSystemIndex === subSystems.id ? 'active ' : ''}`}
                                                                                onClick={() =>
                                                                                    this.subSystemToggle(
                                                                                        subSystems.id
                                                                                    )
                                                                                }
                                                                                style={{
                                                                                    marginBottom: "1rem",
                                                                                    marginLeft: "3rem"
                                                                                }}
                                                                            >
                                                                                <i className={`system-down-icon left text-white ${(subSystems.services || subSystems.references) ? 'fas fa-chevron-down' : ''}`} />
                                                                                <img
                                                                                    src={subsystem}
                                                                                    alt="starlify"
                                                                                    className="m-r-15 m-b-5 "
                                                                                />
                                                                                <span
                                                                                    id={subSystems.id}
                                                                                    className={
                                                                                        this.state.ids.includes(
                                                                                            subSystems.id
                                                                                        )
                                                                                            ? "highlight"
                                                                                            : ""
                                                                                    }
                                                                                >
                                                                                    {" "}
                                                                                    {subSystems.name
                                                                                        ? subSystems.name
                                                                                        : "Empty Subsystem Name"}
                                                                                </span>
                                                                            </p>
                                                                            <Collapse
                                                                                isOpen={
                                                                                    this.state
                                                                                        .currentSubSystemIndex ===
                                                                                        subSystems.id
                                                                                        ? true
                                                                                        : false
                                                                                }
                                                                            >
                                                                                {subSystems.services
                                                                                    ? subSystems.services.map(
                                                                                        (service, i) => {
                                                                                            return (
                                                                                                <div id={service.id}>
                                                                                                    <p
                                                                                                        className="text-grey click"
                                                                                                        style={{
                                                                                                            marginBottom:
                                                                                                                "1rem",
                                                                                                            marginLeft: "4rem"
                                                                                                        }}
                                                                                                    >
                                                                                                        <img
                                                                                                            src={services}
                                                                                                            alt="starlify"
                                                                                                            className="m-r-15 m-b-5 "
                                                                                                        />
                                                                                                        {service.name
                                                                                                            ? service.name
                                                                                                            : "Empty Service Name"}
                                                                                                    </p>
                                                                                                </div>
                                                                                            );
                                                                                        }
                                                                                    )
                                                                                    : ""}
                                                                                {subSystems.references
                                                                                    ? subSystems.references.map(
                                                                                        (references, i) => {
                                                                                            return (
                                                                                                <div id={references.id}>
                                                                                                    <p
                                                                                                        className="text-grey click"
                                                                                                        style={{
                                                                                                            marginBottom:
                                                                                                                "1rem",
                                                                                                            marginLeft: "4rem"
                                                                                                        }}
                                                                                                    >
                                                                                                        <img
                                                                                                            src={reference}
                                                                                                            alt="starlify"
                                                                                                            className="m-r-15 m-b-5 "
                                                                                                        />
                                                                                                        {references.name
                                                                                                            ? reference.name
                                                                                                            : "Empty Reference Name"}
                                                                                                    </p>
                                                                                                </div>
                                                                                            );
                                                                                        }
                                                                                    )
                                                                                    : ""}
                                                                            </Collapse>
                                                                        </div>
                                                                    );
                                                                })
                                                                : ""}
                                                        </Collapse>
                                                    </div>
                                                );
                                            })
                                            : ""}
                                    </Collapse>
                                </VerticalScrollbars>
                            </div>
                            <div className="flows">
                                <p
                                    style={{ marginBottom: "1rem" }}
                                    className={`text-white click flowClick ${this.state.flowsCollapse ? 'active ' : ''}`}
                                >
                                    Flows<i className="fas fa-chevron-down system-down-icon" onClick={this.flowsMenuToggle} />

                                        <i
                                            className="fas fa-plus system-down-icon"
                                            id="flows"

                                            onClick={(e) => {
                                                this.addUiToggle(e, 'flow')
                                           }}
                                        />

                                </p>
                                <VerticalScrollbars style={{ height: 200 }}>
                                {this.state.flowAdd && (<div  className="extra-add">
                                            <input
                                                type="text"
                                                id="flow"
                                                onKeyDown={this.createEntity.bind(this)}
                                                onFocus={(e) => {
                                                    this.resetAddUiToggle(e, 'flow')
                                               }}
                                                //value={el}
                                                //onChange={this.handleChange.bind(this, i)}
                                                //onMouseDown={this.createEntityCopy.bind(this, i)}
                                            />
                                            <button type="button" onClick={(e) => {
                                                this.addUiToggle(e, 'flow')
                                           }}>
                                                <i className="fas fa-times" />
                                            </button>
                                        </div>)}
                                    <Collapse isOpen={this.state.flowsCollapse} id="FlowListing">
                                        {this.state.systemData.flows
                                            ? this.state.systemData.flows.map((item, index) => {
                                                return (
                                                    <div key={item.id}>
                                                         <ContextMenuTrigger id="default_right_click" itemName={item.name} itemId={item.id} itemEntity="flow" collect={collect}>
                                                            <p
                                                            className="text-grey click"
                                                            style={{
                                                                marginBottom: "1rem",
                                                                marginLeft: "2rem"
                                                            }}
                                                            onClick={() => this.flowsToggle(item.id)}
                                                        >
                                                            <span
                                                                id={item.id}
                                                                className={
                                                                    this.state.ids.includes(item.id)
                                                                        ? "highlight"
                                                                        : ""
                                                                }
                                                            >
                                                                {item.name ? item.name : "Empty Flow Name"}
                                                            </span>
                                                        </p>
                                                        </ContextMenuTrigger>
                                                    </div>
                                                );
                                            })
                                            : ""}
                                    </Collapse>
                                </VerticalScrollbars>
                            </div>
                        </div>
                    </div>
                </VerticalScrollbars>

            </div>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return {
        onHighlightClick: (systemId) => {
            const action = { type: "HIGHLIGHT", system: systemId };
            dispatch(action);
        }
    }
}

export default connect(null, mapDispatchToProps)(Systems);
