import React, { Component } from 'react';
import users from '../../_assets/images/menu/users.svg';
import user from '../../_assets/images/menu/user.svg';
import inbox from '../../_assets/images/menu/inbox.svg';
import save from '../../_assets/images/menu/save.svg';
import globe from '../../_assets/images/menu/globe.svg';
import inboxfull from '../../_assets/images/menu/inbox-full.svg';
import share from '../../_assets/images/menu/share.svg';
import search from '../../_assets/images/menu/search.svg';
import Organisation from '../../pages/dashboard/browsers/Organisation';
import Systems from '../../pages/dashboard/browsers/Systems';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faPlus, faChevronDown } from "@fortawesome/free-solid-svg-icons";
library.add(faTimes, faPlus, faChevronDown);

export default class Header extends Component {
    constructor(){
        super();

        this.state = {
          organisationVisible: false,
          systemsVisible: false,
            };

        this.organisationhandleClick = this.organisationhandleClick.bind(this);
      this.organisationhandleOutsideClick = this.organisationhandleOutsideClick.bind(this);
      this.systemshandleClick = this.systemshandleClick.bind(this);
        this.systemshandleOutsideClick = this.systemshandleOutsideClick.bind(this);

    };
    handleClick() {
        // if (!this.state.organisationVisible) {
        //   // attach/remove event handler
        //   document.addEventListener('click', this.handleOutsideClick, false);
        // } else {
        //   document.removeEventListener('click', this.handleOutsideClick, false);
        // }
        if (!this.state.systemsVisible) {
          // attach/remove event handler
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        // this.setState(prevState => ({
        //   organisationVisible: !prevState.organisationVisible,
        // }));
        this.setState(prevState => ({
          systemsVisible: !prevState.systemsVisible,
        }));
    }
  //   organisationhandleClick() {
  //       if (!this.state.organisationVisible) {
  //         // attach/remove event handler
  //         document.addEventListener('click', this.systemshandleOutsideClick, true);
  //       } else {
  //     document.removeEventListener('click', this.systemshandleOutsideClick, true);

  //       }

  //       this.setState(prevState => ({
  //         organisationVisible: !prevState.organisationVisible,
  //       }));
  // }
  systemshandleClick() {
    if (!this.state.systemsVisible) {
      // attach/remove event handler
      document.addEventListener('click', this.systemshandleOutsideClick, false);
    } else {
      document.removeEventListener('click', this.systemshandleOutsideClick, false);

    }

    this.setState(prevState => ({
      systemsVisible: !prevState.systemsVisible,
    }));
  }

  // organisationhandleOutsideClick(e) {
  //       // ignore clicks on the component itself
  //       if (this.node.contains(e.target)) {
  //         return;
  //       }

  //       this.organisationhandleClick();
  //     }
      systemshandleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
          return;
        }

        this.systemshandleClick();
      }
  render() {
    return (
        <div className="dashboard-header"  ref={node => { this.node = node; }}>
            <div className="d-flex text-white">
                <div className="col-md-3">
                    <ul className="first-menu">
                        <li>
                {/* <button onClick={this.organisationhandleClick}><img src={users} alt="starlify" /></button> */}
                <img src={users} alt="starlify" />

                        </li>
                        <li><button onClick={this.systemshandleClick}><img src={inbox} alt="starlify" /></button>

                        </li>
                    </ul>
                </div>
                <div className="col-md-4">
                    <ul className="second-menu">
                            <li><button><img src={save} alt="starlify" /></button></li>
                            <li><button><img src={user} alt="starlify" /></button></li>
                            <li><button><img src={inboxfull} alt="starlify" /></button></li>
                            <li><button><img src={share} alt="starlify" /></button></li>
                    </ul>
                </div>
                <div className="col-md-1">
                <ul className="third-menu">
                            <li><button><img src={globe} alt="starlify" /></button></li>
                    </ul>
                </div>
                <div className="col-md-4">
                    <ul className="fourth-menu">
                            <li><button><img src={save} alt="starlify" /></button></li>
                            <li>Entiros<FontAwesomeIcon className="oi system-down-icon" icon="chevron-own" /></li>
                            <li><button><img src={search} alt="starlify" /></button></li>
                    </ul>
                </div>
        </div>
        <div className="content d-flex">
        {this.state.organisationVisible && (
                              <div className="organisationVisible col-md-4"> 
                                <Organisation />
                              </div>
                            )
          }
           {this.state.systemsVisible && (
                            <div className="systemVisible col-md-4"> 
                              <Systems />
                            </div>
                        )
                        }
        </div>

      </div>
    )
  }
}
