                          item.id === this.props.division ? "active" : "";
                        return (
                          <ContextMenuTrigger
                            key={item.id}
                            id="org_right_click"
                          >
                            <li
                              className={"division-item " + active}
                              id={item.id}
                              data-type="division"
                              onClick={e =>
