                              key={item.id}
                              id="org_right_click"
                            >
                              <li
                                key={item.id}
                                className={"team-item " + active}
                                id={item.id}
                                data-type="team"
                                onClick={e =>
                                  this.props.onClickTeam(
                                    this.props.domain,
                                    item.id
                                  )
                                }
                              >
