import {
	FETCH_MODEL_BEGIN,
	FETCH_MODEL_SUCCESS,
	FETCH_MODEL_FAILURE
} from "./modelActions";

const initialState = {
	activeSystem: "",
	items: [],
	loading: false,
	error: null,
	status: null
};

export default function modelReducer( state = initialState, action ){
	//console.log("reducer", action);

	switch (action.type) {
		case "HIGHLIGHT":
			return{
				...state,
				activeSystem: action.system
			};

		case FETCH_MODEL_BEGIN:
			return {
				...state,
				loading: true,
				error: null
			};

		case FETCH_MODEL_SUCCESS:
			return {
				...state,
				loading: false,
				items: action.payload.model,
				status: "success"
			};

		case FETCH_MODEL_FAILURE:
			return {
				...state,
				loading: false,
				error: action.payload.error,
				items: []
			};

		default:
			return state;
	}
}
