import React, { Component } from "react";
import { Table, Modal, ModalBody, Input, FormGroup } from "reactstrap";
import pen from "../../../_assets/icons/icons/edit-icon.svg";
import list from "../../../_assets/icons/icons/List-icon.svg";
import { connect } from "react-redux";

class ModalAcess extends Component {
  constructor(props) {
    super(props);
    this.close = this.close.bind(this);
    this.fetchDivisions = this.fetchDivisions.bind(this);
    this.fetchTeams = this.fetchTeams.bind(this);
    this.fetchPersons = this.fetchPersons.bind(this);
    this.fetchModelGrants = this.fetchModelGrants.bind(this);
    this.state = {
      modeluri: "",
      model: null,
      models: [],
      chosenModel: null,
      divisions: [],
      grants: [],
      accessEditor: false,
      popup: false,
      eventType: null
    };
  }
  close(type) {
    this.props.close(type);
  }

  modalToggle = () => {
    this.setState({
      popup: !this.state.popup,
      eventType: null
    });
  };

  selectModel = e => {
    this.setState({ modeluri: e.target.value });
    fetch(process.env.REACT_APP_API + e.target.value)
      .then(res => res.json())
      .then(result => {
        this.setState({
          model: result,
          popup: false,
          accessEditor: true
        });
      });
    var modelArr = e.target.value.split("/");
    this.fetchModelGrants(modelArr[2]);
    this.fetchDivisions(this.props.domain);
  };

  fetchModelGrants = modelId => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        this.props.domain +
        "/grant/" +
        modelId
    )
      .then(res => res.json())
      .then(json => {
        this.setState({ grants: json });
      });
  };

  fetchDivisions = domainId => {
    return fetch(process.env.REACT_APP_API + "/hypermedia/domain/" + domainId)
      .then(res => res.json())
      .then(result => {
        var org = result.organisation;
        fetch(
          process.env.REACT_APP_API +
            "/hypermedia/domain/" +
            domainId +
            "/organisation/" +
            org.id +
            "/organisations"
        )
          .then(res => res.json())
          .then(json => {
            var organisations = json;
            for (var i = 0; i < organisations.length; i++) {
              let org = organisations[i];
              org["teams"] = [];
              if (org._links && org._links.length !== 0) {
                this.fetchTeams(domainId, this.state.model.id, org.id).then(
                  teams => (org["teams"] = teams)
                );
              }
            }
            this.setState({
              divisions: organisations
            });
            return true;
          });
      });
  };

  fetchTeams = (domainId, modelId, orgid) => {
    return fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        domainId +
        "/organisation/" +
        orgid +
        "/organisations"
    )
      .then(res => res.json())
      .then(json => {
        var organisations = json;
        for (var i = 0; i < organisations.length; i++) {
          let org = organisations[i];
          org["persons"] = [];
          if (org._links && org._links.length !== 0) {
            this.fetchPersons(domainId, modelId, org.id).then(
              persons => (org["persons"] = persons)
            );
          }
        }
        return organisations;
      });
  };

  fetchPersons = (domainId, modelId, orgid) => {
    return fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        domainId +
        "/organisation/" +
        orgid +
        "/persons"
    )
      .then(res => res.json())
      .then(persons => {
        return persons;
      });
  };

  onGrantChange = (e, grantid) => {
    if (e.target.checked) {
      this.addGrantAccess(
        this.props.domain,
        this.state.model.id,
        grantid,
        e.target.value
      );
    } else {
      this.deleteGrantAccess(
        this.props.domain,
        this.state.model.id,
        grantid,
        e.target.value
      );
    }
  };

  addGrantAccess = (domainId, modelId, grantid, value) => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        domainId +
        "/grant/" +
        modelId +
        "/" +
        grantid +
        "/permission/" +
        value,
      { method: "POST" }
    ).then(result => {
      this.fetchModelGrants(this.state.model.id);
    });
  };

  deleteGrantAccess = (domainId, modelId, grantid, value) => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        domainId +
        "/grant/" +
        modelId +
        "/" +
        grantid +
        "/permission/" +
        value,
      { method: "DELETE" }
    ).then(result => {
      this.fetchModelGrants(this.state.model.id);
    });
  };

  componentDidMount() {
    var domainID = this.props.domain;
    if (domainID !== "") {
      fetch(process.env.REACT_APP_API + "/domain/" + domainID)
        .then(res => res.json())
        .then(result => {
          this.setState({
            models: result.links
          });
        })
        .catch(error => {
          this.setState({ models: [] });
        });
    }
  }

  render() {
    const models = this.state.models;

    return (
      <div className="browser">
        <Modal isOpen={this.state.popup} toggle={this.modalToggle}>
          <ModalBody>
            {this.state.eventType === "model-choose" && (
              <div>
                <h4 className="text-center m-b-30">Choose model</h4>
                <FormGroup>
                  <Input
                    type="select"
                    onChange={this.selectModel.bind(this)}
                    value={this.state.modeluri}
                  >
                    <option value="">Select Model</option>
                    {models.map(item => (
                      <option key={item.uri} value={item.uri}>
                        {item.title}
                      </option>
                    ))}
                  </Input>
                </FormGroup>
              </div>
            )}
          </ModalBody>
        </Modal>
        {this.state.accessEditor === false && (
          <div>
            <div className="browser_header text-white">
              <i className="fas fa-key submenu-icon"></i>
