function mapStateToProps(state) {
  return {
    domain: state.model.domain,
    orgid: state.organisation.orgid,
    division: state.organisation.division,
    divisions: state.organisation.divisions,
    team: state.organisation.team,
    teams: state.organisation.teams,
    persons: state.organisation.persons
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onfetchDivisions: domainId => {
      dispatch(fetchDivisions(domainId));
    },
    onClickDivision: (domainId, divId) => {
      const action = {
        type: "SELECT_DIVISION",
        division: divId
      };
      dispatch(fetchTeams(domainId, divId));
      dispatch(action);
    },
    onClickTeam: (domainId, teamId) => {
      const action = {
        type: "SELECT_TEAM",
        team: teamId
      };
      dispatch(fetchPersons(domainId, teamId));
      dispatch(action);
    }
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Organisation);

