import React, { Component } from "react";
import {
  Modal,
  ModalBody,
  Input,
  Row,
  Col,
  InputGroup,
  InputGroupAddon,
  InputGroupText
} from "reactstrap";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { connect } from "react-redux";
import {
  fetchDivisions,
  fetchTeams,
  fetchPersons
} from "../../../store/organisation/orgActions";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import addCircle from "../../../_assets/icons/icons/add-icon-circle.svg";
import check from "../../../_assets/icons/icons/check-icon.svg";

class Organisation extends Component {
  constructor(props) {
    super(props);
    this.close = this.close.bind(this);
    this.rightClick = this.rightClick.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
    this.addDivisionToggle = this.addDivisionToggle.bind(this);
    this.addingDivisionToggle = this.addingDivisionToggle.bind(this);
    this.state = {
      itemid: null,
      itemname: null,
      popup: false,
      eventType: null,
      addDivision: false,
      addingDivision: false
    };
  }
  addingDivisionToggle() {
    this.setState(prevState => ({
      addingDivision: !prevState.addingDivision
    }));
  }
  addDivisionToggle() {
    this.setState(prevState => ({
      addDivision: !prevState.addDivision
    }));
  }
  //closes the organisation section
  close(menu, type) {
    this.props.close(menu, type);
  }

  componentDidMount() {
    if (this.props.domain !== "") {
      if (this.props.division === "" || this.props.team === "") {
        this.props.onfetchDivisions(this.props.domain);
      }
    }
  }

  componentDidUpdate(oldProps) {
    const newProps = this.props;
    if (oldProps.domain !== newProps.domain) {
      this.props.onfetchDivisions(newProps.domain);
    }
  }

  rightClick = (e, data) => {
    this.setState({ popup: !this.state.popup });
    var type = data.target.getAttribute("data-type") + "-" + data.eventType;
    var id = data.target.id;
    var name = data.target.textContent;
    this.setState({
      itemid: id,
      itemname: name,
      eventType: type
    });
  };

  modalToggle = () => {
    this.setState({
      itemid: null,
      itemname: null,
      popup: !this.state.popup,
      eventType: null
    });
  };

  addDivision = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/organisation/" +
          this.props.orgid +
          "/organisation",
        { method: "POST" }
      )
        .then(res => res.json())
        .then(result => {
          fetch(
            process.env.REACT_APP_API +
              "/hypermedia/domain/" +
              this.props.domain +
              "/organisation/" +
              result.id,
            {
              method: "PATCH",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ name: value })
            }
          )
            .then(res => res.json())
            .then(result => {
              this.props.onfetchDivisions(this.props.domain);
              this.modalToggle();
            });
        });
    }
  };

  renameDivision = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/organisation/" +
          this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
        .then(res => res.json())
        .then(result => {
          document.getElementById(result.id).textContent = result.name;
          this.modalToggle();
        });
    }
  };

  deleteDivision = () => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        this.props.domain +
        "/organisation/" +
        this.state.itemid,
      { method: "DELETE" }
    ).then(result => {
      console.log(result);
      switch (result.status) {
        case 200:
          this.props.onfetchDivisions(this.props.domain);
          this.modalToggle();
          break;
        case 403:
          this.setState({ eventType: "delete-403" });
          break;
        default:
      }
    });
  };

  addTeam = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/organisation/" +
          this.props.division +
          "/organisation",
        { method: "POST" }
      )
        .then(res => res.json())
        .then(result => {
          fetch(
            process.env.REACT_APP_API +
              "/hypermedia/domain/" +
              this.props.domain +
              "/organisation/" +
              result.id,
            {
              method: "PATCH",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ name: value })
            }
          )
            .then(res => res.json())
            .then(result => {
              this.props.onClickDivision(
                this.props.domain,
                this.props.division
              );
              this.modalToggle();
            });
        });
    }
  };

  renameTeam = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/organisation/" +
          this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
        .then(res => res.json())
        .then(result => {
          document.getElementById(result.id).textContent = result.name;
          this.modalToggle();
        });
    }
  };

  deleteTeam = () => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        this.props.domain +
        "/organisation/" +
        this.state.itemid,
      { method: "DELETE" }
    )
      .then(result => {
        switch (result.status) {
          case 200:
            this.props.onClickDivision(this.props.domain, this.props.division);
            this.modalToggle();
            break;
          case 403:
            this.setState({ eventType: "delete-403" });
            break;
          default:
        }
      })
      .catch(error => {
        console.log(error);
      });
  };

  addPerson = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/organisation/" +
          this.props.team +
          "/person",
        { method: "POST" }
      )
        .then(res => res.json())
        .then(result => {
          fetch(
            process.env.REACT_APP_API +
              "/hypermedia/domain/" +
              this.props.domain +
              "/person/" +
              result.id,
            {
              method: "PATCH",
              headers: { "Content-Type": "application/json" },
              body: JSON.stringify({ name: value })
            }
          )
            .then(res => res.json())
            .then(result => {
              this.props.onClickTeam(this.props.domain, this.props.team);
              this.modalToggle();
            });
        });
    }
  };

  renamePerson = e => {
    var value = e.target.value;
    if (e.key === "Enter") {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/domain/" +
          this.props.domain +
          "/person/" +
          this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
        .then(res => res.json())
        .then(result => {
          document.getElementById(result.id).textContent = result.name;
          this.modalToggle();
        });
    }
  };

  deletePerson = () => {
    fetch(
      process.env.REACT_APP_API +
        "/hypermedia/domain/" +
        this.props.domain +
        "/person/" +
        this.state.itemid,
      { method: "DELETE" }
    )
      .then(result => {
        this.props.onClickTeam(this.props.domain, this.props.team);
        this.modalToggle();
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="org_browser">
        <ContextMenu id="org_right_click">
          <MenuItem data={{ eventType: "rename" }} onClick={this.rightClick}>
            Rename
          </MenuItem>
          <MenuItem data={{ eventType: "delete" }} onClick={this.rightClick}>
            Delete
          </MenuItem>
        </ContextMenu>

        <Modal isOpen={this.state.popup} toggle={this.modalToggle}>
          <ModalBody>
            {this.state.eventType === "division-add" && (
              <div>
                <h4 className="text-center m-b-30">Add Division</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addDivision.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === "division-rename" && (
              <div>
                <h4 className="text-center m-b-30">Rename Division</h4>
                <Input
                  type="text"
                  className="text-input"
                  defaultValue={this.state.itemname}
                  onKeyDown={this.renameDivision.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === "division-delete" && (
              <div>
                <h4>Are you sure?</h4>
                <p>
                  Do you really want to delete division {this.state.itemname}?{" "}
                  <br />
                  Process cannot be undone
                </p>
                <div className="delete-button">
                  <button
                    className="btn black-btn"
                    onClick={this.deleteDivision.bind(this)}
                  >
                    confirm
                  </button>
                  <button
                    className="btn transparent-black-btn"
                    onClick={this.modalToggle}
                  >
                    cancel
                  </button>
                </div>
              </div>
            )}
            {this.state.eventType === "team-add" && (
              <div>
                <h4 className="text-center m-b-30">Add Team</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addTeam.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === "team-rename" && (
              <div>
                <h4 className="text-center m-b-30">Rename Team</h4>
                <Input
                  type="text"
                  className="text-input"
                  defaultValue={this.state.itemname}
                  onKeyDown={this.renameTeam.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === "team-delete" && (
              <div>
                <h4>Are you sure?</h4>
                <p>
                  Do you really want to delete team {this.state.itemname}?{" "}
                  <br />
                  Process cannot be undone
                </p>
                <div className="delete-button">
                  <button
                    className="btn black-btn"
                    onClick={this.deleteTeam.bind(this)}
                  >
                    confirm
                  </button>
                  <button
                    className="btn transparent-black-btn"
                    onClick={this.modalToggle}
                  >
                    cancel
                  </button>
                </div>
              </div>
            )}
            {this.state.eventType === "person-add" && (
              <div>
                <h4 className="text-center m-b-30">Add Person</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addPerson.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === "person-rename" && (
              <div>
