import React, { Component } from "react";
import { FormGroup, Input } from "reactstrap";
import photo from "../../_assets/images/photo.jpg";
import { connect } from "react-redux";
import { fetchModel } from "../../store/model/modelActions";
import { fetchFlows } from "../../store/system/systemActions";

class Usermenu extends Component {
  constructor() {
    super();
    this.fetchModels = this.fetchModels.bind(this);
    this.state = {
      domains: [],
      models: []
    };
  }

  componentDidMount() {
    fetch(process.env.REACT_APP_API + "/hypermedia/domains")
    .then(res => res.json())
    .then(result => {
      this.setState({ domains: result });
    });
    if (this.props.domain !== "") {
      this.fetchModels(this.props.domain);
    }
  }

  domainChange = e => {
    this.props.onDomainSelect(e);
    if (e.target.value !== "") {
      this.fetchModels(e.target.value);
    } else {
      this.setState({ models: [] });
    }
  };

  fetchModels = domainID => {
    fetch(process.env.REACT_APP_API + "/domain/" + domainID)
      .then(res => res.json())
      .then(async domain => {
        this.setState({ models: domain.links });
      });
  };

  render() {
    const domains = this.state.domains;
    const models = this.state.models;
    return (
      <div className="usermenu">
        <ul>
          <li className="userarea">
            <img src={photo} alt="starlify" className="photo-icon" />{" "}
            <span className="name text-white">Name Surname</span>
          </li>
          <li>
            <FormGroup>
              <Input
                className="select-css"
                type="select"
                name="domain"
                onChange={this.domainChange.bind(this)}
                value={this.props.domain}
              >
                <option value="">Select Domain</option>
                {domains.map(item => (
                  <option key={item.id} value={item.id}>
                    {item.name}
                  </option>
                ))}
              </Input>
            </FormGroup>
          </li>
          <li>
            <FormGroup>
              <Input
                className="select-css"
                type="select"
                name="model"
                onChange={this.props.onModelSelect}
                value={this.props.model}
              >
                <option value="">Select Model</option>
                {models.map(item => (
                  <option data-key={item.params.id} key={item.uri} value={item.uri}>
                    {item.title}
                  </option>
                ))}
              </Input>
            </FormGroup>
          </li>
          <ul>
            <li className="text-grey no-border m-t-15">
              <span className="icon icon-Megaphone submenu-icon" />
              News
            </li>
            <li className="text-grey no-border">
              <span className="icon icon-Layers submenu-icon" />
              Subscriptions
            </li>
            <li className="text-grey no-border">
              <span className="icon icon-Settings submenu-icon" />
              Options
            </li>
            <li className="text-grey no-border">
              <span className="icon icon-Exit submenu-icon" />
              Sign out
            </li>
          </ul>
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    domain: state.model.domain,
    model: state.model.modelUri
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onDomainSelect: e => {
      const action = { type: "SELECTDOMAIN", domain: e.target.value };
      dispatch(action);
    },

    onModelSelect: e => {
      const selectedIndex = e.target.options.selectedIndex;
      const modelId = e.target.options[selectedIndex].getAttribute('data-key');
      const action = { type: "SELECTMODEL", model: e.target.value, modelId: modelId };
      dispatch(fetchModel(modelId));
      dispatch(fetchFlows(modelId));
      dispatch(action);
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Usermenu);
