    // ignore clicks on the component itself
    if (this.node.contains(e.target)) {
      return;
    }

    this.organisationhandleClick();
  }

  render() {
    return (
      <div className="dashboard">
        <div
          className="dashboard-header"
          ref={node => {
            this.node = node;
          }}
        >
          <ul className="text-white menu-wrapper">
            <li className="">
              <ul className="first-menu">
                <li
                  className={
                    this.state.activeMenu === "organisation" ? "active" : ""
                  }
                >
                  <button onClick={() => this.menuClick("organisation")}>
                    <span className="icon icon-Users menu-icon" />
                  </button>
                </li>
                {this.activeState}
                <li
                  className={
                    this.state.activeMenu.includes("system") ? "active" : ""
