                  }
                >
                  <button onClick={() => this.menuDetailClick("orgDetail")}>
                    <span className="icon icon-User menu-icon" />
                  </button>
                </li>
                <li className={this.state.systemDetailsVisible ? "active" : ""}>
                  <button onClick={() => this.menuDetailClick("systemDetail")}>
