import React, { Component } from 'react';
import * as d3 from 'd3';

import "../../../_assets/css/Graph.css";

class Graph extends Component {

  fetchSystems = () => {
    var items = [];
    items["nodes"] = [];
    items["links"] = [];

    fetch("https://000int-prd-payara2.private.entiros.io:8181/starlify/resources/model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c") // Open Model
    .then(res => res.json())
    .then((result) => {

      var i = 0;
      result.systems.forEach(function(data) {
        var sysid = data.id;
        items["nodes"][i] = { "id": sysid, "name": data.name  || "System", "type": "system", "radius": 25 };
        if (data.services){
          data.services.forEach(function(service){
            var j = ++i;
            items["nodes"][j] = { "id": service.id, name: service.name || "Service", "type": "service", "radius": 8 };
            items["links"].push({"source": sysid, "target": service.id, "type": "service"});
          })
        }

        if (data.references){
          data.references.forEach(function(reference){
            fetch("https://000int-prd-payara2.private.entiros.io:8181/starlify/resources/model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/reference/"+reference.id)
            .then(res => res.json())
            .then((result) => {

              items["links"].push({"source": sysid, "target": result.service.id, "type": "reference"});

            }, (error) => {
              return error;
            })
          })
        }

        i++;
      });

    }, (error) => {
      return error;
    })
    return items;
  }

  componentWillMount(){

    const items = this.fetchSystems(); 

    new Promise(function(resolve, reject) {

      setTimeout(() => {
        resolve(items);
      }, 2000);

    }).then(function(items) {

      const width = window.innerWidth,
        height = window.innerHeight - 100;

      const zoom = d3.zoom()
                  .scaleExtent([0.5, 15])
                  .on("zoom", zoomed);

      var svg = d3.select(".forcegraph").append("svg")
          .attr("width", width)
          .attr("height", height)
          .call(zoom)
          .append("g")
          .attr("transform", "translate(0,0)");

        svg.append('defs').append('marker')
          .attr('id', 'service')
          .attr('viewBox', '-5 -5 20 20')
          .attr('refX', 4)
          .attr('refY', 0)
          .attr('orient', 'auto')
          .attr('markerWidth', 50)
          .attr('markerHeight', 50)
          .attr('xoverflow', 'visible')
          .append('svg:path')
          .attr('d', 'M -5 -2 L 0 0 L -5 2 z')
          .attr('fill', 'White')
          .style('stroke','none');

      var simulation = d3.forceSimulation()
        .force("link", d3.forceLink().id(function(d,i) {
          return d.id;
        })
        .distance(function(d) {
          console.log(d)
          return d.type === "reference" ? 200 : 25;
        }))
        .force("charge", d3.forceManyBody().strength(-250 ))
        .force('gravity', d3.forceRadial(0, 0))
        .force("center", d3.forceCenter(width / 2, height / 2));

      var links = svg.selectAll(".link")
        .data(items.links)
        .enter().append("line")
        .attr("class", "link");

      var nodes = svg.selectAll(".node")
        .data(items.nodes)
        .enter().append("g")
        .attr("class", "node")
        .call(d3.drag()
          .on("start", dragstarted)
          .on("drag", dragged)
          .on("end", dragended)
        );

      var circle = nodes.append("circle");

      var label = nodes.append("text")
        .attr("dy", ".35em")
        .text(function(d) { return d.name; });

      simulation
        .nodes(items.nodes)
        .on("tick", tick);

      simulation
        .force("link")
        .links(items.links);

      function tick() {
        links
          .attr("x1", function (d) { return d.source.x; })
          .attr("y1", function (d) { return d.source.y; })
          .attr("x2", function (d) { return d.target.x; })
          .attr("y2", function (d) { return d.target.y; })
          .attr("marker-end", function(d) {
            return d.type === "reference" ? "url(#service)" : "";
          });

        circle
          .attr("cx", function(d) { return d.x; })
          .attr("cy", function(d) { return d.y; })
          .attr("class", function(d) { return d.type; })
          .attr("r", weightRadius);

        label
          .attr("x", function(d) { return d.x + 8; })
          .attr("y", function(d) { return d.y; });
      }

      function weightRadius(d) {
        var r = d.radius;
        return r;
      }

      function zoomed() {
          svg.attr('transform', 'translate(' + d3.event.transform.x + ',' + d3.event.transform.y + ') scale(' + d3.event.transform.k + ')');
      }

      function dragstarted(d) {
          if (!d3.event.active) simulation.alphaTarget(0.3).restart();
          d.fx = d.x;
          d.fy = d.y;
      }

      function dragged(d) {
          d.fx = d3.event.x;
          d.fy = d3.event.y;
      }

      function dragended(d) {
          if (!d3.event.active) simulation.alphaTarget(0);
          d.fx = null;
          d.fy = null;
      }

    });
  }

  render() {
    return (
      <div className="forcegraph"></div>
    )
  }
}

export default Graph;
