import React, { Component } from "react";
import {
    Collapse,
    Modal,
    ModalBody,
    InputGroup,
    InputGroupAddon,
    InputGroupButtonDropdown,
    InputGroupDropdown,
    Input,
    Button,
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import subsystem from "../../../_assets/images/subsystem.png";
import services from "../../../_assets/images/services.png";
import reference from "../../../_assets/images/reference.png";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { connect } from 'react-redux';


function collect(props) {
    return { itemName: props.itemName, itemId: props.itemId };
}

class Systems extends Component {
    constructor(props) {
        super(props);
        this.systemMenuToggle = this.systemMenuToggle.bind(this);
        this.flowsMenuToggle = this.flowsMenuToggle.bind(this);
        this.systemToggle = this.systemToggle.bind(this);
        this.flowsToggle = this.flowsToggle.bind(this);
        this.subSystemToggle = this.subSystemToggle.bind(this);
        this.searchSystem = this.searchSystem.bind(this);
        this.systemSelected = this.systemSelected.bind(this);
        this.close = this.close.bind(this);
        this.flowsSelected = this.flowsSelected.bind(this);
        this.toggle = this.toggle.bind(this);
        this.modalToggle = this.modalToggle.bind(this);
        this.toggleDropDown = this.toggleDropDown.bind(this);
        this.toggleSplit = this.toggleSplit.bind(this);
        this.values = [];
        this.state = {
            dropdownOpen: false,
            splitButtonOpen: false,
            modal: false,
            deleteModal: false,
            addClass: false,
            romoveClass: false,
            flowsCollapse: true,
            systemsCollapse: true,
            collapseSystem: false,
            collapseSubSystem: false,
            currentSystemIndex: "",
            currentSubSystemIndex: "",
            currentFlowIndex: "",
            systemData: { system: "" },
            showsystemDetail: false,
            showflowDetail: false,
            systemDetails: {},
            flowDetails: {},
            system: [],
            flow: [],
            searchString: "",
            ids: [],
            search: false,
            boxClass: ['_search'],
            box1Class: ['_search'],
            popoverOpen: false,
            itemId: '',
            itemName: '',
            itemValue: '',
            itemType: '',
            itemEntity: ''
        };



    }

    componentWillMount() {
        this.fetchSystemAndFlows()
    }

    //fetch system and flows
    fetchSystemAndFlows() {
        return fetch(
            process.env.REACT_APP_API + "model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6",
            {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            }
        )
            .then(response => response.json())
            .then(responseJson => {
                this.setState({ systemData: responseJson });
            });
    }



    createFlow() {
        // return this.state.flow
        //     ? this.state.flow.map((el, i) => (
        //         <div key={i} className="extra-add">
        //             <input
        //                 type="text"
        //                 id="flow"
        //                 onBlur={this.handleChange.bind(this, i)}
        //             />
        //             <button type="button" onClick={this.removeClick.bind(this, i)}>
        //                 <i class="fas fa-times" />
        //             </button>
        //         </div>
        //     ))
        //     : "";
    }


    handleChange(i, event) {
        let type = event.target.id;
        let systemName = event.target.value;

        if (type === "system") {
            const values = [...this.state.system];
            values[i] = event.target.value;
            this.setState({ system: values })
            this.setState({ itemEntity: 'system' })

        } else {
            let values = [...this.state.flow];
            values[i] = event.target.value;
            this.setState({ values });
            this.state.flow.splice(i, 1);
            return fetch(
                process.env.REACT_APP_API +
                "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow",
                {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    return fetch(
                        process.env.REACT_APP_API +
                        "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow/" +
                        responseJson.id,
                        {
                            method: "POST",
                            headers: {
                                "Content-Type": "application/json"
                            },
                            body: JSON.stringify({ name: systemName })
                        }
                    )
                        .then(response => response.json())
                        .then(responseJson => { });
                });
        }
    }

    addSystemUi() {
        this.setState(prevState => ({ system: [...prevState.system, ""] }));
    }

    removeSystemUi(index) {
        const values = [...this.state.system];
        values.splice(index, 1);
        this.setState({ system: values });
    }

    systemSelected(systemId, status) {
        this.props.systemSelected(systemId, status);
    }

    close(menu, type) {
        this.props.close(menu, type);
    }

    flowsSelected(flowId, status) {
        this.props.flowsSelected(flowId, status);
    }

    systemMenuToggle() {
        this.setState(state => ({ systemsCollapse: !state.systemsCollapse }));
    }
    flowsMenuToggle() {
        this.setState(state => ({ flowsCollapse: !state.flowsCollapse }));
    }

    flowsToggle(flowId) {

        if (
            this.state.currentFlowIndex === flowId ||
            (!this.state.showflowDetail && this.state.search)
        ) {
            this.setState({ search: false })
            this.setState({ currentFlowIndex: 0 })
                ;
            this.flowsSelected(flowId, true);
        } else {
            this.setState({ currentFlowIndex: flowId });
            this.setState({ showflowDetail: true });
            this.flowsSelected(flowId, true);
            return fetch(
                process.env.REACT_APP_API +
                "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow/72ad974f-e4e2-41c2-8838-79ebd240f315",
                {
                    method: "GET",
                    headers: {
                        "Content-Type": "application/json"
                    }
                }
            )
                .then(response => response.json())
                .then(responseJson => {
                    this.setState({ flowDetail: responseJson });
                });
        }
    }

    systemToggle(systemId) {

        //this.setState({ collapse: !this.state.collapse });
        if (
            this.state.currentSystemIndex === systemId ||
            (!this.state.showsystemDetail && this.state.search)
        ) {
            this.setState({ search: false })
            this.setState({ currentSystemIndex: 0 });
            this.systemSelected(systemId, false);
        } else {
            this.setState({ currentSystemIndex: systemId });
            this.systemSelected(systemId, true);
            //this.setState({ showsystemDetail: true });
            // return fetch(
            //     process.env.REACT_APP_API +
            //     "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/system/f1dc3452-e1a5-4a4e-9ae4-5fee3b34b647",
            //     {
            //         method: "GET",
            //         headers: {
            //             "Content-Type": "application/json"
            //         }
            //     }
            // )
            //     .then(response => response.json())
            //     .then(responseJson => {
            //         this.setState({ systemDetails: responseJson });
            //     });
        }
        this.props.onHighlightClick(systemId);
    }

    subSystemToggle(systemId) {
        this.setState({ collapseSubSystem: !this.state.collapseSubSystem });
        if (this.state.currentSubSystemIndex === systemId) {
            this.setState({ currentSubSystemIndex: 0 })
        } else {
            this.setState({ currentSubSystemIndex: systemId });
        }
    }

    searchSystem(event) {
        // this.state.ids = [];
        // this.setState({ search: true });
        // var lis = [];

        // var lis = document
        //     .getElementById("systemListing")
        //     .getElementsByTagName("span");
        // for (var i = 0; i < lis.length; i++) {
        //     var stringText = lis[i].innerHTML.toLowerCase();
        //     if (
        //         event.currentTarget.value &&
        //         stringText.includes(event.currentTarget.value.toLowerCase())
        //     ) {
        //         this.state.ids.push(lis[i].id);
        //     }
        // }

        // var lis = document
        //     .getElementById("FlowListing")
        //     .getElementsByTagName("span");
        // for (var i = 0; i < lis.length; i++) {
        //     var stringText = lis[i].innerHTML;
        //     if (
        //         event.currentTarget.value &&
        //         stringText.includes(event.currentTarget.value)
        //     ) {
        //         this.state.ids.push(lis[i].id);
        //     }
        // }
    }

    //create entity 
    createEntityCopy() {
        const values = [...this.state.system];
        values.map((value, index) => {
            if (value !== '') {

                fetch(
                    process.env.REACT_APP_API +
                    `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/${this.state.itemEntity}`,
                    {
                        method: "POST",
                        headers: {
                            "Content-Type": "application/json"
                        }
                    }
                )
                    .then(response => response.json())
                    .then(responseJson => {
                        return fetch(
                            process.env.REACT_APP_API +
                            `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/${this.state.itemEntity}/` +
                            responseJson.id,
                            {
                                method: "PATCH",
                                headers: {
                                    "Content-Type": "application/json"
                                },
                                body: JSON.stringify({ name: value })
                            }
                        )
                            .then(response => response.json())
                            .then(responseJson => { this.fetchSystemAndFlows() });
                    });
                values.splice(index, 1);
                this.setState({ system: values })
            }
        });

    }

    //create entity 
    createEntity() {
        fetch(
            process.env.REACT_APP_API +
            `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/${this.state.itemEntity}`,
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                }
            }
        )
            .then(response => response.json())
            .then(responseJson => {
                return fetch(
                    process.env.REACT_APP_API +
                    `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/${this.state.itemEntity}/` +
                    responseJson.id,
                    {
                        method: "PATCH",
                        headers: {
                            "Content-Type": "application/json"
                        },
                        body: JSON.stringify({ name: 'value' })
                    }
                )
                    .then(response => response.json())
                    .then(responseJson => { this.fetchSystemAndFlows() });
            });
    }


    //create service
    createService(event) {
        let serviceName = event.target.value;

        fetch(
            process.env.REACT_APP_API +
            `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/system/${this.state.itemId}/service`,
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                }
            }
        )
            .then(response => response.json())
            .then(responseJson => {
                //console.log('test')
                return fetch(
                    process.env.REACT_APP_API +
                    `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/service/` + responseJson.id,

                    {
                        method: "PATCH",
                        headers: {
                            "Content-Type": "application/json"
                        },
                        body: JSON.stringify({ name: serviceName })
                    }
                )
                    .then(response => response.json())
                    .then(responseJson => {
                        this.fetchSystemAndFlows();
                    })
            });
    }

    //Delete entity
    deleteEntity() {
        return fetch(
            process.env.REACT_APP_API +
            `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/entity/${this.state.itemType}/${this.state.itemId}`,
            {
                method: "DELETE",
                headers: {
                    "Content-Type": "application/json"
                }
            }
        )
            .then(response => {
                this.setState(prevState => ({
                    modal: !prevState.modal
                }));
                this.fetchSystemAndFlows()
            });
    }

    //update item value for edit
    renameHandleChange = (e) => {
        this.setState({ itemName: e.target.value });
    }

    //Update entity
    updateEntity() {
        fetch(
            process.env.REACT_APP_API +
            `model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/${this.state.itemEntity}/` +
            this.state.itemId,
            {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ name: this.state.itemName })
            }
        )
            .then(responseJson => { this.fetchSystemAndFlows() });

    }

    //control right click modal
    modalToggle(e, data) {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));

        if (data) {
            this.setState({
                itemId: data.itemId,
                itemName: data.itemName,
                itemType: data.itemType,
                itemEntity: data.itemEntity
            })

        }
    }

    //make search control active 
    toggle() {
        document.getElementById("searchControl").className = "_search active";
    }
    //remove active class from search control
    removeToggle(event) {
        if (event.target.value === '') {
            document.getElementById("searchControl").className = "_search";
        }
    }
    //Drop select for add reference
    toggleDropDown() {
        this.setState({
          dropdownOpen: !this.state.dropdownOpen
        });
      }

      toggleSplit() {
        this.setState({
          splitButtonOpen: !this.state.splitButtonOpen
        });
      }


    render() {


        return (
            <div className="org_browser" >


                <ContextMenu id="system_right_click">
                    <MenuItem data={{ itemType: 'add_sub_system', itemEntity: 'system' }} onClick={this.modalToggle}>
                        Add Sub System
            </MenuItem>
                    <MenuItem data={{ itemType: 'service', itemEntity: 'service' }} onClick={this.modalToggle}>
                        Add Service
            </MenuItem>
                    <MenuItem data={{ itemType: 'add_reference', itemEntity: 'reference' }} onClick={this.modalToggle}>
                        Add Reference
            </MenuItem>
                    <MenuItem data={{ itemType: 'delete', itemEntity: 'system' }} onClick={this.modalToggle}>
                        Delete
            </MenuItem>
                    <MenuItem data={{ itemType: 'rename', itemEntity: 'system' }} onClick={this.modalToggle}>
                        Rename
            </MenuItem>
                </ContextMenu>
                <Modal isOpen={this.state.modal} toggle={this.modalToggle} className={this.props.className}>
                    <ModalBody className={this.state.itemType === 'delete' ? 'text-center delete-modal-body ' : ''}>
                        {this.state.itemType === 'add_sub_system' && (
                            <div>
                                <input
                                    type="text"
                                    className="text-input"
                                    placeholder="Add New Sub System"
                                />
                            </div>)}
                        {this.state.itemType === 'service' && (
                            <div>
                                <input
                                    type="text"
                                    className="text-input"
                                    placeholder="Add New Service"
                                    onBlur={this.createService.bind(this)}
                                />

                            </div>)}
                        {this.state.itemType === 'add_reference' && (
                            <div>
                                <input
                                    type="text"
                                    className="text-input"
                                    placeholder="Add New Reference"
                                />
                                <select>


                                {this.state.systemData.systems
                                            ? this.state.systemData.systems.map((item, index) => {
                                                return (
                                                    <option>{item.name}</option>
                                                )}):''}
                                </select>      
                                <InputGroup>
                                    <InputGroupButtonDropdown addonType="prepend" isOpen={this.state.splitButtonOpen} toggle={this.toggleSplit}>
                                        <Button outline>Split Button</Button>
                                        <DropdownToggle split outline />
                                        <DropdownMenu>
                                        <DropdownItem header>Header</DropdownItem>
                                        <DropdownItem disabled>Action</DropdownItem>
                                        <DropdownItem>Another Action</DropdownItem>
                                        <DropdownItem divider />
                                        <DropdownItem>Another Action</DropdownItem>
                                        </DropdownMenu>
                                    </InputGroupButtonDropdown>
                                    <Input placeholder="and..." />
                                    <InputGroupAddon addonType="append"><Button color="secondary">I'm a button</Button></InputGroupAddon>
                                </InputGroup>

                            </div>)}
                        {this.state.itemType === 'rename' && (
                            <div>
                                <input
                                    type="text"
                                    className="text-input"
                                    placeholder="Rename"
                                    value={this.state.itemName}
                                    onBlur={this.updateEntity.bind(this)}
                                    onChange={this.renameHandleChange.bind(this)}
                                />

                            </div>)}
                        {this.state.itemType === 'delete' && (
                            <div>
                                <h2>Are you sure?</h2>
                                <p>Do you really want to delete these records? <br />
                                    Process cannot be undone</p>
                                <div className="delete-button">
                                    <a class="btn transparent-btn" href="#" onClick={this.modalToggle}>cancel</a>
                                    <a class="btn green-btn" onClick={this.deleteEntity.bind(this)}>confirm</a>
                                </div></div>)}
                    </ModalBody>
                </Modal>
                <h3 className="text-center text-white">
                    Systems and Flows
                    <i className="fas fa-times system-down-icon browser_close" onClick={() => this.close('system', 'menu')} />
                </h3>
                <VerticalScrollbars autoHide>

                    <div className="browser_wrapper">
                        <div className="org-selector">
                            <p className="text-white text-left">Selector</p>
                            <div className="_search" id="searchControl">
                                <input
                                    type="text"
                                    className="search-input"
                                    onKeyUp={this.searchSystem}
                                    // onClick={this.toggle.bind(this)}
                                    // onBlur={this.removeToggle.bind(this)}
                                />
                                <span className="icon icon-Search search-icon" />
                            </div>
                            <div className="systems w-100 d-inline-block montserrat text-grey">
                                <div className="system-search montserrat" />

                                <p
                                    className={`text-white click systemClick ${this.state.systemsCollapse ? 'active ' : ''}`}
                                    style={{ marginBottom: "1rem" }}
                                >
                                    <span onClick={this.systemMenuToggle}>
                                        Systems
                    <i className="fas fa-chevron-down system-down-icon" />
                                    </span>
                                    {this.state.systemData.systems && (
                                        <i
                                            className="fas fa-plus system-down-icon"
                                            id="system"
                                            onClick={this.addSystemUi.bind(this)}
                                        />)}
                                </p>
                                <VerticalScrollbars style={{ height: 220 }}>
                                    {this.state.system.map((el, i) => (
                                        <div key={i} className="extra-add">
                                            <input
                                                type="text"
                                                id="system"
                                                value={el}
                                                onChange={this.handleChange.bind(this, i)}
                                                onMouseDown={this.createEntityCopy.bind(this, i)}
                                            />
                                            <button type="button" onClick={this.removeSystemUi.bind(this, i)}>
                                                <i className="fas fa-times" />
                                            </button>
                                        </div>
                                    ))
                                    }

                                    <Collapse
                                        isOpen={this.state.systemsCollapse}
                                        id="systemListing"

                                    >
                                        {this.state.systemData.systems
                                            ? this.state.systemData.systems.map((item, index) => {
                                                return (
                                                    <div key={item.id} className="listingItem">
                                                        <ContextMenuTrigger id="system_right_click" itemName={item.name} itemId={item.id} collect={collect}>
                                                            <p
                                                                className={`text-grey click ${this.state.currentSystemIndex === item.id ? 'active ' : ''}`}
                                                                onClick={() => this.systemToggle(item.id)}
                                                                style={{
                                                                    marginLeft: "1rem"
                                                                }}
                                                            >

                                                                <i className={`system-down-icon left text-white ${(item.services || item.references || item.subSystems) ? 'fas fa-chevron-down' : ''}`} />

                                                                <img
                                                                    src={subsystem}
                                                                    alt="starlify"
                                                                    className="m-r-15 m-b-5 "
                                                                />

                                                                <span
                                                                    id={item.id}
                                                                    className={
                                                                        this.state.ids.includes(item.id)
                                                                            ? "highlight "
                                                                            : "test text-grey"
                                                                    }
                                                                >
                                                                    {item.name ? item.name : "Empty System Name"}
                                                                </span>



                                                            </p>
                                                        </ContextMenuTrigger>
                                                        <Collapse
                                                            isOpen={
                                                                this.state.currentSystemIndex === item.id ||
                                                                    this.state.search
                                                                    ? true
                                                                    : false
                                                            }
                                                        >
                                                            {item.services
                                                                ? item.services.map((service, i) => {
                                                                    return (
                                                                        <div id={service.id} key={service.id}>
                                                                            <p
                                                                                className="text-grey click"
                                                                                style={{
                                                                                    marginBottom: "1rem",
                                                                                    marginLeft: "5rem"
                                                                                }}
                                                                            >

                                                                                <img
                                                                                    src={services}
                                                                                    alt="starlify"
                                                                                    className="m-r-15 m-b-5 "
                                                                                />
                                                                                {service.name
                                                                                    ? service.name
                                                                                    : "Empty Service Name"}
                                                                            </p>
                                                                        </div>
                                                                    );
                                                                })
                                                                : ""}

                                                            {item.references
                                                                ? item.references.map((referenceData, i) => {
                                                                    return (
                                                                        <p key={referenceData.id}
                                                                            className="text-grey click"
                                                                            style={{
                                                                                marginBottom: "1rem",
                                                                                marginLeft: "3rem"
                                                                            }}
                                                                        >
                                                                            <img
                                                                                src={reference}
                                                                                alt="starlify"
                                                                                className="m-r-15 m-b-5 "
                                                                            />
                                                                            {referenceData.name
                                                                                ? referenceData.name
                                                                                : "Empty Reference Name"}
                                                                        </p>
                                                                    );
                                                                })
                                                                : ""}
                                                            {item.subSystems
                                                                ? item.subSystems.map((subSystems, i) => {
                                                                    return (
                                                                        <div>
                                                                            <p
                                                                                className={`text-grey click ${this.state.currentSubSystemIndex === subSystems.id ? 'active ' : ''}`}
                                                                                onClick={() =>
                                                                                    this.subSystemToggle(
                                                                                        subSystems.id
                                                                                    )
                                                                                }
                                                                                style={{
                                                                                    marginBottom: "1rem",
                                                                                    marginLeft: "3rem"
                                                                                }}
                                                                            >
                                                                                <i className={`system-down-icon left text-white ${(subSystems.services || subSystems.references) ? 'fas fa-chevron-down' : ''}`} />
                                                                                <img
                                                                                    src={subsystem}
                                                                                    alt="starlify"
                                                                                    className="m-r-15 m-b-5 "
                                                                                />
                                                                                <span
                                                                                    id={subSystems.id}
                                                                                    className={
                                                                                        this.state.ids.includes(
                                                                                            subSystems.id
                                                                                        )
                                                                                            ? "highlight"
                                                                                            : ""
                                                                                    }
                                                                                >
                                                                                    {" "}
                                                                                    {subSystems.name
                                                                                        ? subSystems.name
                                                                                        : "Empty Subsystem Name"}
                                                                                </span>
                                                                            </p>
                                                                            <Collapse
                                                                                isOpen={
                                                                                    this.state
                                                                                        .currentSubSystemIndex ===
                                                                                        subSystems.id
                                                                                        ? true
                                                                                        : false
                                                                                }
                                                                            >
                                                                                {subSystems.services
                                                                                    ? subSystems.services.map(
                                                                                        (service, i) => {
                                                                                            return (
                                                                                                <div id={service.id}>
                                                                                                    <p
                                                                                                        className="text-grey click"
                                                                                                        style={{
                                                                                                            marginBottom:
                                                                                                                "1rem",
                                                                                                            marginLeft: "4rem"
                                                                                                        }}
                                                                                                    >
                                                                                                        <img
                                                                                                            src={services}
                                                                                                            alt="starlify"
                                                                                                            className="m-r-15 m-b-5 "
                                                                                                        />
                                                                                                        {service.name
                                                                                                            ? service.name
                                                                                                            : "Empty Service Name"}
                                                                                                    </p>
                                                                                                </div>
                                                                                            );
                                                                                        }
                                                                                    )
                                                                                    : ""}
                                                                                {subSystems.references
                                                                                    ? subSystems.references.map(
                                                                                        (references, i) => {
                                                                                            return (
                                                                                                <div id={references.id}>
                                                                                                    <p
                                                                                                        className="text-grey click"
                                                                                                        style={{
                                                                                                            marginBottom:
                                                                                                                "1rem",
                                                                                                            marginLeft: "4rem"
                                                                                                        }}
                                                                                                    >
                                                                                                        <img
                                                                                                            src={reference}
                                                                                                            alt="starlify"
                                                                                                            className="m-r-15 m-b-5 "
                                                                                                        />
                                                                                                        {references.name
                                                                                                            ? reference.name
                                                                                                            : "Empty Reference Name"}
                                                                                                    </p>
                                                                                                </div>
                                                                                            );
                                                                                        }
                                                                                    )
                                                                                    : ""}
                                                                            </Collapse>
                                                                        </div>
                                                                    );
                                                                })
                                                                : ""}
                                                        </Collapse>
                                                    </div>
                                                );
                                            })
                                            : ""}
                                    </Collapse>
                                </VerticalScrollbars>
                            </div>
                            <div className="flows">
                                <p
                                    style={{ marginBottom: "1rem" }}
                                    className={`text-white click flowClick ${this.state.flowsCollapse ? 'active ' : ''}`}
                                >
                                    Flows<i className="fas fa-chevron-down system-down-icon" onClick={this.flowsMenuToggle} />
                                    {this.state.systemData.flow && (
                                        <i
                                            className="fas fa-plus system-down-icon"
                                            id="flows"
                                            onClick={this.addSystemUi.bind(this)}
                                        />
                                    )}
                                </p>
                                <VerticalScrollbars style={{ height: 200 }}>
                                    {this.createFlow()}
                                    <Collapse isOpen={this.state.flowsCollapse} id="FlowListing">
                                        {this.state.systemData.flow
                                            ? this.state.systemData.flow.map((item, index) => {
                                                return (
                                                    <div key={item.id}>
                                                        <p
                                                            className="text-grey click"
                                                            style={{
                                                                marginBottom: "1rem",
                                                                marginLeft: "2rem"
                                                            }}
                                                            onClick={() => this.flowsToggle(item.id)}
                                                        >
                                                            <span
                                                                id={item.id}
                                                                className={
                                                                    this.state.ids.includes(item.id)
                                                                        ? "highlight"
                                                                        : ""
                                                                }
                                                            >
                                                                {item.name ? item.name : "Empty Flow Name"}
                                                            </span>
                                                        </p>
                                                    </div>
                                                );
                                            })
                                            : ""}
                                    </Collapse>
                                </VerticalScrollbars>
                            </div>
                        </div>
                    </div>
                </VerticalScrollbars>

            </div>
        );
    }
}

function mapDispatchToProps(dispatch) {
    return {
        onHighlightClick: (systemId) => {
            const action = { type: "HIGHLIGHT", system: systemId };
            dispatch(action);
        }
    }
}

export default connect(null, mapDispatchToProps)(Systems);
