import React, { Component } from 'react';
import search from '../../../_assets/images/menu/search.svg';
import VerticalScrollbars from '../../../_assets/Scrollbars/VerticalScrollbars';
import { Collapse } from 'reactstrap';
import { library } from '@fortawesome/fontawesome-svg-core';
import subsystem from '../../../_assets/images/subsystem.png';
import services from '../../../_assets/images/services.png';
import reference from '../../../_assets/images/reference.png';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes, faPlus, faChevronDown } from "@fortawesome/free-solid-svg-icons";
library.add(faTimes, faPlus, faChevronDown);

export default class Systems extends Component {
  constructor(props) {
    super(props);
    this.systemsToggle = this.systemsToggle.bind(this);
    this.flowsToggle = this.flowsToggle.bind(this);
    this.toggle = this.toggle.bind(this);
    this.toggle1 = this.toggle1.bind(this);
    this.toggle2 = this.toggle2.bind(this);
    this.toggle3 = this.toggle3.bind(this);
    this.toggle4 = this.toggle4.bind(this);
    this.toggle5 = this.toggle5.bind(this);
    this.toggle00 = this.toggle00.bind(this);
    this.toggle11 = this.toggle11.bind(this);
    this.toggle12 = this.toggle12.bind(this);
    this.toggle13 = this.toggle13.bind(this);
    this.toggle14 = this.toggle14.bind(this);
    this.toggle15 = this.toggle15.bind(this);

    this.state = {
      flowsCollapse: true,
      systemsCollapse: true,
      collapse: true,
      collapse1: true,
      collapse2: false,
      collapse3: false,
      collapse4: false,
      collapse5: false,
      collapse00: false,
      collapse11: false,
      collapse12: false,
      collapse13: false,
      collapse14: false,
      collapse15: false,
      systemData: require("../../../_temp/systems.json"),
      flowsData: require("../../../_temp/flows.json"),
      isEmptySystem: true
    };

  }

  componentDidMount() {
    console.log(this.state.systemData.topLevelSystems)

    //cthis.state.systemData.landscapeModels)
    //   axios({
    //     method: 'get',
    //     url: 'model/f5f56e0c-ecff-459d-bace-15c6d37f9e43/landscape/systems',
    //     auth: {
    //       username: process.env.REACT_APP_BAUSR,
    //       password: process.env.REACT_APP_BAPWD
    //     }
    // })
    // .then(response => {
    //   console.log(response);
    // })
  }

  showSystem = () => {
    this.setState({
      //...this.state,
      isEmptySystem: false,
      //isAddTripState: true
    })
  }

  flowsToggle() {
    this.setState(state => ({ flowsCollapse: !state.flowsCollapse }));
  }
  systemsToggle() {
    this.setState(state => ({ systemsCollapse: !state.systemsCollapse }));
  }
  toggle() {
    this.setState(state => ({ collapse: !state.collapse }));
  }
  toggle1() {
    this.setState(state => ({ collapse1: !state.collapse1 }));
  }
  toggle2() {
    this.setState(state => ({ collapse2: !state.collapse2 }));
  }
  toggle3() {
    this.setState(state => ({ collapse3: !state.collapse3 }));
  }
  toggle4() {
    this.setState(state => ({ collapse4: !state.collapse4 }));
  }
  toggle5() {
    this.setState(state => ({ collapse5: !state.collapse5 }));
  }
  toggle00() {
    this.setState(state => ({ collapse00: !state.collapse00 }));
  }
  toggle11() {
    this.setState(state => ({ collapse11: !state.collapse11 }));
  }
  toggle12() {
    this.setState(state => ({ collapse12: !state.collapse12 }));
  }
  toggle13() {
    this.setState(state => ({ collapse13: !state.collapse13 }));
  }
  toggle14() {
    this.setState(state => ({ collapse14: !state.collapse14 }));
  }
  toggle15() {
    this.setState(state => ({ collapse15: !state.collapse15 }));
  }
  render() {
    return (

      <VerticalScrollbars style={{ width: 350 }} autoHide>
        <div className="org_browser">
          <h3 className="text-center text-white">Systems and Flows <i class="fas fa-chevron-down system-down-icon" /><i class="fas fa-plus system-down-icon" /></h3>
          <div className="org-selector">
            <p className="text-white text-left">Selector</p>
            <input type="text" className="search-input" />
            <img src={search} alt="starlify" className="search-icon" />
          </div>
          <div className="systems w-100 d-inline-block">

            <VerticalScrollbars style={{ height: 220 }}>
              <p className="text-white click" onClick={this.systemsToggle} style={{ marginBottom: '1rem' }}>Systems <i class="fas fa-chevron-down system-down-icon" /><i class="fas fa-plus system-down-icon" /></p>
              <Collapse isOpen={this.state.systemsCollapse}>
                {this.state.systemData.topLevelSystems.map((item, index) => {
                  return (<div>
                    <p className="text-grey click" onClick={this.toggle} style={{ marginBottom: '1rem', marginLeft: '1rem' }}><img src={subsystem} alt="starlify" className="m-r-15 m-b-5 " />{item.name}</p>
                    <Collapse isOpen={this.state.collapse}>
                      {item.subSystems ? item.subSystems.map((subitem, i) => {
                        return (<div>
                          <p className="text-grey click" onClick={this.toggle1} style={{ marginBottom: '1rem', marginLeft: '2rem' }}><img src={subsystem} alt="starlify" className="m-r-15 m-b-5 " />{subitem.name}</p>
                          <Collapse isOpen={this.state.collapse1}>
                            {subitem.providedEndpoints ? subitem.providedEndpoints.map((service, i) => {
                              return (<div>
                                <p className="text-grey click" style={{ marginBottom: '1rem', marginLeft: '3rem' }}><img src={services} alt="starlify" className="m-r-15 m-b-5 " />{service.descriptions.externalName}</p>
                                {/* <p className="text-grey click" style={{ marginBottom: '1rem', marginLeft: '3rem' }}><img src={services} alt="starlify" className="m-r-15 m-b-5 " />Service</p> */}

                              </div>
                              )
                            }) : ''}
                            {subitem.consumedEndpoints ? subitem.consumedEndpoints.map((referenceData, i) => {
                              return (<p className="text-grey click" style={{ marginBottom: '1rem', marginLeft: '3rem' }}><img src={reference} alt="starlify" className="m-r-15 m-b-5 " />{referenceData.modeledEntityType}</p>
                              )
                            }) : ''}
                          </Collapse></div>)
                      }) : ''}
                    </Collapse>
                  </div>)
                })}
              </Collapse>
            </VerticalScrollbars>
          </div>
          <div className="flows">
            <VerticalScrollbars style={{ height: 200 }}>
              <p className="text-white click" onClick={this.flowsToggle} style={{ marginBottom: '1rem' }}>Flows <i class="fas fa-chevron-down system-down-icon" /><i class="fas fa-plus system-down-icon" /></p>
              <Collapse isOpen={this.state.flowsCollapse}>
                {this.state.flowsData.nodes.map((item, index) => {
                  return (
                    <div>
                      <p className="text-grey click" style={{ marginBottom: '1rem', marginLeft: '2rem' }}>{item.properties.name}</p>
                    </div>
                  )
                })
                }

              </Collapse>
            </VerticalScrollbars>
          </div>
        </div>
      </VerticalScrollbars>
    )
  }
}
