import React, { Component } from "react";
import { Table, Modal, ModalBody, Input, FormGroup, Col, Row, Collapse } from "reactstrap";
import pen from "../../../_assets/icons/icons/edit-icon.svg";
import check from "../../../_assets/icons/icons/check-icon.svg";
import { connect } from "react-redux";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";

class ModalAcess extends Component {
  constructor(props) {
    super(props);
    this.close = this.close.bind(this);
    this.fetchDivisions = this.fetchDivisions.bind(this);
    this.fetchTeams = this.fetchTeams.bind(this);
    this.fetchPersons = this.fetchPersons.bind(this);
    this.fetchModelGrants = this.fetchModelGrants.bind(this);
    this.grantToggle = this.grantToggle.bind(this);
    this.state = {
      modeluri: "",
      model: null,
      models: [],
      divisions: [],
      grants: [],
      accessEditor: false,
      popup: false,
      eventType: null,
      grantModal: false
    };
  }
  grantToggle() {
    this.setState(prevState => ({
      grantModal: !prevState.grantModal
    }));
  }

  close(type) {
    this.props.close(type);
  }

  modalToggle = () => {
    this.setState({
      popup: !this.state.popup,
      eventType: null
    });
  };

  selectModel = e => {
    this.setState({ modeluri: e.target.value });
    fetch(process.env.REACT_APP_API + e.target.value)
      .then(res => res.json())
      .then(result => {
        this.setState({
          model: result,
          accessEditor: true
        });
      })
      .catch(error => {
        this.setState({
          model: null,
          accessEditor: false
        });
      });
      var modelArr = e.target.value.split("/");
      this.fetchModelGrants(modelArr[2]);
      this.fetchDivisions(this.props.domain);
  };

  fetchModelGrants = modelId => {
    fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + this.props.domain + "/grant/" + modelId )
      .then(res => res.json())
      .then(json => {
        this.setState({ grants: json });
      });
  };

  fetchDivisions = domainId => {
    return fetch(process.env.REACT_APP_API + "/hypermedia/domain/" + domainId)
      .then(res => res.json())
      .then(result => {

        var org = result.organisation;
        fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + domainId + "/organisation/" + org.id + "/organisations" )
          .then(res => res.json())
          .then(json => {
            var organisations = json;
            for (var i = 0; i < organisations.length; i++) {
              let org = organisations[i];
              org["teams"] = [];
              if (org._links && org._links.length !== 0 && this.state.model !== null) {
                this.fetchTeams(domainId, this.state.model.id, org.id).then(
                  teams => (org["teams"] = teams)
                );
              }
            }
            this.setState({
              divisions: organisations
            });
            return true;
          });

      });
  };

  fetchTeams = (domainId, modelId, orgid) => {
    return fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + domainId + "/organisation/" + orgid + "/organisations" )
      .then(res => res.json())
      .then(json => {
        var organisations = json;
        for (var i = 0; i < organisations.length; i++) {
          let org = organisations[i];
          org["persons"] = [];
          if (org._links && org._links.length !== 0) {
            this.fetchPersons(domainId, modelId, org.id).then(
              persons => (org["persons"] = persons)
            );
          }
        }
        return organisations;
      });
  };

  fetchPersons = (domainId, modelId, orgid) => {
    return fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + domainId + "/organisation/" + orgid + "/persons" )
      .then(res => res.json())
      .then(persons => {
        return persons;
      });
  };

  onGrantChange = (e, grantid) => {
    if (e.target.checked) {
      this.addGrantAccess(
        this.props.domain,
        this.state.model.id,
        grantid,
        e.target.value
      );
    } else {
      this.deleteGrantAccess(
        this.props.domain,
        this.state.model.id,
        grantid,
        e.target.value
      );
    }
  };

  addGrantAccess = (domainId, modelId, grantid, value) => {
    fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + domainId + "/grant/" + modelId + "/" + grantid + "/permission/" + value, { method: "POST" } )
      .then(result => {
        console.log("added", result);
        this.fetchModelGrants(this.state.model.id);
      })
      .catch(error => {
        console.log(error);
      });
  };

  deleteGrantAccess = (domainId, modelId, grantid, value) => {
    fetch( process.env.REACT_APP_API + "/hypermedia/domain/" + domainId + "/grant/" + modelId + "/" + grantid + "/permission/" + value, { method: "DELETE" } )
      .then(result => {
        console.log("deleted", result);
        this.fetchModelGrants(this.state.model.id);
      })
      .catch(error => {
        console.log(error);
      });
  };

  componentDidMount() {
    var domainID = this.props.domain;
    if (domainID !== "") {
      fetch(process.env.REACT_APP_API + "/domain/" + domainID)
        .then(res => res.json())
        .then(result => {
          this.setState({
            models: result.links
          });
        })
        .catch(error => {
          this.setState({ models: [] });
        });
    }
  }

  componentWillReceiveProps(newProps) {
    const { domain, type } = newProps;
    switch (type) {
      case "SELECTDOMAIN":
        this.setState({
          modeluri: "",
          model: null,
          models: [],
          divisions: [],
          grants: [],
          accessEditor: false,
          popup: false,
          eventType: null
        });
        if (domain !== "") {
          fetch(process.env.REACT_APP_API + "/domain/" + domain)
            .then(res => res.json())
            .then(result => {
              this.setState({
                models: result.links
              });
            })
            .catch(error => {
              this.setState({ models: [] });
            });
        }
        break;
      default:
        break;
    }
  }

  render() {
    const models = this.state.models;
    console.log(this.state.divisions);
    return (
      <div className="browser">
        <Modal isOpen={this.state.popup} toggle={this.modalToggle} className="selectModal" >
          <ModalBody>
            {this.state.eventType === "model-choose" && (
              <div>
                  <h3 className="d-inline-block modal-title">Choose model</h3>
                  <button className="btn grey-btn modal-top-btn" onClick={this.modalToggle}> Done <img src={check} alt="" /> </button>
                  <p className="text-white text-size-16">
                    Choose which model you want to edit
                  </p>
                  <div className="model-list">
                    <ul>
                      {models.map((item, i) => (
                        <li  key={"models_" + i} className="d-flex">
                          <Col md="8" className="modelName"> {item.title} </Col>
                          <Col md="4">
                            <input type="radio" id={"mod_opt"+i} name="modelUri" value={item.uri} checked={this.state.modeluri === item.uri} onChange={this.selectModel} />
                            <label for={"mod_opt"+i}></label>
                            <div class="check"></div>
                          </Col>
                        </li>
                      ))}
                    </ul>
                  </div>
              </div>
            )}




          </ModalBody>
        </Modal>
        <Modal isOpen={this.state.grantModal} toggle={this.grantToggle}>
          <ModalBody>
            <h3 className="d-inline-block modal-title">
              Edit access for Division 3
            </h3>
            <button className="btn grey-btn modal-top-btn">
              Done <img src={check} alt="" />
            </button>
            <div className="modalAccesListing">
              <div className="browser_body">
                <div className="division_model">
                  <div className="browser_body_content">
                    <div>
                      <ul className="modelListWrapper">
                        <li className="modelListHeader text-white">
                          <Row>
                            <Col sm="5"></Col>
                            <Col sm="3" className="text-center">
                              Read
                            </Col>
                            <Col sm="3" className="text-center">
                              Write
                            </Col>
                            <Col sm="1"></Col>
                          </Row>
                        </li>
                        <li className="modelListbody text-white divisionListItem">
                          <Row>
                            <Col sm="5" className="divisionitemtext">
                              Division
                            </Col>
                            <Col sm="3" className="text-center">
                              <input
                                type="checkbox"
                                id="checkbox-1-1"
                                class="regular-checkbox"
                              />
                              <label for="checkbox-1-1"></label>
                            </Col>
                            <Col sm="3" className="text-center">
                              <input
                                type="checkbox"
                                id="checkbox-1-2"
                                class="regular-checkbox"
                              />
                              <label for="checkbox-1-2"></label>
                            </Col>
                            <Col sm="1"></Col>
                          </Row>
                        </li>
                        <li className="text-white subModelListbody">
                          <ul className="teamListWrapper">
                            <li className="teamListItem">
                              <Row>
                                <Col sm="5" className="teamitemtext">
                                  Team1
                                </Col>
                                <Col sm="3" className="text-center">
                                  <input
                                    type="checkbox"
                                    id="checkbox-2-1"
                                    class="regular-checkbox"
                                  />
                                  <label for="checkbox-2-1"></label>
                                </Col>
                                <Col sm="3" className="text-center">
                                  <input
                                    type="checkbox"
                                    id="checkbox-2-2"
                                    class="regular-checkbox"
                                  />
                                  <label for="checkbox-2-2"></label>
                                </Col>
                                <Col sm="1"></Col>
                              </Row>
                            </li>
                            <li>
                              <ul className="developerListWrapper">
                                <li className="developerListItem">
                                  <Row>
                                    <Col sm="5" className="developeritemtext">
                                      Developer1
                                    </Col>
                                    <Col sm="3" className="text-center">
                                      <input
                                        type="checkbox"
                                        id="checkbox-3-1"
                                        class="regular-checkbox"
                                      />
                                      <label for="checkbox-3-1"></label>
                                    </Col>
                                    <Col sm="3" className="text-center">
                                      <input
                                        type="checkbox"
                                        id="checkbox-3-2"
                                        class="regular-checkbox"
                                      />
                                      <label for="checkbox-3-2"></label>
                                    </Col>
                                    <Col sm="1"></Col>
                                  </Row>
                                </li>
                                <li className="developerListItem">
                                  <Row>
                                    <Col sm="5" className="developeritemtext">
                                      Developer2
                                    </Col>
                                    <Col sm="3" className="text-center">
                                      <input
                                        type="checkbox"
                                        id="checkbox-4-1"
                                        class="regular-checkbox"
                                      />
                                      <label for="checkbox-4-1"></label>
                                    </Col>
                                    <Col sm="3" className="text-center">
                                      <input
                                        type="checkbox"
                                        id="checkbox-4-2"
                                        class="regular-checkbox"
                                      />
                                      <label for="checkbox-4-2"></label>
                                    </Col>
                                    <Col sm="1"></Col>
                                  </Row>
                                </li>
                              </ul>
                            </li>
                          </ul>
                        </li>
                      </ul>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </ModalBody>
        </Modal>
        {this.state.accessEditor === false && (
          <div className="modalAccesListing">
            <div className="browser_header text-white">
              <div>
                <i className="fas fa-key submenu-icon"></i>
                <h3 className="d-inline-block">Models Access</h3>
                <i
                  className="fas fa-times system-down-icon"
                  onClick={() => this.close("modelAccess")}
                />
              </div>
              <div className="text-right modelChooseWrapper">
                <a
                  className="modelChoose raleway"
                  onClick={() => { this.setState({ eventType: "model-choose", popup: true }); }}
                >
                  Choose model
                </a>
              </div>
            </div>
            <div className="browser_body">
              <p className="text-center text-white m-t-65 text-size-18">
                So you need to give access to your division, teams or
                developers?
                <br />
                You’ve come to the right place.
              </p>
              <p className="text-center text-white m-t-65 text-size-18">
                Just click the Choose model button and choose what <br />
                model you need to work in and then you can start adding <br />
                your division, teams and developers.
              </p>
              <h1 className="text-center text-white m-t-65">No chosen model</h1>
            </div>
          </div>
        )}

        {this.state.accessEditor === true && (
          <div className="modalAccesListing">
            <div className="browser_header text-white">
              <div>
                <i className="fas fa-key submenu-icon"></i>
                <h3 className="d-inline-block">Models Access</h3>
                <i
                  className="fas fa-times system-down-icon"
                  onClick={() => this.close("modelAccess")}
                />
              </div>
              <p>
                Edit models access for <span>{this.state.model.name}</span>
              </p>
              <div className="text-right modelChooseWrapper">
                <a
                  className="d-inline-block modelChoose"
                  onClick={() => { this.setState({ eventType: "model-choose", popup: true }); }}
                >
                  Change model
                </a>
              </div>
            </div>
            <div className="browser_body">
              <div className="division_model">
                <div className="browser_body_header text-white">
                  <h4 className="d-inline-block">Divisions</h4>
                </div>
                <div className="browser_body_content">
                  <div>
                    <ul className="modelListWrapper">
                      <li className="modelListHeader text-white">
                        <Row>
                          <Col sm="5"></Col>
                          <Col sm="3" className="text-center"> Read </Col>
                          <Col sm="3" className="text-center"> Write </Col>
                          <Col sm="1"></Col>
                        </Row>
                      </li>

                      {this.state.divisions && this.state.divisions.length !== 0 && this.state.divisions.map((division, i) => {
                          var divRead = false;
                          var divWrite = false;
                          this.state.grants.forEach((item) => {
                            if (item.principal.id === division.id) {
                              divRead = item.permissions.includes( "RESOURCE_READ" ) ? true : false;
                              divWrite = item.permissions.includes( "RESOURCE_ANY" ) ? true : false;
                            }
                          });
                          return (
                            <li key={"div_key"+i} className="text-white subModelListbody">
                              <ul className="teamListWrapper">
                                <li className="teamListItem">
                                  <Row>
                                    <Col sm="5" className="teamitemtext"> {division.name} </Col>
                                    <Col sm="3" className="text-center">
                                      <i className={divRead ? "fas fa-check" : "fas fa-times"}></i>
                                    </Col>
                                    <Col sm="3" className="text-center">
                                      <i className={divWrite ? "fas fa-check" : "fas fa-times"}></i>
                                    </Col>
                                    <Col sm="1"> <i className="fas fa-pen"></i> </Col>
                                  </Row>
                                </li>
                                <li>
                                  <ul className="developerListWrapper">
                                    {
                                      division.teams.map((team) => {
                                        var tmRead = false;
                                        var tmWrite = false;
                                        this.state.grants.forEach((item) => {
                                          if (item.principal.id === team.id) {
                                            tmRead = item.permissions.includes( "RESOURCE_READ" ) ? true : false;
                                            tmWrite = item.permissions.includes( "RESOURCE_ANY" ) ? true : false;
                                          }
                                        });
                                        return (
                                          <li className="developerListItem">
                                            <Row>
                                              <Col sm="5" className="developeritemtext"> {team.name} </Col>
                                              <Col sm="3" className="text-center">
                                                <i className={tmRead ? "fas fa-check" : "fas fa-times"}></i>
                                              </Col>
                                              <Col sm="3" className="text-center">
                                                <i className={tmWrite ? "fas fa-check" : "fas fa-times"}></i>
                                              </Col>
                                            </Row>
                                          </li>
                                        )
                                      })
                                    }
                                  </ul>
                                </li>
                              </ul>
                            </li>
                          );
                        })}

                    </ul>
                  </div>
                  <Table className="text-white">
                    <thead>
                      <tr>
                        <th width="50%"></th>
                        <th width="25%" className="text-white text-small">
                          Read
                        </th>
                        <th width="25%" className="text-white text-small">
                          Write
                        </th>
                      </tr>
                    </thead>
                    <tbody>
                      {this.state.divisions &&
                        this.state.divisions.length !== 0 &&
                        this.state.divisions.map((division, index) => {
                          const perms = ["RESOURCE_ANY", "RESOURCE_READ"];
                          var divRead = false;
                          var divWrite = false;
                          this.state.grants.forEach((item, i) => {
                            if (item.principal.id === division.id) {
                              divRead = perms.some(r => item.permissions.includes(r) ) ? "checked" : "";
                              divWrite = item.permissions.includes( "RESOURCE_ANY" ) ? "checked" : "";
                            }
                          });
                          return (
                            <div className="tablewrapper">
                              <tr key={division.id}>
                                <td
                                  width="50%"
                                  className="text-grey text-left division-table-name"
                                >
                                  {" "}
                                  {division.name}{" "}
                                </td>
                                <td
                                  width="25%"
                                  className="division-table-check"
                                >
                                  <div className="customCheckbox">
                                    <input
                                      type="checkbox"
                                      id={"read_" + division.id}
                                      checked={divRead}
                                      defaultValue="RESOURCE_READ"
                                      onChange={e => {
                                        this.onGrantChange(e, division.id);
                                      }}
                                    />
                                    <label
                                      htmlFor={"read_" + division.id}
                                    ></label>
                                  </div>
                                </td>
                                <td
                                  width="25%"
                                  className="division-table-check"
                                >
                                  <div className="customCheckbox">
                                    <input
                                      type="checkbox"
                                      defaultValue="RESOURCE_ANY"
                                      id={"write_" + division.id}
                                      checked={divWrite}
                                      onChange={e => {
                                        this.onGrantChange(e, division.id);
                                      }}
                                    />
                                    <label
                                      htmlFor={"write_" + division.id}
                                    ></label>
                                  </div>
                                </td>
                              </tr>

                            </div>
                          );
                        })}
                    </tbody>
                  </Table>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    domain: state.model.domain,
    type: state.model.type
  };
}

export default connect(mapStateToProps)(ModalAcess);
