import React, { Component } from "react";
import ReactDOM from "react-dom";
import { Col } from "reactstrap";
import photo from "../../_assets/images/photo.jpg";
import Organisation from "../../pages/dashboard/browsers/Organisation";
import Systems from "../../pages/dashboard/browsers/Systems";

export default class Header extends Component {
  constructor(props) {
		super(props);
		this.state = {
			panelIndex: 0
		};
	}

	getBtnId = (e) => {
		if(e.target && e.target.nodeName == "BUTTON") {
			console.log(e.target);
			this.setState({
				panelIndex: Number(e.target.id)
			});
	  }
	};

	render() {
		return (
			<div className="container">
				<HelpMenu
					getBtnId={this.getBtnId}
				/>
				<HelpPanels
					panelIndex={this.state.panelIndex}
				/>
			</div>
		)
	}
}

const HelpMenu = ({ getBtnId }) => {
	return (
		<div 
			className="btnGroup"
			onClick={getBtnId}
		>
			<button id="0">Option 1</button>
			<button id="1">Option 2</button>
			<button id="2">Option 3</button>
			<button id="3">Option 4</button>
		</div>
	);
};

class HelpPanels extends React.Component {

	render() {
		const panels = [
			<HelpPanel1 />,
			<HelpPanel2 />,
			<HelpPanel3 />,
			<HelpPanel4 />
		];
		const correctPanel = panels[this.props.panelIndex];
		return (
			<div className="panel-box">
			{correctPanel}
			</div>
		);
	}
}

const HelpPanel1 = () => (
	<h1>Panel for Option 1</h1>
);
const HelpPanel2 = () => (
	<h1>Panel for Option 2</h1>
);
const HelpPanel3 = () => (
	<h1>Panel for Option 3</h1>
);
const HelpPanel4 = () => (
	<h1>Panel for Option 4</h1>
);

ReactDOM.render(
	<HelpPage />,
	document.getElementById('root')
);
