import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { Popover, PopoverBody, Collapse } from "reactstrap";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { connect } from 'react-redux';
import { Modal, ModalBody } from "reactstrap";
import { fetchSystemDetils } from '../../../store/system/systemActions'

function collect(props) {
  return { item:props.item};
}

class SystemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: true,
      collapseId: false,
      modal: false,
    };
    this.close = this.close.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.toggleId = this.toggleId.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
    this.updateSystemDetails = this.updateSystemDetails.bind(this);
  }

  updateSystemDetails(event) {
    let value = event.target.value;
        if ((event.key === 'Enter' || event.type === 'blur') && this.state.modal && value) { 
            event.target.value = '';
            this.setState(prevState => ({
                modal: !prevState.modal
            }));
            fetch(
                process.env.REACT_APP_API + `${this.props.activeModel}/system/` + this.props.details.id,
                {
                    method: "PATCH",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify({ description: value })
                }
            )
            .then(response => {
                this.props.updateSystemDetails(this.props.activeModel, this.props.details.id)
            }
         )
        }

  }

  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  toggleId() {
    this.setState({
      collapseId: !this.state.collapseId
    });
  }

  //Closes the system details section
  close(menu, type) {
    this.props.close(menu, type);
  }

  //control right click modal
  modalToggle(e, data) {
    console.log(data)
    this.setState(prevState => ({
        modal: !prevState.modal
    }));

    if (data) {
        this.setState({
            item: data.item,
        })

    }
}

  render() {
    return (
      <div className="org_browser">
        <ContextMenu id="systemdetail_right_click">
        <MenuItem data={{ itemType: 'rename' }} onClick={this.modalToggle}>
          Update
      </MenuItem>
      </ContextMenu>
      <Modal isOpen={this.state.modal} toggle={this.modalToggle} >
                    <ModalBody>
                            <div>
                            <textarea onBlur={this.updateSystemDetails.bind(this)} onKeyDown={this.updateSystemDetails.bind(this)}>
                            {this.props.details.description}
                            </textarea>
                            </div>
                    </ModalBody>
       </Modal>
        <h3 className="text-center text-white">
          System Details
          <i
            className="fas fa-times system-down-icon browser_close"
            onClick={() => this.close("systemDetail", "menuDetail")}
          />
        </h3>
        <VerticalScrollbars autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left">
                {/* {this.state.systemDetails.name} */}
              </p>
            </div>
            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">
                  <span id="PopoverId">{this.props.details.name}</span>

                  <i
                    className="fas fa-chevron-down system-down-icon"
                    onClick={this.toggleDescription}
                  />
                </p>
                <Popover
                  placement="bottom"
                  isOpen={this.state.collapseId}
                  target="PopoverId"
                  toggle={this.toggleId}
                >
                  {/* <PopoverHeader>Popover Title</PopoverHeader> */}
                  <PopoverBody>{this.props.details.id}</PopoverBody>
                </Popover>
              </div>
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">Support lvl: Gold</p>
              </div>
              <Collapse
                isOpen={this.state.collapse}
                className="systemDetailsDisc"
              >
                <ContextMenuTrigger id="systemdetail_right_click" item={this.props.details} collect={collect}>
                <p className="text-grey">
                {this.props.details.description ? this.props.details.description : 'No Description'}
                </p>
                </ContextMenuTrigger>
              </Collapse>
            </div>
            {/* <div className="developer w-100 d-inline-block">
            <p className="text-white text-left">Developer</p>
            <ul className="text-left">
              <li className="developer-item">
                Name Surname <span className="developer-img" />
              </li>
            </ul>
          </div>
          <div className="ob-block row">
            <p className="text-white text-left col-md-12">Subsystem</p>
            <div className="col-md-6">
              <p className="text-grey text-left">
                <img src={subsystem} alt="starlify" className="m-r-15 m-b-5 " />
                System
              </p>
            </div>
            <div className="col-md-6">
              <p className="text-grey text-left">ID:039982397</p>
            </div>
          </div> */}
          <div className="ob-block row">
              <p className="text-white text-left col-md-12">Sub systems</p>
              {this.props.details.subsystemLinks
                ? this.props.details.subsystemLinks.map((subsystem, i) => {
                    return (
                      <div key={subsystem.id} className="d-flex w-100">
                        <div className="col-md-12">

                          <p className="text-grey text-left">
                          <span className="systemCircle" />
                            {subsystem.title}
                          </p>

                        </div>

                      </div>
                    );
                  })
                : ""}
            </div>
            <div className="ob-block row">
              <p className="text-white text-left col-md-12">Services</p>
              {this.props.details.services
                ? this.props.details.services.map((services, i) => {
                    return (
                      <div key={services.id} className="d-flex w-100">
                        <div className="col-md-12">
                          <p className="text-grey text-left">
                            <span className="serviceCircle" />

                            {services.name}
                          </p>

                        </div>

                      </div>
                    );
                  })
                : ""}
            </div>
            <div className="ob-block row">
              <p className="text-white text-left col-md-12">References</p>
              {this.props.details.references
                ? this.props.details.references.map((references, i) => {
                    return (
                      <div className="d-flex w-100">
                        <div className="col-md-12">
                          <p className="text-grey text-left">
                            <span className="referenceArrow">â†’</span>
                            {references.name}
                          </p>
                        </div>
                        {/* <div className="col-md-6">
                        <p className="text-grey text-left">{references.id}</p>
                      </div> */}
                      </div>
                    );
                  })
                : ""}
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}
function mapDispatchToProps(dispatch) {
  return {
        updateSystemDetails: (modelId, systemId) => {
          dispatch(fetchSystemDetils(modelId, systemId));
        }
  }
}

function mapStateToProps(state){
  return{
    activeSystem: state.model.activeSystem,
    activeModel: state.model.modelUri,
    details: state.system.details,
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SystemDetails);
