import React, { Component } from "react";
import { fetchFlowDetils } from "../../../store/system/systemActions";
import { connect } from "react-redux";
import { Modal, ModalBody, FormGroup, Input } from "reactstrap";
import Select from "react-select";
import check from "../../../_assets/icons/icons/check-icon.svg";
import addCircle from "../../../_assets/icons/icons/add-icon-circle.svg";
import sequence from "../../../_assets/images/Sequence-diagram.png";
import edit from "../../../_assets/icons/icons/edit.svg";

class FlowDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {},
      formData: {
        name: "",
        description: "",
        supportLevel: "",
        custom: { documentationUrl: "" }
      },
      filterOptions: [],
      invocations: [],
      referenceData: {
        consumerSystem: "",
        consumedService: "",
        referenceName: ""
      },
      servicesList: "",
      invocationsList: []
    };

    this.close = this.close.bind(this);
    this.toggle = this.toggle.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
  }

  //show or hide tool tip
  toggle = target => {
    this.setState({
      ...this.state,
      [target]: !this.state[target]
    });
  };

  handleUrlChange(event) {
    const { formData } = this.state;
    formData[event.target.name] = { documentationUrl: event.target.value };
    this.setState({
      formData
    });
  }

  handleChange(event) {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({
      formData
    });
  }

  handleReferenceChange(event) {
    const { referenceData } = this.state;
    referenceData[event.target.name] = event.target.value;
    this.setState({
      referenceData
    });
    console.log(referenceData);
  }

  openAddReference(event) {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({
      formData
    });
  }

  updateFlow() {
    fetch(
      process.env.REACT_APP_API +
        `${this.props.activeModel}/flow/` +
        this.props.activeFlow,
      {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(this.state.formData)
      }
    )
      .then(response => {
        if (this.state.deletedSourceSystems.length > 0)
          this.deleteSourceSystems();
        if (this.state.deletedTargetSystems.length > 0)
          this.deleteTargetSystems();
        this.addSourceSystems();
        this.addTargetSystems();
        setTimeout(() => {
          this.props.getFlowDetails(
            this.props.activeModelId,
            this.props.activeFlow
          );
          this.setState(prevState => ({
            modal: !prevState.modal
          }));
        }, 2000);
      })
      .catch(error => console.log(error));
  }

  addSourceSystems() {
    console.log(this.state.sourceSystems);
    this.state.sourceSystems.map((system, i) => {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/network/" +
          this.props.activeModelId +
          "/flow/" +
          this.props.activeFlow +
          "/source/" +
          system.value,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => {})
        .catch(error => console.log(error));
      return true;
    });
  }

  addTargetSystems() {
    this.state.targetSystems.map((system, i) => {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/network/" +
          this.props.activeModelId +
          "/flow/" +
          this.props.activeFlow +
          "/target/" +
          system.value,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => {})
        .catch(error => console.log(error));
      return true;
    });
  }

  deleteSourceSystems() {
    this.state.deletedSourceSystems.map((system, i) => {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/network/" +
          this.props.activeModelId +
          "/flow/" +
          this.props.activeFlow +
          "/source/" +
          system.value,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => {})
        .catch(error => console.log(error));
      return true;
    });
  }

  deleteTargetSystems() {
    console.log(this.state.deletedTargetSystems);
    this.state.deletedTargetSystems.map((system, i) => {
      fetch(
        process.env.REACT_APP_API +
          "/hypermedia/network/" +
          this.props.activeModelId +
          "/flow/" +
          this.props.activeFlow +
          "/target/" +
          system.value,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => {})
        .catch(error => console.log(error));
      return true;
    });
  }

  handleSourceChange(option, event) {
    console.log(option);
    let difference = this.state.sourceSystems.filter(x => !option.includes(x)); // calculates diff
    console.log(difference);
    if (difference.length > 0) {
      this.state.deletedSourceSystems.push(difference[0]);
    }
    this.setState(state => {
      return {
        sourceSystems: option
      };
    });
  }

  handleTargetChange(option) {
    let difference = this.state.targetSystems.filter(x => !option.includes(x)); // calculates diff
    if (difference.length > 0) {
      this.state.deletedTargetSystems.push(difference[0]);
    }
    this.setState(state => {
      return {
        targetSystems: option
      };
    });
  }

  //show or hide description
  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  //closes the detail window
  close(type) {
    this.props.close(type);
  }
  componentWillReceiveProps(nextProps) {
    if (this.props.activeFlow !== nextProps.activeFlow)
      this.props.getFlowDetails(this.props.activeModelId, nextProps.activeFlow);
  }

  //get flow details
  componentWillMount() {




    this.props.getFlowDetails(this.props.activeModelId, this.props.activeFlow);

  }

  displayInvocation(invocationList) {
      console.log(invocationList)
      invocationList.map((invocation, i) => {
      fetch(
        process.env.REACT_APP_API +
          `/hypermedia/network/${this.props.activeModelId}/system/${this.props.activeFlow}/reference/${invocation.params.id}`,
        {
          method: "GET"
        }
      )
        .then(response => response.json())
        .then(responseJson => {
          var consumer, service, provider;
          responseJson._links
            .filter(links => links.rel === "OF")
            .map((referenceData, i) => {
              consumer = referenceData.title;
            });
          responseJson._links
            .filter(links => links.rel === "TO")
            .map((serviceData, i) => {
              service = serviceData.title;
              fetch(process.env.REACT_APP_API + serviceData.uri, {
                method: "GET"
              })
                .then(response => response.json())
                .then(responseJson => {console.log(responseJson)
                  responseJson._links
                    .filter(links => links.rel === "OF")
                    .map((systemData, i) => {
                      provider = systemData.title;
                    });
                    this.setState({'test':'test'})
                  this.state.invocations.push({
                    consumer: consumer,
                    service: service,
                    provider: provider,
                    referenceId: invocation.params.id
                  });
                  console.log(this.state.invocations);
                })
                .catch(error => console.log(error));
            });
        })
        .catch(error => console.log(error));
    });
  }

  //update flow details
  updateFlowDetails(event) {
    let value = event.target.value;
    if (
      (event.key === "Enter" || event.type === "blur") &&
      this.state.modal &&
      value
    ) {
      event.target.value = "";
      this.setState(prevState => ({
        modal: !prevState.modal
      }));
      fetch(
        process.env.REACT_APP_API +
          `${this.props.activeModel}/flow/` +
          this.props.activeFlow,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ description: value })
        }
      )
        .then(response => {
          this.props.getFlowDetails(
            this.props.activeModel,
            this.props.activeFlow
          );
        })
        .catch(error => console.log(error));
    }
  }

  //Delete entity
  deleteReference(referenceId, event) {
    return fetch(
      process.env.REACT_APP_API +
        `/hypermedia/network/${
          this.props.activeModelId
        }/${"modeled"}/${referenceId}`,
      {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json"
        }
      }
    ).then(response => {});
  }

  //control right click modal
  modalToggle(e, data) {
    this.state.sourceSystems = [];
    this.state.targetSystems = [];
    this.state.deletedSourceSystems = [];
    this.state.deletedTargetSystems = [];
    this.setState(prevState => ({
      modal: !prevState.modal
    }));

    this.props.flowDetails._links
      .filter(links => links.rel === "SOURCE")
      .map(source => {console.log(source)
        this.state.sourceSystems.push({
          value: source.params.id,
          label: source.title
        });
      });

    this.props.flowDetails.invocations
      .map(invocation => {console.log(invocation._links)
        invocation._links
      .filter(links => links.rel === "THROUGH")
      .map(reference => {
          this.state.invocationsList.push(reference)
      });

      });
     this.displayInvocation(this.state.invocationsList)

      this.props.flowDetails._links
      .filter(links => links.rel === "TARGET")
      .map(target => {
        this.state.targetSystems.push({
          value: target.params.id,
          label: target.title
        });
      });  

    this.setState({
      formData: {
        name: this.props.flowDetails.name,
        description: this.props.flowDetails.description,
        supportLevel: this.props.flowDetails.supportLevel,
        custom: {
          documentationUrl: this.props.flowDetails.custom.documentationUrl
        }
      }
    });
    this.getSystems();
  }

  getSystems() {
    return fetch(
      process.env.REACT_APP_API +
        "/hypermedia/network/" +
        this.props.activeModelId +
        "/allsystems"
    )
      .then(res => res.json())
      .then(response => {
        this.setState({ systems: response });
        response.map((systems, i) => {
          this.state.filterOptions.push({
            value: systems.id,
            label: systems.name
          });
        });
      })
      .catch(error => console.log(error));
  }

  getSystemServices(event) {
    let parents = this.state.systems;
    let parent = parents.filter(parent => parent.name === event.target.value);
    if (parent) this.setState({ servicesList: parent[0].services });
  }

  //create service
  createReference(event) {
    this.setState(prevState => ({
      referenceModal: !prevState.referenceModal
    }));

    fetch(
      process.env.REACT_APP_API +
        `/hypermedia/network/${this.props.activeModelId}/system/${this.state.referenceData.consumerSystem}/reference/service/${this.state.referenceData.consumedService}`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => response.json())
      .then(responseJson => {
        return fetch(
          process.env.REACT_APP_API +
            `${this.props.activeModel}/service/${this.props.activeFlow}/invocation/` +
            responseJson.id,

          {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            }
          }
        )
          .then(response => response.json())
          .then(responseJson => {
            var data = {
