import {useState} from "react";

export default function useToggleList(fullList = []) {
    let [list, setFilteredList] = useState(fullList);
    const toggleItem = item => {
        let newList = [...list];
        let listIndex = newList.indexOf(item);
        if (listIndex > -1) {
            newList.splice(listIndex, 1);
        } else {
            newList.push(item);
        }
        setFilteredList(newList);
    };
    return [list, toggleItem];
}
