import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { Collapse, Modal, ModalBody, Tooltip } from "reactstrap";
import { fetchSystemDetils } from "../../../store/system/systemActions";
import { connect } from "react-redux";

class SystemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: true,
      systemCollapseId: false,
      subSystemCollapseId: false,
      modal: false,
      details: {}
    };
    this.close = this.close.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.toggle = this.toggle.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
    this.updateSystemDetails = this.updateSystemDetails.bind(this);
  }

  toggle = target => {
    this.setState({
      ...this.state,
      [target]: !this.state[target]
    });
  };
  componentWillReceiveProps(nextProps) {
    if (this.props.activeSystem != nextProps.activeSystem)
      this.props.getSystemDetails(
        this.props.activeModel,
        nextProps.activeSystem
      );
  }
  componentWillMount() {
    this.props.getSystemDetails(
      this.props.activeModel,
      this.props.activeSystem
    );
  }

  updateSystemDetails(event) {
    let value = event.target.value;
    if (
      (event.key === "Enter" || event.type === "blur") &&
      this.state.modal &&
      value
    ) {
      event.target.value = "";
      this.setState(prevState => ({
        modal: !prevState.modal
      }));
      fetch(
        process.env.REACT_APP_API +
          `${this.props.activeModel}/system/` +
          this.props.systemDetails.id,
        {
          method: "PATCH",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({ description: value })
        }
      )
        .then(response =>
          this.props.getSystemDetails(
            this.props.activeModel,
            this.props.systemDetails.id
          )
        )
        .catch(error => console.log(error));
    }
  }

  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  //Closes the system details section
  close(menu, type) {
    this.props.close(menu, type);
  }

  //control right click modal
  modalToggle(e, data) {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));

    if (data) {
      this.setState({
        details: { description: data.description }
      });
    }
  }

  render() {
    return (
      <div className="org_browser">
        <Modal isOpen={this.state.modal} toggle={this.modalToggle}>
          <ModalBody>
            <div>
              <input
                defaultValue={this.state.details.description}
                onBlur={this.updateSystemDetails.bind(this)}
                onKeyDown={this.updateSystemDetails.bind(this)}
                placeholder="Add Description"
              ></input>
            </div>
          </ModalBody>
        </Modal>
        <h3 className="text-center text-white">
          System Details
          <i
            className="fas fa-times system-down-icon browser_close"
            onClick={() => this.close("systemDetail", "menuDetail")}
          />
          <i
            className="far fa-edit"
            onClick={() => this.modalToggle(this, this.props.systemDetails)}
          ></i>
        </h3>

        <VerticalScrollbars autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left"></p>
            </div>
            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">
                  <span id="system-detail-name">
                    {this.props.systemDetails.name}
                  </span>

                  <i
                    className="fas fa-chevron-down system-down-icon"
                    onClick={this.toggleDescription}
                  />
                </p>
                <Tooltip
                  placement="top"
                  toggle={() => this.toggle("system-detail-name")}
                  isOpen={this.state["system-detail-name"]}
                  autohide={false}
                  target={"system-detail-name"}
                >
                  {this.props.systemDetails.id}
                </Tooltip>
              </div>
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">Support lvl: Gold</p>
              </div>
              <Collapse
                isOpen={this.state.collapse}
                className="systemDetailsDisc"
              >
                <p className="text-grey">
                  {this.props.systemDetails.description
                    ? this.props.systemDetails.description
                    : "No Description"}
                </p>
              </Collapse>
            </div>

            <div className="ob-block row">
              <p className="text-white text-left col-md-12">Sub systems</p>
              {this.props.systemDetails.subsystemLinks
                ? this.props.systemDetails.subsystemLinks.map(
                    (subsystem, i) => {
                      return (
                        <div key={i} className="d-flex w-100">
                          <div className="col-md-12">
                            <p className="text-grey text-left">
                              <span className="systemCircle"></span>
                              <span>{subsystem.title}</span>
                            </p>
                          </div>
                        </div>
                      );
                    }
                  )
                : ""}
            </div>
            <div className="ob-block row">
              <p className="text-white text-left col-md-12">Services</p>
              {this.props.systemDetails.services
                ? this.props.systemDetails.services.map((services, i) => {
                    return (
                      <div key={services.id} className="d-flex w-100">
                        <div className="col-md-12">
                          <p className="text-grey text-left">
                            <span className="serviceCircle" />

                            <span id={`service-${i}`}>{services.name}</span>
                          </p>
                          <Tooltip
                            placement="top"
                            toggle={() => this.toggle(`service-${i}`)}
                            isOpen={this.state[`service-${i}`]}
                            autohide={false}
                            target={`service-${i}`}
                          >
                            {services.id}
                          </Tooltip>
                        </div>
                      </div>
                    );
                  })
                : ""}
            </div>
            <div className="ob-block row">
              <p className="text-white text-left col-md-12">References</p>
              {this.props.systemDetails.references
                ? this.props.systemDetails.references.map((references, i) => {
                    return (
                      <div key={i} className="d-flex w-100">
                        <div className="col-md-12">
                          <p className="text-grey text-left">
                            <span className="referenceArrow">â†’</span>

                            <span id={`reference-${i}`}>{references.name}</span>
                          </p>
                          <Tooltip
                            placement="top"
                            toggle={() => this.toggle(`reference-${i}`)}
                            isOpen={this.state[`reference-${i}`]}
                            autohide={false}
                            target={`reference-${i}`}
                          >
                            {references.id}
                          </Tooltip>
                        </div>
                      </div>
                    );
                  })
                : ""}
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    getSystemDetails: (modelId, systemId) => {
      dispatch(fetchSystemDetils(modelId, systemId));
    }
  };
}

function mapStateToProps(state) {
  return {
    activeSystem: state.model.activeSystem,
    activeModel: state.model.modelUri,
    systemDetails: state.system.systemDetails
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SystemDetails);
