export default async function (elem, callback = () => {}) {
    let prom = Promise.resolve();
    if (!document.fullscreenElement && !document.mozFullScreenElement &&
        !document.webkitFullscreenElement && !document.msFullscreenElement) {
        if (elem.requestFullscreen) {
            prom = elem.requestFullscreen();
        } else if (elem.msRequestFullscreen) {
            prom = elem.msRequestFullscreen();
        } else if (elem.mozRequestFullScreen) {
            prom = elem.mozRequestFullScreen();
        } else if (elem.webkitRequestFullscreen) {
            prom = elem.webkitRequestFullscreen(Element.ALLOW_KEYBOARD_INPUT);
        }
    } else {
        if (document.exitFullscreen) {
            prom = document.exitFullscreen();
        } else if (document.msExitFullscreen) {
            prom = document.msExitFullscreen();
        } else if (document.mozCancelFullScreen) {
            prom = document.mozCancelFullScreen();
        } else if (document.webkitExitFullscreen) {
            prom = document.webkitExitFullscreen();
        }
    }
    await prom;
    callback();
}
