    this.modalToggle = this.modalToggle.bind(this);
    this.state = {
      itemid: null,
      itemname: null,
      popup: false,
      eventType: null
    }
  }

  //closes the organisation section
  close(menu,type) {
    this.props.close(menu,type);
  }

  componentDidMount(){
    if (this.props.domain !== "") {
      if (this.props.division === "" || this.props.team === "") {
        this.props.onfetchDivisions(this.props.domain);
      }
    }
  }

  componentDidUpdate(oldProps) {
    const newProps = this.props
    if(oldProps.domain !== newProps.domain) {
      this.props.onfetchDivisions(newProps.domain);
    }
  }

  rightClick = (e, data) => {
    this.setState({popup: !this.state.popup})
    var type = data.target.getAttribute("data-type") + "-" + data.eventType;
    var id = data.target.id;
    var name = data.target.textContent;
    this.setState({
      itemid: id,
      itemname: name,
      eventType: type
    })
  }

  modalToggle = () => {
    this.setState({
      itemid: null,
      itemname: null,
      popup: !this.state.popup,
      eventType: null
    });
  }

  addDivision = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.props.orgid+"/organisation",
        { method: "POST" }
      )
      .then(res => res.json())
      .then((result) => {
        fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+result.id,
          {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: value })
          }
        )
        .then(res => res.json())
        .then((result) => {
          this.props.onfetchDivisions(this.props.domain);
          this.modalToggle();
        });
      });
    }
  }

  renameDivision = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
      .then(res => res.json())
      .then((result) => {
        document.getElementById(result.id).textContent = result.name;
        this.modalToggle();
      });
    }
  }

  deleteDivision = () => {
    fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.state.itemid,
      { method: "DELETE" }
    )
    .then((result) => {
      this.props.onfetchDivisions(this.props.domain);
      this.modalToggle();
    });
  }

  addTeam = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.props.division+"/organisation",
        { method: "POST" }
      )
      .then(res => res.json())
      .then((result) => {
        fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+result.id,
          {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: value })
          }
        )
        .then(res => res.json())
        .then((result) => {
          this.props.onClickDivision(this.props.domain, this.props.division);
          this.modalToggle();
        });
      });
    }
  }

  renameTeam = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
      .then(res => res.json())
      .then((result) => {
        document.getElementById(result.id).textContent = result.name;
        this.modalToggle();
      });
    }
  }

  deleteTeam = () => {
    fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.state.itemid,
      { method: "DELETE" }
    )
    .then((result) => {
      this.props.onClickDivision(this.props.domain, this.props.division);
      this.modalToggle();
    });
  }

  addPerson = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/organisation/"+this.props.team+"/person",
        { method: "POST" }
      )
      .then(res => res.json())
      .then((result) => {
        fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/person/"+result.id,
          {
            method: "PATCH",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ name: value })
          }
        )
        .then(res => res.json())
        .then((result) => {
          this.props.onClickTeam(this.props.domain, this.props.team);
          this.modalToggle();
        });
      });
    }
  }

  renamePerson = (e) => {
    var value = e.target.value;
    if(e.key === 'Enter'){
      fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/person/"+this.state.itemid,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ name: value })
        }
      )
      .then(res => res.json())
      .then((result) => {
        document.getElementById(result.id).textContent = result.name;
        this.modalToggle();
      });
    }
  }

  deletePerson = () => {
    fetch(process.env.REACT_APP_API + "/hypermedia/domain/"+this.props.domain+"/person/"+this.state.itemid,
      { method: "DELETE" }
    )
    .then((result) => {
      this.props.onClickTeam(this.props.domain, this.props.team);
      this.modalToggle();
    });
  }

  render() {
    return (
      <div className="org_browser">

        <ContextMenu id="org_right_click">
          <MenuItem data={{ eventType: 'rename' }} onClick={this.rightClick}>Rename</MenuItem>
          <MenuItem data={{ eventType: 'delete' }} onClick={this.rightClick}>Delete</MenuItem>
        </ContextMenu>

        <Modal isOpen={this.state.popup} toggle={this.modalToggle} >
          <ModalBody>
            {this.state.eventType === 'division-add' && (
              <div>
                <h4 className="text-center m-b-30">Add Division</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addDivision.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'division-rename' && (
              <div>
                <h4 className="text-center m-b-30">Rename Division</h4>
                <Input
                  type="text"
                  className="text-input"
                  defaultValue={this.state.itemname}
                  onKeyDown={this.renameDivision.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'division-delete' && (
              <div>
              <h4>Are you sure?</h4>
              <p>Do you really want to delete division {this.state.itemname}? <br />
              Process cannot be undone</p>
              <div className="delete-button">
                <button className="btn black-btn" onClick={this.deleteDivision.bind(this)}>confirm</button>
                <button className="btn transparent-black-btn" onClick={this.modalToggle}>cancel</button>
              </div></div>
            )}
            {this.state.eventType === 'team-add' && (
              <div>
                <h4 className="text-center m-b-30">Add Team</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addTeam.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'team-rename' && (
              <div>
                <h4 className="text-center m-b-30">Rename Team</h4>
                <Input
                  type="text"
                  className="text-input"
                  defaultValue={this.state.itemname}
                  onKeyDown={this.renameTeam.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'team-delete' && (
              <div>
              <h4>Are you sure?</h4>
              <p>Do you really want to delete team {this.state.itemname}? <br />
              Process cannot be undone</p>
              <div className="delete-button">
                <button className="btn black-btn" onClick={this.deleteTeam.bind(this)}>confirm</button>
                <button className="btn transparent-black-btn" onClick={this.modalToggle}>cancel</button>
              </div></div>
            )}
            {this.state.eventType === 'person-add' && (
              <div>
                <h4 className="text-center m-b-30">Add Person</h4>
                <Input
                  type="text"
                  className="text-input"
                  onKeyDown={this.addPerson.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'person-rename' && (
              <div>
                <h4 className="text-center m-b-30">Rename Person</h4>
                <Input
                  type="text"
                  className="text-input"
                  defaultValue={this.state.itemname}
                  onKeyDown={this.renamePerson.bind(this)}
                />
              </div>
            )}
            {this.state.eventType === 'person-delete' && (
              <div>
              <h4>Are you sure?</h4>
              <p>Do you really want to delete person {this.state.itemname}? <br />
              Process cannot be undone</p>
              <div className="delete-button">
                <button className="btn black-btn" onClick={this.deletePerson.bind(this)}>confirm</button>
                <button className="btn transparent-black-btn" onClick={this.modalToggle}>cancel</button>
              </div></div>
            )}
          </ModalBody>
        </Modal>

        <h3 className="text-center text-white">
          Organisation Browser
          <i className="fas fa-times system-down-icon browser_close" onClick={() => this.close('organisation','menu')} />
        </h3>
        <VerticalScrollbars style={{ width: 350 }} autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left">Org selector</p>
              <input type="text" className="search-input" />
              <span className="icon icon-Search search-icon" />
            </div>

            {this.props.orgid !== "" &&
              <div className="division w-100 d-inline-block">
                <p className="text-white text-left">Division <i className="fas fa-plus system-down-icon" onClick={()=>{this.setState({eventType:"division-add",popup:true})}} /> </p>
                {this.props.divisions.length !== 0 &&
                  <VerticalScrollbars style={{ height: 120 }}>
                    <ul>
                      {
                        this.props.divisions.map((item, index) => {
                          let active = (item.id === this.props.division ? "active" : "");
                          return (
                            <ContextMenuTrigger key={item.id} id="org_right_click">
                              <li className={"division-item "+active} id={item.id} data-type="division" onClick={(e) => this.props.onClickDivision(this.props.domain, e.target.id)}>
                                {item.name}
                              </li>
                            </ContextMenuTrigger>
                          )
                        })
                      }
                    </ul>
                  </VerticalScrollbars>
                }
              </div>
            }

            {this.props.division !== "" &&
              <div className="team">
                <p className="text-white text-left">Team <i className="fas fa-plus system-down-icon" onClick={()=>{this.setState({eventType:"team-add",popup:true})}} /></p>
                {this.props.teams.length !== 0 &&
                  <div className="team-container">
                    <VerticalScrollbars style={{ height: 150 }}>
                      <ul>
                        {
                          this.props.teams.map((item, index) => {
                            let active = (item.id === this.props.team ? "active" : "");
                            return (
                              <ContextMenuTrigger key={item.id} id="org_right_click">
                                <li key={item.id} className={"team-item "+active} id={item.id} data-type="team"
                                onClick={(e) => this.props.onClickTeam(this.props.domain, item.id)}>
                                  {item.name}
                                </li>
                              </ContextMenuTrigger>
                            )
                          })
                        }
                      </ul>
                    </VerticalScrollbars>
                  </div>
                }
              </div>
            }

            {this.props.team !== "" &&
              <div className="developer w-100 d-inline-block">
                <p className="text-white text-left">Developer <i className="fas fa-plus system-down-icon" onClick={()=>{this.setState({eventType:"person-add",popup:true})}} /> </p>
                {this.props.persons.length !== 0 &&
                  <VerticalScrollbars style={{ height: 180 }}>
                    <ul className="text-left">
                      {this.props.persons.map((item, index) => {
                        return (
                          <ContextMenuTrigger key={item.id} id="org_right_click">
                            <li key={item.id} id={item.id} className="developer-item" data-type="person">
                              {item.name}
                            </li>
                          </ContextMenuTrigger>
                        )
                      })}
                    </ul>
                  </VerticalScrollbars>
                }
              </div>
            }

          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    domain: state.model.domain,
    orgid: state.organisation.orgid,
    division: state.organisation.division,
    divisions: state.organisation.divisions,
    team: state.organisation.team,
    teams: state.organisation.teams,
    persons: state.organisation.persons
  };
}

function mapDispatchToProps(dispatch) {
  return {
    onfetchDivisions: domainId => {
      dispatch(fetchDivisions(domainId));
    },
    onClickDivision: (domainId, divId) => {
      const action = { type: "SELECT_DIVISION", division: divId };
      dispatch(fetchTeams(domainId, divId));
      dispatch(action);
    },
    onClickTeam: (domainId, teamId) => {
      const action = { type: "SELECT_TEAM", team: teamId };
      dispatch(fetchPersons(domainId, teamId));
      dispatch(action);
    }
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Organisation);
