import React, { Component } from 'react';
import users from '../../_assets/images/menu/users.svg';
import user from '../../_assets/images/menu/user.svg';
import inbox from '../../_assets/images/menu/inbox.svg';
import save from '../../_assets/images/menu/save.svg';
import globe from '../../_assets/images/menu/globe.svg';
import inboxfull from '../../_assets/images/menu/inbox-full.svg';
import share from '../../_assets/images/menu/share.svg';
import search from '../../_assets/images/menu/search.svg';

export default class Header extends Component {
    constructor(){
        super();

        this.state = {
          popupVisible: false,
            };

        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);

    };
    handleClick() {
        if (!this.state.popupVisible) {
          // attach/remove event handler
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
           popupVisible: !prevState.popupVisible,
        }));
      }

      handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
          return;
        }

        this.handleClick();
      }
  render() {
    return (
        <div className="dashboard-header">
            <div className="d-flex text-white"  ref={node => { this.node = node; }}>
                <div className="col-md-3">
                    <ul className="first-menu">
                        <li><button  onClick={this.handleClick}><img src={users} alt="starlify" /></button></li>
                        <li><button><img src={inbox} alt="starlify" /></button></li>
                    </ul>
                </div>
                <div className="col-md-4">
                    <ul className="second-menu">
                            <li><button><img src={save} alt="starlify" /></button></li>
                            <li><button><img src={user} alt="starlify" /></button></li>
                            <li><button><img src={inboxfull} alt="starlify" /></button></li>
                            <li><button><img src={share} alt="starlify" /></button></li>
                    </ul>
                </div>
                <div className="col-md-1">
                <ul className="third-menu">
                            <li><button><img src={globe} alt="starlify" /></button></li>
                    </ul>
                </div>
                <div className="col-md-4">
                    <ul className="fourth-menu">
                            <li><button><img src={save} alt="starlify" /></button></li>
                            <li>Entiros</li>
                            <li><button><img src={search} alt="starlify" /></button></li>
                    </ul>
                </div>
            </div>
            {this.state.popupVisible && (
          <div className="signincontent"> 
            <h3 className="text-center">Sign Up</h3>
            <form method="post"  name="userRegistrationForm"  onSubmit= {this.submituserRegistrationForm} >
              <input className="col-md-12" type="text" name="emailid" value={this.state.fields.emailid} onChange={this.handleChange} placeholder="EMAIL"  />
              <div className="errorMsg">{this.state.errors.emailid}</div>
              <input className="col-md-12" type="password" name="password" value={this.state.fields.password} onChange={this.handleChange} placeholder="PASSWORD" />
              <div className="errorMsg">{this.state.errors.password}</div>
              <input className="col-md-12" type="password" name="passwordconfirm" value={this.state.fields.passwordconfirm} onChange={this.handleChange} placeholder="CONFIRM PASSWORD" />
              <div className="errorMsg">{this.state.errors.password}</div>
              <button type="submit" className="btn transparent-btn col-md-12"  value="Register">SIGN IN</button>
            </form>
            <div className="formSeperate">OR SIGN IN WITH</div>
            <button  className="btn red-btn col-md-12"><img src={google} className="img-fluid logo" alt="Starlify" /></button>
          </div>
        )
        }
      </div>
    )
  }
}
