          this.node = node;
        }}
      >
        <div className="d-flex text-white menu-wrapper">
          <Col md="3">
            <ul className="first-menu">
              <li
                className={
                  this.state.activeMenu === "organisation" ? "active" : ""
                }
              >
                <button onClick={() => this.menuClick("organisation")}>
                  <span className="icon icon-Users menu-icon" />
                </button>
              </li>
              {this.activeState}
              <li
                className={
                  this.state.activeMenu.includes("system") ? "active" : ""
                }
              >
                <button onClick={() => this.menuClick("system")}>
                  <span className="icon icon-EmptyBox menu-icon" />
                </button>
              </li>
            </ul>
          </Col>
          <div className="col-md-4">
            <ul className="second-menu">
              <li
                className={
                  this.state.activeMenuDetail.includes("tagger") ? "active" : ""
                }
              >
                <button onClick={() => this.menuDetailClick("tagger")}>
                  <span className="icon icon-Floppy menu-icon" />
                </button>
              </li>
              <li
                className={
                  this.state.activeMenuDetail === "orgDetail" ? "active" : ""
                }
              >
                <button onClick={() => this.menuDetailClick("orgDetail")}>
                  <span className="icon icon-User menu-icon" />
                </button>
              </li>
              <li
                className={
                  this.state.activeMenuDetail === "systemDetail" ? "active" : ""
                }
              >
                <button onClick={() => this.menuDetailClick("systemDetail")}>
                  <span className="icon icon-FileBox menu-icon" />
                </button>
              </li>
              <li
                className={
                  this.state.activeMenuDetail === "flowDetail" ? "active" : ""
                }
              >
                <button onClick={() => this.menuDetailClick("flowDetail")}>
                  <span className="icon icon-Share menu-icon" />
                </button>
              </li>
            </ul>
          </div>
          <div className="col-md-1">
            <ul className="third-menu">
              <li>
                <button>
                  <span className="icon icon-WorldWide menu-icon" />
                </button>
              </li>
            </ul>
          </div>
          <div className="col-md-4">
            <ul className="fourth-menu">
              <li className="usermenuclick user-avatar">
                <span onClick={this.userhandleClick}>
                  Entiros <i className="fas fa-chevron-down m-l-15 m-r-30" />
                  <img src={photo} alt="starlify" className="photo-icon" />
                </span>
                {this.state.userDetailsVisible && (
                  <div className="userVisible">
                    <Userblock />
                  </div>
                )}
              </li>
              <li className="dash" />
              <li>
                <button>
                  <span className="icon icon-Search menu-icon" />
                </button>
              </li>
            </ul>
          </div>
        </div>
        <div className="content">
          {this.state.organisationVisible && (
            <div className="organisationVisible browser-item">
              <Organisation close={this.close} />
            </div>
          )}
          {this.state.systemShow && (
            <div className="systemVisible browser-item">
              <Systems
                systemSelected={this.systemSelected}
                flowsSelected={this.flowsSelected}
                close={this.close}
              />
            </div>
          )}
          {this.state.taggerVisible && (
            <div className="taggerVisible browser-item">
              <Tagger close={this.close} />
            </div>
          )}
          {this.state.organisationDetailsVisible && (
            <div className="organisationdetailsVisible browser-item">
              <OrganisationDetails close={this.close} />
            </div>
          )}
          {this.state.systemDetailsVisible && this.props.activeSystem && (
            <div className="systemDetailsVisible browser-item">
              <SystemDetails
                systemId={this.state.systemId}
                close={this.close}
              />
            </div>
          )}
          {this.state.flowDetailsVisible && this.state.flowId && (
            <div className="flowDetailsVisible browser-item">
              <FlowDetails close={this.close} />
            </div>
          )}
          <div className="flowDetailsVisible browser-item">
              <FlowDetails close={this.close} />
            </div>
        </div>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    activeSystem: state.model.activeSystem
  };
}

export default connect(mapStateToProps)(Header);
