import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";

export default class FlowDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addClass: false,
      romoveClass: false,
      flowDetails: {}
    };
    this.close = this.close.bind(this);
  }

  close(type) {
    this.props.close(type);
  }

  componentWillReceiveProps(nextProp) {
    this.setState({ ...this.state, nextProp });
    this.getFlowDetails();
  }
  componentWillMount() {
    this.getFlowDetails();
  }
  getFlowDetails() {
    fetch(
      process.env.REACT_APP_API +
        "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow/72ad974f-e4e2-41c2-8838-79ebd240f315" ,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ flowDetails: responseJson });
      });
  }
  toggle() {
    this.setState({ addClass: !this.state.addClass });
  }
  removeToggle() {
    this.setState({ romoveClass: !this.state.romoveClass });
  }
  render() {
    let boxClass = ["_search"];
    if (this.state.addClass) {
      boxClass.push("active");
    }
    let box1Class = ["_search"];
    if (this.state.romoveClass) {
      box1Class.push("active");
    }
    return (
      <div className="org_browser" onClick={this.removeToggle.bind(this)}>
        <h3 className="text-center text-white">
          Flow Details
          <i class="fas fa-times system-down-icon browser_close" onClick={() => this.close('flowDetail')}/>
        </h3>
        <VerticalScrollbars autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left">Selected flow</p>
              <div className={(boxClass.join(" "), box1Class.join(" "))}>
                <input
                  type="text"
                  className="search-input"
                  onClick={this.toggle.bind(this)}
                />
                <span className="icon icon-Search search-icon" />
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">
                    {this.state.flowDetails.id}<i class="fas fa-chevron-down system-down-icon" />
                </p>
              </div>
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">Support lvl: Gold</p>
              </div>
            </div>

            <div className=" row">
              <div className="col-md-4">
                <p className="text-white text-left">System</p>
              </div>
              <div className="col-md-8">
                <ul>
                  <li className="tag">TEM</li>
                </ul>
              </div>
            </div>
            <div className=" row">
              <div className="col-md-4">
                <p className="text-white text-left">Target</p>
              </div>
              <div className="col-md-8">
                <ul>
                  <li className="tag">GIB</li>
                  <li className="tag">SAP</li>
                </ul>
              </div>
            </div>
            <div className="doc-url">
              <p className="text-white text-left">Documentation URL</p>
              <p className="text-grey text-left">
                https//:confluence.entiros.se/xOU90
              </p>
            </div>
            <div className="ob-block row m-t-50">
              <div className="col-md-12 m-b-20">
                <p className="text-white text-left">
                  Flow Martix <i class="fas fa-sync-alt  system-down-icon" />
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}
