import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { fetchFlowDetils } from '../../../store/system/systemActions'
import { connect } from 'react-redux';
import { Popover, PopoverBody, Collapse } from "reactstrap";
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
import { Modal, ModalBody } from "reactstrap";

class FlowDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      addClass: false,
      romoveClass: false,
      flowDetails: {}
    };
    this.close = this.close.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.toggleId = this.toggleId.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
  }

  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  toggleId() {
    this.setState({
      collapseId: !this.state.collapseId
    });
  }

  close(type) {
    this.props.close(type);
  }


  componentWillMount() {
    this.props.getFlowDetails(this.props.activeModel, this.props.activeFlow);
  }
  updateFlowDetails(event) {
    let value = event.target.value;
    if ((event.key === 'Enter' || event.type === 'blur') && this.state.modal && value) { 
        event.target.value = '';
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
        fetch(
            process.env.REACT_APP_API + `${this.props.activeModel}/flow/` + this.props.activeFlow,
            {
                method: "PATCH",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ description: value })
            }
        )
        .then(response => {
            this.props.getFlowDetails(this.props.activeModel, this.props.activeFlow)
        }
     )
    }
  }
  toggle() {
    this.setState({ addClass: !this.state.addClass });
  }
  removeToggle() {
    this.setState({ romoveClass: !this.state.romoveClass });
  }

  //control right click modal
  modalToggle(e, data) {
      console.log(data)
      this.setState(prevState => ({
          modal: !prevState.modal
      }));

      if (data) {
          this.setState({
              item: data.item,
          })

    }
  }

  render() {
    let boxClass = ["_search"];
    if (this.state.addClass) {
      boxClass.push("active");
    }
    let box1Class = ["_search"];
    if (this.state.romoveClass) {
      box1Class.push("active");
    }
    return (
      <div className="org_browser" onClick={this.removeToggle.bind(this)}>
        <ContextMenu id="flowdetail_right_click">
        <MenuItem data={{ itemType: 'rename' }} onClick={this.modalToggle}>
          Update
      </MenuItem>
      </ContextMenu>
      <Modal isOpen={this.state.modal} toggle={this.modalToggle} >
                    <ModalBody>
                            <div>
                            <textarea onBlur={this.updateFlowDetails.bind(this)} onKeyDown={this.updateFlowDetails.bind(this)} defaultValue={this.props.details.description}/>
                            </div>
                    </ModalBody>
       </Modal>
        <h3 className="text-center text-white">
          Flow Details
          <i className="fas fa-times system-down-icon browser_close" onClick={() => this.close('flowDetail')}/>
        </h3>
        <VerticalScrollbars autoHide>
          <div className="browser_wrapper">

            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
              <p className="text-white text-left">
                  <span id="PopoverId">{this.props.details.name}</span>

                  <i
                    className="fas fa-chevron-down system-down-icon"
                    onClick={this.toggleDescription}
                  />
                </p>
                <Popover
                  placement="bottom"
                  isOpen={this.state.collapseId}
                  target="PopoverId"
                  toggle={this.toggleId}
                >
                  {/* <PopoverHeader>Popover Title</PopoverHeader> */}
                  <PopoverBody>{this.props.details.id}</PopoverBody>
                </Popover>
              </div>
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">Support lvl: Gold</p>
              </div>
              <Collapse
                isOpen={this.state.collapse}
                className="systemDetailsDisc"
              >
                <ContextMenuTrigger id="flowdetail_right_click" item={this.props.details} >
                <p className="text-grey">
                {this.props.details.description ? this.props.details.description : 'No Description'}
                </p>
                </ContextMenuTrigger>
              </Collapse>
            </div>

            <div className=" row">
              <div className="col-md-4">
                <p className="text-white text-left">System</p>
              </div>
              <div className="col-md-8">
                <ul>
                  <li className="tag">TEM</li>
                </ul>
              </div>
            </div>
            <div className=" row">
              <div className="col-md-4">
                <p className="text-white text-left">Target</p>
              </div>
              <div className="col-md-8">
                <ul>
                  <li className="tag">GIB</li>
                  <li className="tag">SAP</li>
                </ul>
              </div>
            </div>
            <div className="doc-url">
              <p className="text-white text-left">Documentation URL</p>
              <p className="text-grey text-left">
                https//:confluence.entiros.se/xOU90
              </p>
            </div>
            <div className="ob-block row m-t-50">
              <div className="col-md-12 m-b-20">
                <p className="text-white text-left">
                  Flow Martix <i className="fas fa-sync-alt  system-down-icon" />
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
            <div className="ob-block row">
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="tag">GIB</p>
              </div>
              <div className="col-md-4">
                <p className="text-small text-grey">
                  EDI 856F - Advanced Ship Notice, Shipping
                </p>
              </div>
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}

function mapDispatchToProps(dispatch) {
  return {
    getFlowDetails: (modelId, flowId) => {
          dispatch(fetchFlowDetils(modelId, flowId));
        }
  }
}

function mapStateToProps(state){
  return{
    activeSystem: state.model.activeSystem,
    activeFlow: state.system.activeFlow,
    activeModel: state.model.modelUri,
    details: state.system.details,
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(FlowDetails);
