import React, { Component } from "react";
import { Collapse } from "reactstrap";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import subsystem from "../../../_assets/images/subsystem.png";
import services from "../../../_assets/images/services.png";
import reference from "../../../_assets/images/reference.png";

class Systems extends Component {
  constructor(props) {
    super(props);
    this.systemMenuToggle = this.systemMenuToggle.bind(this);
    this.systemToggle = this.systemToggle.bind(this);
    this.flowsToggle = this.flowsToggle.bind(this);
    this.subSystemToggle = this.subSystemToggle.bind(this);
    this.searchSystem = this.searchSystem.bind(this);
    this.systemSelected = this.systemSelected.bind(this);
    this.state = {
      addClass: false,
      romoveClass: false,
      flowsCollapse: true,
      systemsCollapse: true,
      collapseSystem: false,
      collapseSubSystem: false,
      currentSystemIndex: "",
      currentSubSystemIndex: "",
      systemData: { system: "" },
      showsystemDetail: false,
      systemDetails: {},
      values: [],
      system: [],
      flow: [],
      searchString: "",
      ids: [],
      search: false
    };
  }

  componentWillMount() {
    return fetch(
      process.env.REACT_APP_API + "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ systemData: responseJson });
      });
  }

  createSystem() {
    return this.state.system
      ? this.state.system.map((el, i) => (
          <div key={i} className="extra-add">
            <input
              type="text"
              id="system"
              onBlur={this.handleChange.bind(this, i)}
            />
            <button type="button" onClick={this.removeClick.bind(this, i)}>
              <i class="fas fa-times" />
            </button>
          </div>
        ))
      : "";
  }

  createFlow() {
    return this.state.flow
      ? this.state.flow.map((el, i) => (
          <div key={i} className="extra-add">
            <input
              type="text"
              id="flow"
              onBlur={this.handleChange.bind(this, i)}
            />
            <button type="button" onClick={this.removeClick.bind(this, i)}>
              <i class="fas fa-times" />
            </button>
          </div>
        ))
      : "";
  }

  handleChange(i, event) {
    let type = event.target.id;
    let systemName = event.target.value;

    if (type == "system") {
      let values = [...this.state.system];
      values[i] = event.target.value;
      this.setState({ values });
      this.state.system.splice(i, 1);
      return fetch(
        process.env.REACT_APP_API +
          "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/system",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => response.json())
        .then(responseJson => {
          return fetch(
            process.env.REACT_APP_API +
              "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/system/" +
              responseJson.id,
            {
              method: "PATCH",
              headers: {
                "Content-Type": "application/json"
              },
              body: JSON.stringify({ name: systemName })
            }
          )
            .then(response => response.json())
            .then(responseJson => {});
        });
    } else {
      let values = [...this.state.flow];
      values[i] = event.target.value;
      this.setState({ values });
      this.state.flow.splice(i, 1);
      return fetch(
        process.env.REACT_APP_API +
          "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => response.json())
        .then(responseJson => {
          return fetch(
            process.env.REACT_APP_API +
              "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/flow/" +
              responseJson.id,
            {
              method: "POST",
              headers: {
                "Content-Type": "application/json"
              },
              body: JSON.stringify({ name: systemName })
            }
          )
            .then(response => response.json())
            .then(responseJson => {});
        });
    }
  }

  addClick(event) {
    if (event.target.id == "system")
      this.setState(prevState => ({ system: [...prevState.system, ""] }));
    else this.setState(prevState => ({ flow: [...prevState.flow, ""] }));
  }

  removeClick(i) {
    //let value = [...this.state.values];
    //value.splice(i, 1);
    //this.setState({ values : value });
    // console.log(values)
  }

  systemSelected(systemId) {
    this.props.systemSelected(systemId);
  }

  systemMenuToggle() {
    this.setState(state => ({ systemsCollapse: !state.systemsCollapse }));
  }

  flowsToggle() {
    this.setState(state => ({ flowsCollapse: !state.flowsCollapse }));
  }

  systemToggle(systemId) {
    this.systemSelected(systemId);
    this.setState({ collapse: !this.state.collapse });
    if (
      this.state.currentSystemIndex == systemId ||
      (!this.state.showsystemDetail && this.state.search)
    ) {
      this.state.search = false;
      this.state.currentSystemIndex = 0;
    } else {
      this.setState({ currentSystemIndex: systemId });
      this.setState({ showsystemDetail: true });
      return fetch(
        process.env.REACT_APP_API +
          "model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/system/f1dc3452-e1a5-4a4e-9ae4-5fee3b34b647",
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json"
          }
        }
      )
        .then(response => response.json())
        .then(responseJson => {
          this.setState({ systemDetails: responseJson });
        });
    }
  }

  subSystemToggle(systemId) {
    this.setState({ collapseSubSystem: !this.state.collapseSubSystem });
    if (this.state.currentSubSystemIndex == systemId) {
      this.state.currentSubSystemIndex = 0;
    } else {
      this.setState({ currentSubSystemIndex: systemId });
    }
  }

  searchSystem(event) {
    this.state.ids = [];
    this.setState({ search: true });
    var lis = [];

    var lis = document
      .getElementById("systemListing")
      .getElementsByTagName("span");
    for (var i = 0; i < lis.length; i++) {
      var stringText = lis[i].innerHTML;
      if (
        event.currentTarget.value &&
        stringText.includes(event.currentTarget.value)
      ) {
        this.state.ids.push(lis[i].id);
      }
    }

    var lis = document
      .getElementById("FlowListing")
      .getElementsByTagName("span");
    for (var i = 0; i < lis.length; i++) {
      var stringText = lis[i].innerHTML;
      if (
        event.currentTarget.value &&
        stringText.includes(event.currentTarget.value)
      ) {
        this.state.ids.push(lis[i].id);
      }
    }
  }
  toggle() {
    this.setState({ addClass: !this.state.addClass });
  }
  removeToggle() {
    this.setState({ romoveClass: !this.state.romoveClass });
  }
  render() {
    let boxClass = ["_search"];
    if (this.state.addClass) {
      boxClass.push("active");
    }
    let box1Class = ["_search"];
    if (this.state.romoveClass) {
      box1Class.push("active");
    }
    return (
      <div className="org_browser" onClick={this.removeToggle.bind(this)}>
        <h3 className="text-center text-white">
          Systems and Flows <i class="fas fa-times system-down-icon" />
        </h3>
        <VerticalScrollbars style={{ width: 350 }} autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left">Selector</p>
              <div className={(boxClass.join(" "), box1Class.join(" "))}>
                <input
                  type="text"
                  className="search-input"
                  onKeyUp={this.searchSystem}
                  onClick={this.toggle.bind(this)}
                />
                <span className="icon icon-Search search-icon" />
              </div>
              <div className="systems w-100 d-inline-block montserrat text-grey">
                <div className="system-search montserrat" />

                <p
                  className="text-white click"
                  onClick={this.systemMenuToggle}
                  style={{ marginBottom: "1rem" }}
                >
                  Systems <i className="fas fa-chevron-down system-down-icon" />
                  <i
                    className="fas fa-plus system-down-icon"
                    id="system"
                    onClick={this.addClick.bind(this)}
                  />
                </p>
                <VerticalScrollbars style={{ height: 220 }}>
                  {this.createSystem()}

                  <Collapse
                    isOpen={this.state.systemsCollapse}
                    id="systemListing"
                  >
                    {this.state.systemData.systems
                      ? this.state.systemData.systems.map((item, index) => {
                          return (
                            <div>
                              <p
                                className="text-grey click"
                                onClick={() => this.systemToggle(item.id)}
                                style={{
                                  marginBottom: "1rem",
                                  marginLeft: "1rem"
                                }}
                              >
                                <img
                                  src={subsystem}
                                  alt="starlify"
                                  className="m-r-15 m-b-5 "
                                />
                                <span
                                  id={item.id}
                                  className={
                                    this.state.ids.includes(item.id)
                                      ? "highlight"
                                      : ""
                                  }
                                >
                                  {item.name ? item.name : "Empty System Name"}
                                </span>
                              </p>
                              <Collapse
                                isOpen={
                                  this.state.currentSystemIndex == item.id ||
                                  this.state.search
                                    ? true
                                    : false
                                }
                              >
                                {item.services
                                  ? item.services.map((service, i) => {
                                      return (
                                        <div id={service.id}>
                                          <p
                                            className="text-grey click"
                                            style={{
                                              marginBottom: "1rem",
                                              marginLeft: "3rem"
                                            }}
                                          >
                                            <img
                                              src={services}
                                              alt="starlify"
                                              className="m-r-15 m-b-5 "
                                            />
                                            {service.name
                                              ? service.name
                                              : "Empty Service Name"}
                                          </p>
                                        </div>
                                      );
                                    })
                                  : ""}

                                {item.references
                                  ? item.references.map((referenceData, i) => {
                                      return (
                                        <p
                                          className="text-grey click"
                                          style={{
                                            marginBottom: "1rem",
                                            marginLeft: "3rem"
                                          }}
                                        >
                                          <img
                                            src={reference}
                                            alt="starlify"
                                            className="m-r-15 m-b-5 "
                                          />
                                          {referenceData.name
                                            ? referenceData.name
                                            : "Empty Reference Name"}
                                        </p>
                                      );
                                    })
                                  : ""}
                                {item.subSystems
                                  ? item.subSystems.map((subSystems, i) => {
                                      return (
                                        <div>
                                          <p
                                            className="text-grey click"
                                            onClick={() =>
                                              this.subSystemToggle(
                                                subSystems.id
                                              )
                                            }
                                            style={{
                                              marginBottom: "1rem",
                                              marginLeft: "3rem"
                                            }}
                                          >
                                            <img
                                              src={subsystem}
                                              alt="starlify"
                                              className="m-r-15 m-b-5 "
                                            />
                                            <span
                                              id={subSystems.id}
                                              className={
                                                this.state.ids.includes(
                                                  subSystems.id
                                                )
                                                  ? "highlight"
                                                  : ""
                                              }
                                            >
                                              {" "}
                                              {subSystems.name
                                                ? subSystems.name
                                                : "Empty Subsystem Name"}
                                            </span>
                                          </p>
                                          <Collapse
                                            isOpen={
                                              this.state
                                                .currentSubSystemIndex ==
                                              subSystems.id
                                                ? true
                                                : false
                                            }
                                          >
                                            {subSystems.services
                                              ? subSystems.services.map(
                                                  (service, i) => {
                                                    return (
                                                      <div id={service.id}>
                                                        <p
                                                          className="text-grey click"
                                                          style={{
                                                            marginBottom:
                                                              "1rem",
                                                            marginLeft: "4rem"
                                                          }}
                                                        >
                                                          <img
                                                            src={services}
                                                            alt="starlify"
                                                            className="m-r-15 m-b-5 "
                                                          />
                                                          {service.name
                                                            ? service.name
                                                            : "Empty Service Name"}
                                                        </p>
                                                      </div>
                                                    );
                                                  }
                                                )
                                              : ""}
                                            {subSystems.references
                                              ? subSystems.references.map(
                                                  (references, i) => {
                                                    return (
                                                      <div id={references.id}>
                                                        <p
                                                          className="text-grey click"
                                                          style={{
                                                            marginBottom:
                                                              "1rem",
                                                            marginLeft: "4rem"
                                                          }}
                                                        >
                                                          <img
                                                            src={reference}
                                                            alt="starlify"
                                                            className="m-r-15 m-b-5 "
                                                          />
                                                          {references.name
                                                            ? reference.name
                                                            : "Empty Reference Name"}
                                                        </p>
                                                      </div>
                                                    );
                                                  }
                                                )
                                              : ""}
                                          </Collapse>
                                        </div>
                                      );
                                    })
                                  : ""}
                              </Collapse>
                            </div>
                          );
                        })
                      : ""}
                  </Collapse>
                </VerticalScrollbars>
              </div>
              <div className="flows">

                  <p
                    className="text-white click"
                    onClick={this.flowsToggle}
                    style={{ marginBottom: "1rem" }}
                  >
                    Flows <i class="fas fa-chevron-down system-down-icon" />
                    <i
                      class="fas fa-plus system-down-icon"
                      id="flows"
                      onClick={this.addClick.bind(this)}
                    />
                </p>
                <VerticalScrollbars style={{ height: 200 }}>
                  {this.createFlow()}
                  <Collapse isOpen={this.state.flowsCollapse} id="FlowListing">
                    {this.state.systemData.flow
                      ? this.state.systemData.flow.map((item, index) => {
                          return (
                            <div>
                              <p
                                className="text-grey click"
                                style={{
                                  marginBottom: "1rem",
                                  marginLeft: "2rem"
                                }}
                              >
                                <span
                                  id={item.id}
                                  className={
                                    this.state.ids.includes(item.id)
                                      ? "highlight"
                                      : ""
                                  }
                                >
                                  {item.name ? item.name : "Empty Flow Name"}
                                </span>
                              </p>
                            </div>
                          );
                        })
                      : ""}
                  </Collapse>
                </VerticalScrollbars>
              </div>
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}

export default Systems;
