import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import subsystem from "../../../_assets/images/subsystem.png";
import { Button, Popover, PopoverHeader, PopoverBody } from "reactstrap";
import {
  Collapse,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter
} from "reactstrap";
export default class SystemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      systemDetails: {},
      systemId: "",
      collapse: true,
      popoverOpen: false
    };
    this.close = this.close.bind(this);
    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(state => ({ collapse: !state.collapse }));
    this.setState({
      popoverOpen: !this.state.popoverOpen
    });
  }

  componentWillReceiveProps(nextProp) {
    //console.log(nextProp)
    this.setState({
      systemId: nextProp.systemId
    });
    //alert(nextProp.systemId)
    //this.setState({systemId : nextProp.systemId })
    this.getSystemDetails(nextProp.systemId);
  }

  componentWillMount() {
    this.getSystemDetails(this.props.systemId);
  }

  //get system details using systemId
  getSystemDetails(systemId) {
    //debugger
    fetch(
      process.env.REACT_APP_API +
        "model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/system/" +
        systemId,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json"
        }
      }
    )
      .then(response => response.json())
      .then(responseJson => {
        this.setState({ systemDetails: responseJson });
      });
  }

  //Closes the system details section
  close(menu, type) {
    this.props.close(menu, type);
  }

  render() {
    return (
      <div className="org_browser">
        <h3 className="text-center text-white">
          System Details
          <i
            className="fas fa-times system-down-icon browser_close"
            onClick={() => this.close("systemDetail", "menuDetail")}
          />
        </h3>
        <VerticalScrollbars autoHide>
          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left">
                {/* {this.state.systemDetails.name} */}
              </p>
              <input type="text" className="search-input" />
              <span className="icon icon-Search search-icon" />
            </div>
            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left" id="Popover1">
                  {this.state.systemDetails.name}

                  <i
                    className="fas fa-chevron-down system-down-icon"
                    onClick={this.toggle}
                  />
                </p>
                <Popover
                  placement="bottom"
                  isOpen={this.state.popoverOpen}
                  target="Popover1"
                  toggle={this.toggle}
                >
                  {/* <PopoverHeader>Popover Title</PopoverHeader> */}
                  <PopoverBody>{this.state.systemDetails.id}</PopoverBody>
                </Popover>
              </div>
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">Support lvl: Gold</p>
              </div>
              <Collapse
                isOpen={this.state.collapse}
                className="systemDetailsDisc"
              >
                <p className="text-grey">
                  {this.state.systemDetails.description}
                </p>
              </Collapse>
            </div>
            {/* <div className="developer w-100 d-inline-block">
            <p className="text-white text-left">Developer</p>
            <ul className="text-left">
              <li className="developer-item">
                Name Surname <span className="developer-img" />
              </li>
            </ul>
          </div>
          <div className="ob-block row">
            <p className="text-white text-left col-md-12">Subsystem</p>
            <div className="col-md-6">
              <p className="text-grey text-left">
                <img src={subsystem} alt="starlify" className="m-r-15 m-b-5 " />
                System
              </p>
            </div>
            <div className="col-md-6">
              <p className="text-grey text-left">ID:039982397</p>
            </div>
          </div> */}
            <div className="ob-block row">
              {this.state.systemDetails.services ? (
                <p className="text-white text-left col-md-12">Services</p>
              ) : (
                ""
              )}
              {this.state.systemDetails.services
                ? this.state.systemDetails.services.map((services, i) => {
                    return (
                      <div key={services.id} className="d-flex">
                        <div className="col-md-6">
                          <p className="text-grey text-left">
                            <img
                              src={subsystem}
                              alt="starlify"
                              className="m-r-15 m-b-5 "
                            />
                            {services.name}
                          </p>
                          {/* <Popover
                            placement="bottom"
                            isOpen={this.state.popoverOpen}
                            target="Popover2"
                            toggle={this.toggle}
                          >
                            {/* <PopoverHeader>Popover Title</PopoverHeader> */}
                          {/* <PopoverBody>
                              {this.state.systemDetails.id}
                            </PopoverBody>
                          </Popover> */}
                        </div>
                        {/* <div className="col-md-6">
                        <p className="text-grey text-left">{services.id}</p>
                      </div> */}
                      </div>
                    );
                  })
                : ""}
            </div>
            <div className="ob-block row">
              {this.state.systemDetails.references ? (
                <p className="text-white text-left col-md-12">References</p>
              ) : (
                ""
              )}
              {this.state.systemDetails.references
                ? this.state.systemDetails.references.map((references, i) => {
                    return (
                      <div className="d-flex w-100">
                        <div className="col-md-6">
                          <p className="text-grey text-left">
                            <img
                              src={subsystem}
                              alt="starlify"
                              className="m-r-15 m-b-5 "
                            />
                            {references.name}
                          </p>
                        </div>
                        {/* <div className="col-md-6">
                        <p className="text-grey text-left">{references.id}</p>
                      </div> */}
                      </div>
                    );
                  })
                : ""}
            </div>
          </div>
        </VerticalScrollbars>
      </div>
    );
  }
}
