import React, { Component } from "react";
import { Col } from "reactstrap";
import photo from "../../_assets/images/photo.jpg";
import Organisation from "../../pages/dashboard/browsers/Organisation";
import Systems from "../../pages/dashboard/browsers/Systems";
import Tagger from "../../pages/dashboard/browsers/Tagger";
import OrganisationDetails from "../../pages/dashboard/browsers/OrganisationDetails";
import SystemDetails from "../../pages/dashboard/browsers/SystemDetails";
import FlowDetails from "../../pages/dashboard/browsers/FlowDetails";
import Userblock from "../../pages/dashboard/Userblock/Userblock";

export default class Header extends Component {
  constructor() {
    super();

    this.state = {
      organisationVisible: false,
      systemsVisible: false,
      taggerVisible: false,
      organisationDetailsVisible: false,
      systemDetailsVisible: false,
      userVisible: false,
      systemId : ''
    };

    this.organisationhandleClick = this.organisationhandleClick.bind(this);
    this.organisationhandleOutsideClick = this.organisationhandleOutsideClick.bind(
      this
    );
    this.systemshandleClick = this.systemshandleClick.bind(this);
    this.systemshandleOutsideClick = this.systemshandleOutsideClick.bind(this);
    this.taggerhandleClick = this.taggerhandleClick.bind(this);
    this.taggerhandleOutsideClick = this.taggerhandleOutsideClick.bind(this);
    this.organisationDetailshandleClick = this.organisationDetailshandleClick.bind(
      this
    );
    this.organisationDetailshandleOutsideClick = this.organisationDetailshandleOutsideClick.bind(
      this
    );
    this.systemDetailshandleClick = this.systemDetailshandleClick.bind(this);
    this.systemDetailshandleOutsideClick = this.systemDetailshandleOutsideClick.bind(
      this
    );
    this.flowDetailshandleClick = this.flowDetailshandleClick.bind(this);
    this.flowDetailshandleOutsideClick = this.flowDetailshandleOutsideClick.bind(
      this
    );
    this.userhandleClick = this.userhandleClick.bind(this);
    this.userhandleOutsideClick = this.userhandleOutsideClick.bind(this);
    this.systemSelected = this.systemSelected.bind(this);
  }

  systemSelected(systemId) {
//debugger;
    //this.setState({ currentSystemIndex: systemId });
    this.setState({ systemId: systemId });
    return fetch(process.env.REACT_APP_API+'model/2d0a2236-3083-4572-ad7b-a8d2ea94c18c/system/'+systemId, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({ systemDetails: responseJson });
      })

  }
  handleClick1() {
    if (!this.state.organisationVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      organisationVisible: !prevState.organisationVisible
    }));
  }
  handleClick2() {
    if (!this.state.systemsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      systemsVisible: !prevState.systemsVisible
    }));
  }
  handleClick3() {
    if (!this.state.taggerVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      taggerVisible: !prevState.taggerVisible
    }));
  }
  handleClick4() {
    if (!this.state.organisationDetailsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      organisationDetailsVisible: !prevState.organisationDetailsVisible
    }));
  }
  handleClick5() {
    if (!this.state.systemDetailsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      systemDetailsVisible: !prevState.systemDetailsVisible
    }));
  }
  handleClick6() {
    if (!this.state.flowDetailsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      flowDetailsVisible: !prevState.flowDetailsVisible
    }));
  }
  handleClick7() {
    if (!this.state.userVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.handleOutsideClick, false);
    } else {
      document.removeEventListener("click", this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      userDetailsVisible: !prevState.userDetailsVisible
    }));
  }
  organisationhandleClick() {
    if (!this.state.organisationVisible) {
      //attach/remove event handler
      document.addEventListener("click", this.organisationhandleClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.organisationhandleClick,
        false
      );
    }

    this.setState(prevState => ({
      organisationVisible: !prevState.organisationVisible
    }));
  }
  systemshandleClick() {
    if (!this.state.systemsVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.systemshandleOutsideClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.systemshandleOutsideClick,
        false
      );
    }

    this.setState(prevState => ({
      systemsVisible: !prevState.systemsVisible
    }));
  }

  taggerhandleClick() {
    if (!this.state.taggerVisible) {
      // attach/remove event handler
      document.addEventListener("click", this.taggerhandleOutsideClick, false);
    } else {
      document.removeEventListener(
        "click",
        this.taggerhandleOutsideClick,
        false
      );
    }

    this.setState(prevState => ({
      taggerVisible: !prevState.taggerVisible
    }));
  }
  organisationDetailshandleClick() {
    if (!this.state.organisationDetailsVisible) {
      // attach/remove event handler
      document.addEventListener(
        "click",
        this.organisationDetailshandleOutsideClick,
        false
      );
    } else {
      document.removeEventListener(
        "click",
        this.organisationDetailshandleOutsideClick,
        false
