import React, { Component } from "react";
import { Col } from "reactstrap";
import photo from "../../_assets/images/photo.jpg";
import Organisation from "../../pages/dashboard/browsers/Organisation";
import Systems from "../../pages/dashboard/browsers/Systems";
import Tagger from "../../pages/dashboard/browsers/Tagger";
import OrganisationDetails from "../../pages/dashboard/browsers/OrganisationDetails";
// import OrgEditor from "../../pages/dashboard/browsers/OrgEditor";
import SystemDetails from "../../pages/dashboard/browsers/SystemDetails";
import FlowDetails from "../../pages/dashboard/browsers/FlowDetails";
import Usermenu from "../usermenu/Usermenu";
import ModalAcess from "../../pages/dashboard/browsers/ModalAcess";
import { connect } from "react-redux";

class Header extends Component {
  constructor(props) {
    super(props);

    this.state = {
      organisationVisible: false,
      systemsVisible: false,
      taggerVisible: false,
      systemShow: false,
      organisationDetailsVisible: false,
      systemDetailsVisible: false,
      userVisible: false,
      systemId: "",
      flowId: "",
      activeState: [],
      activeMenu: "",
      activeMenuDetail: "",
      toggle: {
        modelAccess: false
      }
    };

    //this.handleClick = this.handleClick.bind(this);
    this.menuClick = this.menuClick.bind(this);
    this.menuDetailClick = this.menuDetailClick.bind(this);
    this.userhandleClick = this.userhandleClick.bind(this);
    //this.userhandleOutsideClick = this.userhandleOutsideClick.bind(this);
    this.systemSelected = this.systemSelected.bind(this);
    this.close = this.close.bind(this);
  }

  menuClick(menu) {
    if (this.state.activeMenu === menu) {
      this.setState({ activeMenu: "" });
    } else {
      this.setState({ activeMenu: menu });
      //alert(this.state.activeMenuDetail)
      if (this.state.activeMenuDetail !== "tagger") {
        this.setState({ activeMenuDetail: "" });
      }
    }

    if (menu === "system") {
      this.setState({ systemId: "" });
      this.setState({ flowId: "" });
      this.setState(prevState => ({
        systemShow: !prevState.systemShow,
        organisationVisible: false,
        systemDetailsVisible: false,
        flowDetailsVisible: false,
        organisationDetailsVisible: false
        //taggerVisible : false
      }));

      // this.setState({ organisationVisible: false });
    } else if (menu === "organisation") {
      this.setState({ systemId: "" });
      this.setState({ flowId: "" });
      this.setState(prevState => ({
        organisationVisible: !prevState.organisationVisible,
        systemShow: prevState.organisationVisible,
        systemDetailsVisible: false,
        flowDetailsVisible: false,
        organisationDetailsVisible: false
      }));
      this.setState({ systemShow: false });
      this.setState({ systemDetailsVisible: false });
      this.setState({ flowDetailsVisible: false });
    }
  }

  menuDetailClick(menu) {
    this.setState({ activeMenuDetail: menu });
    if (menu === "tagger") {
      this.setState(prevState => ({
        taggerVisible: !prevState.taggerVisible
      }));
      this.setState({ systemDetailsVisible: false });
      this.setState({ organisationDetailsVisible: false });
      this.setState({ flowDetailsVisible: false });
    } else if (menu === "systemDetail" && this.props.activeSystem) {
      this.setState(prevState => ({
        systemDetailsVisible: !prevState.systemDetailsVisible
      }));
      this.setState({ taggerVisible: false });
      this.setState({ organisationDetailsVisible: false });
      this.setState({ flowDetailsVisible: false });
    } else if (menu === "flowDetail" && this.props.activeFlow) {
      this.setState(prevState => ({
        flowDetailsVisible: !prevState.flowDetailsVisible
      }));
      this.setState({ taggerVisible: false });
      this.setState({ organisationDetailsVisible: false });
      this.setState({ systemDetailsVisible: false });
    } else if (menu === "orgDetail") {
      this.setState(prevState => ({
        organisationDetailsVisible: !prevState.organisationDetailsVisible
      }));
      this.setState({ taggerVisible: false });
      this.setState({ flowDetailsVisible: false });
      this.setState({ systemDetailsVisible: false });
    }
  }

  close(menu, type) {
    if (type === "menu") this.setState({ activeMenu: "" });
    else this.setState({ activeMenuDetail: "" });
    if (menu === "system") {
      this.setState(prevState => ({
        systemShow: !prevState.systemShow
      }));
    } else if (menu === "organisation") {
      this.setState(prevState => ({
        organisationVisible: !prevState.organisationVisible
      }));
    } else if (menu === "tagger") {
      this.setState(prevState => ({
        taggerVisible: !prevState.taggerVisible
      }));
    } else if (menu === "orgDetail") {
      this.setState(prevState => ({
        organisationDetailsVisible: !prevState.organisationDetailsVisible
      }));
    } else if (menu === "systemDetail") {
      this.setState(prevState => ({
        systemDetailsVisible: !prevState.systemDetailsVisible
      }));
    } else if (menu === "flowDetail") {
      this.setState(prevState => ({
        flowDetailsVisible: !prevState.flowDetailsVisible
      }));
    } else if (menu === "modelAccess") {
      this.setState(prev => ({
        toggle: {
          modelAccess: !prev.toggle.modelAccess
        }
