import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { fetchSystemDetils } from "../../../store/system/systemActions";
import { fetchModel } from '../../../store/model/modelActions'
import { connect } from "react-redux";
import { Collapse, Modal, ModalBody, Tooltip, Input, Label, Button } from "reactstrap";


class SystemDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      collapse: true,
      systemCollapseId: false,
      subSystemCollapseId: false,
      modal: false,
      editSubSystem : false,
      details: {},
      formData: {
        name: "",
        description: "",
        supportLevel: ""
      },
    };
    this.close = this.close.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.toggle = this.toggle.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
    this.updateSystem = this.updateSystem.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.addUiToggle = this.addUiToggle.bind(this);
  }

  //manage add more system/flow text box to ui
  addUiToggle(event, type) {
    if (type === 'system') {
        this.setState(state => ({ systemAdd: !state.systemAdd }));
    } else if (type === 'service') {
      this.setState(state => ({ serviceAdd: !state.serviceAdd }));
    }
    else if (type === 'reference') {
      this.setState(state => ({ referenceAdd: !state.referenceAdd }));
    }
}

editfield(key, title, type){
  if(type == 'subsystem') {
    this.setState({ ['subsystem-'+ key]: true });
    this.setState({ ['subsystem-value-'+ key]: title });
  } else if(type == 'service') {
    this.setState({ ['service-'+ key]: true });
    this.setState({ ['service-value-'+ key]: title });
  } else if(type == 'reference') {
    this.setState({ ['reference-'+ key]: true });
    this.setState({ ['reference-value-'+ key]: title });
  }

}

editFieldChange(key, type, event){
  if(type == 'subsystem') {
    this.setState({ ['subsystem-'+ key]: true });
    this.setState({ ['subsystem-value-'+ key]: event.target.value });
  } else if(type == 'service') {
    this.setState({ ['service-'+ key]: true });
    this.setState({ ['service-value-'+ key]: event.target.value });
  } else if(type == 'reference') {
    this.setState({ ['reference-'+ key]: true });
    this.setState({ ['reference-value-'+ key]: event.target.value });
  }
}

updateEntity(key, uri, type){
  var value = '';
  var id = '';
  if(type === 'system'){
    let splitArray = uri.split("/");
    if(splitArray.length > 0)
      id = splitArray[4]
    this.setState({ ['subsystem-'+ key]: false });
    value = this.state[`subsystem-value-${key}`]
  } else if(type == 'service') {
    this.setState({ ['service-'+ key]: false });
    id = uri
    value = this.state[`service-value-${key}`]
  } else if(type == 'reference') {
    id = uri
    this.setState({ ['reference-'+ key]: false });
    value = this.state[`reference-value-${key}`]
  }





            fetch(
                process.env.REACT_APP_API + `/model/${this.props.activeModelId}/${type}/` + id,
                {
                    method: "PATCH",
                    headers: {
                        "Content-Type": "application/json"
                    },
                    body: JSON.stringify({ name: value })
                }
            )
                .then(response => {
                  this.props.getSystems(this.props.activeModelId)
                    this.props.getSystemDetails(this.props.activeModel,this.props.activeSystem); 
                }
                )

}

getAllSystems() {
  return fetch(process.env.REACT_APP_API + '/hypermedia/network/' + this.props.activeModelId + '/allsystems')
    .then(res => res.json())
    .then(response => {
      this.setState({ 'systems': response })
    })
    .catch(error => console.log(error));
}

//create Sub System
createSubSystem(event) {
  let value = event.target.value;
  if (event.key === 'Enter') {
      event.target.value = '';
      fetch(
          process.env.REACT_APP_API + `/hypermedia/network/${this.props.activeModelId}/system/${this.props.activeSystem}/system`,
          { method: "POST", headers: { "Content-Type": "application/json" } }
      )
          .then(response => response.json())
          .then(responseJson => {
              return fetch(
                  process.env.REACT_APP_API + `/hypermedia/network/${this.props.activeModelId}/system/` + responseJson.id,
                  { method: "PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify({ name: value }) }
              )
                  .then(response => { 
                    this.props.getSystems(this.props.activeModelId)
                    this.props.getSystemDetails(this.props.activeModel,this.props.activeSystem); 
                  })

          });
  }
}

//create service
createService(event) {
  let value = event.target.value;
  if (event.key === 'Enter') {
      event.target.value = '';
      fetch(
          process.env.REACT_APP_API + `/model/${this.props.activeModelId}/system/${this.props.activeSystem}/service`,
          { method: "POST", headers: { "Content-Type": "application/json" } }
      )
          .then(response => response.json())
          .then(responseJson => {
              return fetch(
                  process.env.REACT_APP_API + `/model/${this.props.activeModelId}/service/` + responseJson.id,
                  { method: "PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify({ name: value }) }
              )
                  .then(response => { 
                    this.props.getSystems(this.props.activeModelId)
                    this.props.getSystemDetails(this.props.activeModel,this.props.activeSystem); 
                  })

          });
  }
}

//create reference
createReference(event) {
  let value = event.target.value;
  if ((event.key === 'Enter' || event.type === 'blur') && value && this.state.serviceId) {
      event.target.value = '';
      fetch(
          process.env.REACT_APP_API + `${this.props.activeModel}/system/${this.props.activeSystem}/reference/${this.state.serviceId}`,
          {
              method: "POST",
              headers: {
                  "Content-Type": "application/json"
              },
              body: JSON.stringify({ name: value })
          }
      )
          .then(response => response.json())
          .then(responseJson => {
              return fetch(
                  process.env.REACT_APP_API + `${this.props.activeModel}/reference/` + responseJson.id,

                  {
                      method: "PATCH",
                      headers: {
                          "Content-Type": "application/json"
                      },
                      body: JSON.stringify({ name: value })
                  }
              )
                  .then(response => {
                      this.props.getSystems(this.props.activeModelId)
                      this.props.getSystemDetails(this.props.activeModel, this.props.activeSystem)
                      this.getAllSystems();
                  }
                  )
          });
  }
}

//Delete sub system
getSubSystemId(Uri, type) {
  let splitArray = Uri.split("/");
  if(splitArray.length > 0) {
    this.deleteEntity(splitArray[4])
  }
}

//Delete entity
deleteEntity(id) {
  return fetch(
    process.env.REACT_APP_API + `/model/${this.props.activeModelId}/modeled/${id}`,
    {
        method: "DELETE",
        headers: {
            "Content-Type": "application/json"
        }
    }
)
    .then(response => {
        this.props.getSystems(this.props.activeModelId)
        this.props.getSystemDetails(this.props.activeModel, this.props.activeSystem)
    });
}
//get systems and services list for adding new reference
getSystemService(event) {
  let parents = this.state.systems;
  let parent = parents.filter(parent => parent.name === event.target.value);
  if (parent)
      this.setState({ servicesList: parent[0].services });
}
  handleChange(event) {
    const { formData } = this.state;
    formData[event.target.name] = event.target.value;
    this.setState({
      formData
    });
  }

  serviceHandleChange(event) {
    this.setState({ serviceId: event.target.value })
}

  toggle = target => {
    this.setState({
      ...this.state,
      [target]: !this.state[target]
    });
  };

  componentWillReceiveProps(nextProps) {
    if (this.props.activeSystem !== nextProps.activeSystem)
      this.props.getSystemDetails(this.props.activeModel, nextProps.activeSystem);
  }

  componentWillMount() {
    this.props.getSystemDetails(this.props.activeModel, this.props.activeSystem);
    this.getAllSystems();
  }

  updateSystem() {
      this.setState(prevState => ({
        modal: !prevState.modal
      }));
      fetch(
        process.env.REACT_APP_API + `/hypermedia/network/${this.props.activeModelId}/system/` + this.props.systemDetails.id,
        { method: "PATCH", headers: { "Content-Type": "application/json" }, body: JSON.stringify(this.state.formData) }
      )
        .then(response =>
          this.props.getSystemDetails(this.props.activeModel, this.props.systemDetails.id)
        )
        .catch(error => console.log(error));
  }

  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  //Closes the system details section
  close(menu, type) {
    this.props.close(menu, type);
  }

  //control right click modal
  modalToggle(e, data) {
    this.setState({
      formData: {
        name : this.props.systemDetails.name,
        description : this.props.systemDetails.description
      }
    });
    this.setState(prevState => ({
      modal: !prevState.modal
    }));

    if (data) {
      this.setState({
        details: { description: data.description }
      });
    }
  }

  render() {
    var { formData, systems } = this.state;
    return (
      <div className="org_browser">
        <Modal isOpen={this.state.modal} toggle={this.modalToggle}>
          <ModalBody>

          <div className="browser_wrapper">
            <div className="org-selector">
              <p className="text-white text-left"></p>
            </div>
            <div className="ob-block row">
              <div className="col-md-6 m-b-20">
                <p className="text-white text-left">
                  <span id="system-detail-name">
                    <Label for="name">Name</Label>
              <Input
                type="text"
                name="name"
                className="text-input"
                value= {formData.name}
                onChange={this.handleChange}
              />
                  </span>


                </p>

              </div>

              <Label for="name">Description</Label>
                <p className="text-grey">
                <Input
                type="text"
                name="description"
                className="text-input"
                value= {formData.description || ''}
                onChange={this.handleChange}
              />

                </p>

            </div>

            <div className="ob-block row">
              <p className="text-white text-left col-md-12">Sub systems</p>
              {!this.state.systemAdd && (
                                        <i
                                            className="fas fa-plus system-down-icon"
                                            id="system"

                                            onClick={(e) => {
                                                this.addUiToggle(e, 'system')
                                            }}
                                        />)}


                                {this.state.systemAdd && (<div className="extra-add">
                                    <input
                                        type="text"
                                        id="system"
                                        onKeyDown={this.createSubSystem.bind(this)}

                                    />
                                    <button type="button" onClick={(e) => {
                                        this.addUiToggle(e, 'system')
                                    }}>
                                        <i className="fas fa-times" />
                                    </button>
                                </div>)}


              {this.props.systemDetails.subsystemLinks
                ? this.props.systemDetails.subsystemLinks.map(
                    (subsystem, i) => {
                      return (
