                                    <input
                                        type="text"
                                        id="system"
                                        onKeyDown={this.createService.bind(this)}

                                    />
                                    <button type="button" onClick={(e) => {
                                        this.addUiToggle(e, 'service')
                                    }}>
                                        <i className="fas fa-times" />
                                    </button>
                                </div>)}

              {this.props.systemDetails.services
                ? this.props.systemDetails.services.map((service, i) => {
                    return (
                      <div key={service.id} className="d-flex w-100">
                        <div className="col-md-12">
                          <p className="text-grey text-left">
                          {!this.state[`service-${i}`] && (
                                <span>{service.name}</span> 
