              {this.props.systemDetails.references
                ? this.props.systemDetails.references.map((reference, i) => {
                    return (
                      <div key={i} className="d-flex w-100">
                        <div className="col-md-12">
                        <p className="text-grey text-left">
                          {!this.state[`reference-${i}`] && (
                                <span>{reference.name}</span> 
                              )}
                              </p>
                              {this.state[`reference-${i}`] && (
                                <div>
                                <input
                                type="text"
                                id="reference"
                                onChange={this.editFieldChange.bind(this,i , 'reference')}
                                value={this.state[`reference-value-${i}`]}

                            />
                            <i className="fas fa-check" onClick={this.updateEntity.bind(this,i , reference.id, 'reference')}></i></div>
                              )}
                              <span><i className="far fa-edit edit-icon" onClick={this.editfield.bind(this,i , reference.name, 'reference')}></i></span>
                              <span><br></br><i className="fas fa-trash"  onClick={this.deleteEntity.bind(this, reference.id)}></i></span>


