import React, { Component } from 'react';
import * as d3 from 'd3';

import "../../../_assets/css/Graph.css";

class Graph extends Component {

  componentDidMount(){

    const width = window.innerWidth - 100,
        height = window.innerHeight - 100;

    var sysJson = require("../../../_temp/systems1.json");

    var systems = [];
    var i = 0;
    sysJson.topLevelSystems.forEach(function(data) {
      try {
        systems[i]= {"name": data.name, "type": data.modeledEntityType};
        var j = 0;
        systems[i]["points"] = [];
        let svcJson = require("../../../_temp/systems/"+data.modeledEntityId+".json");
        svcJson.providedEndpoints.forEach(function(point) {
          systems[i]["points"][j]= {"name": point.name, "type": point.modeledEntityType};

          j++;
        })
      } catch (err) {
        systems[i]= {"name": data.name, "type": data.modeledEntityType};
      }

      i++;
    });

    var x = 0;
    var items = [];
    items["nodes"] = [];
    items["links"] = [];
    systems.forEach(function(elem, key){
      var source = x;
      items["nodes"][x] = {"name": elem.name, "type": elem.type};
      if(elem.points){
        elem.points.forEach(function(point){
          var target = ++x;
          items["nodes"][target] = {"name": point.name, "type": point.type};
          items["links"].push({"source": source, "target": target});
        })
      }
      ++x;
    });

    console.log(items);

    const zoom = d3.zoom()
                  .scaleExtent([1, 15])
                  .on("zoom", zoomed);

    var svg = d3.select(".forcegraph").append("svg")
        .attr("width", width)
        .attr("height", height)
        .call(zoom)
        .append("g")
        .attr("transform", "translate(40,0)");

    var simulation = d3.forceSimulation()
      .force("link", d3.forceLink().id(function(d,i) {
        return i;
      }).distance(50))
      .force("charge", d3.forceManyBody().strength(-50 ))
      .force('gravity', d3.forceRadial(0, 0))
      .force("center", d3.forceCenter(width / 2, height / 2));

    var links = svg.selectAll(".link")
      .data(items.links)
      .enter().append("line")
      .attr("class", "link");

    var nodes = svg.selectAll(".node")
      .data(items.nodes)
      .enter().append("g")
      .attr("class", "node")
      .call(d3.drag()
        .on("start", dragstarted)
        .on("drag", dragged)
        .on("end", dragended)
      );

    var circle = nodes.append("circle")
      .attr("r", 2);

    var label = nodes.append("text")
      .attr("dy", ".35em")
      .text(function(d) { return d.name; });

    svg.append('defs').append('marker')
        .attr('id', 'Service')
        .attr('viewBox', '-15 -5 20 20')
        .attr('refX', 12)
        .attr('refY', 0)
        .attr('orient', 'auto')
        .attr('markerWidth', 50)
        .attr('markerHeight', 50)
        .attr('xoverflow', 'visible')
        .append('svg:path')
        .attr('d', 'M -15 -5 L 0 0 L -15 5 z')
        .attr('fill', 'White')
        .style('stroke','none');

      svg.append('defs').append('marker')
        .attr('id', 'Reference')
        .attr('viewBox', '-0 -5 10 10')
        .attr('refX', 25)
        .attr('refY', 0)
        .attr('orient', 'auto')
        .attr('markerWidth', 20)
        .attr('markerHeight', 20)
        .attr('xoverflow', 'visible')
        .append('svg:path')
        .attr('d', 'M 15 -5 L 0 0 L 15 5 z')
        .attr('fill', 'Green')
        .style('stroke','none');

      simulation
        .nodes(items.nodes)
        .on("tick", tick);
      simulation
        .force("link")
        .links(items.links);

    function tick() {

      links
        .attr("x1", function (d) { return d.source.x; })
        .attr("y1", function (d) { return d.source.y; })
        .attr("x2", function (d) { return d.target.x; })
        .attr("y2", function (d) { return d.target.y; })
        .attr("marker-end", function(d) { return "url(#" + d.target.type + ")"; });

      circle
        .attr("cx", function(d) { return d.x; })
        .attr("cy", function(d) { return d.y; })
        .attr("r", function(d) {  return d.type === "System" ? 8 : 3; });

      label
        .attr("x", function(d) { return d.x + 8; })
        .attr("y", function(d) { return d.y; });
    }


    function zoomed() {
        svg.attr('transform', 'translate(' + d3.event.transform.x + ',' + d3.event.transform.y + ') scale(' + d3.event.transform.k + ')');
    }

    function dragstarted(d) {
        if (!d3.event.active) simulation.alphaTarget(0.3).restart();
        d.fx = d.x;
        d.fy = d.y;
    }

    function dragged(d) {
        d.fx = d3.event.x;
        d.fy = d3.event.y;
    }

    function dragended(d) {
        if (!d3.event.active) simulation.alphaTarget(0);
        d.fx = null;
        d.fy = null;
    }

  }

  render() {
    return (
      <div className="forcegraph"></div>
    )
  }
}

export default Graph;
