import React, { Component } from "react";
import VerticalScrollbars from "../../../_assets/Scrollbars/VerticalScrollbars";
import { fetchFlowDetils } from "../../../store/system/systemActions";
import { connect } from "react-redux";
import { ContextMenu, MenuItem } from "react-contextmenu";
import { Collapse, Modal, ModalBody, Tooltip } from "reactstrap";

class FlowDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      details: {}
    };

    this.close = this.close.bind(this);
    this.toggle = this.toggle.bind(this);
    this.toggleDescription = this.toggleDescription.bind(this);
    this.modalToggle = this.modalToggle.bind(this);
  }

  //show or hide tool tip
  toggle = target => {
    this.setState({
      ...this.state,
      [target]: !this.state[target]
    });
  };

  //show or hide description
  toggleDescription() {
    this.setState(state => ({ collapse: !state.collapse }));
  }

  //closes the detail window
  close(type) {
    this.props.close(type);
  }

  //get flow details
  componentWillMount() {
    this.props.getFlowDetails(this.props.activeModel, this.props.activeFlow);
  }

  //update flow details
  updateFlowDetails(event) {
    let value = event.target.value;
    if (
      (event.key === "Enter" || event.type === "blur") &&
      this.state.modal &&
      value
    ) {
      event.target.value = "";
      this.setState(prevState => ({
        modal: !prevState.modal
      }));
      fetch(
        process.env.REACT_APP_API +
          `${this.props.activeModel}/flow/` +
          this.props.activeFlow,
        {
          method: "PATCH",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ description: value })
        }
      )
        .then(response => {
          this.props.getFlowDetails(
            this.props.activeModel,
            this.props.activeFlow
          );
        })
        .catch(error => console.log(error));
    }
  }

  //control right click modal
  modalToggle(e, data) {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));

    if (data) {
      this.setState({
        details: { description: data.description }
      });
    }
  }

  render() {
    return (
      <div className="browser">
        <Modal isOpen={this.state.modal} toggle={this.modalToggle}>
          <ModalBody>
            <div>
              <textarea
                onBlur={this.updateFlowDetails.bind(this)}
                onKeyDown={this.updateFlowDetails.bind(this)}
                defaultValue={this.props.flowDetails.description}
              />
            </div>
          </ModalBody>
        </Modal>
        <ContextMenu id="flowdetail_right_click">
          <MenuItem data={{ itemType: "rename" }} onClick={this.modalToggle}>
            Update
          </MenuItem>
        </ContextMenu>
        <div className="browser_header text-white">
          <div>
            <i className="fas fa-key submenu-icon"></i>
            <h3 className="d-inline-block">Flow Details</h3>
            <i
              className="far fa-edit"
              onClick={() => this.modalToggle(this, this.props.flowDetails)}
            ></i>
            <i
              className="fas fa-times system-down-icon browser_close"
              onClick={() => this.close("flowDetail")}
            />
          </div>
        </div>

        <div className="browser_wrapper browser_body">
          <div className="name m-b-20">
            <p className="text-grey">Name</p>
            <p className="text-white">Test Flow 1</p>
          </div>
          <div className="support m-b-20">
            <p className="text-grey">Support level</p>
            <p className="text-white">Gold</p>
          </div>
          <div className="url m-b-20">
            <p className="text-grey">Documentation URL</p>
            <p className="text-white">https://:confluence.entiros.se/xOU90</p>
          </div>
          <div className="desc m-b-20">
            <p className="text-grey">Description</p>
            <p className="text-white">Description</p>
          </div>
          <div className="system m-b-20">
            <p className="text-grey">System</p>
            <p className="text-white">Test System</p>
          </div>
          <div className="target m-b-20">
            <p className="text-grey">Target</p>
            <p className="text-white">Test System</p>
            <p className="text-white">Test System</p>
          </div>
          <div className="ob-block row">
            <div className="col-md-6 m-b-20">
              <p className="text-white text-left">
                <span id="flow-detail-name">{this.props.flowDetails.name}</span>

                <i
                  className="fas fa-chevron-down system-down-icon"
                  onClick={this.toggleDescription}
                />
              </p>
              <Tooltip
                placement="top"
                toggle={() => this.toggle(`flow-detail-name`)}
                isOpen={this.state[`flow-detail-name`]}
                autohide={false}
                target={`flow-detail-name`}
              >
                {this.props.flowDetails.id}
              </Tooltip>
            </div>
            <div className="col-md-6 m-b-20">
              <p className="text-white text-left">
                Support lvl: {this.props.flowDetails.supportLevel}
              </p>
            </div>
            <Collapse
              isOpen={this.state.collapse}
              className="systemDetailsDisc"
            >
              <p className="text-grey">
                {this.props.flowDetails.description
                  ? this.props.flowDetails.description
                  : "No Description"}
              </p>
            </Collapse>
          </div>

          <div className=" row">
            <div className="col-md-4">
              <p className="text-white text-left">System</p>
            </div>
