import React, { Component } from 'react';
import * as d3 from 'd3';
import { connect } from 'react-redux';

class Graph extends Component {

  constructor(){
    super();
    this.buildForceGraph = this.buildForceGraph.bind(this);
    this.state = {
      load_graph: false,
      systems: []
    }
  }

  buildForceGraph = (systems) => {
    new Promise(async function(resolve, reject) {
        var items = [];
        items["nodes"] = [];
        items["links"] = [];

        for (let i = 0; i < systems.length; i++) {
          const system = systems[i];
          items["nodes"][i] = { "id": system.id, "name": system.name  || "System", "type": "system", "radius": 15 };
          if (system.services){
            system.services.forEach(function(service){
              var j = ++i;
              items["nodes"][j] = { "id": service.id, name: service.name || "Service", "type": "service", "radius": 5 };
              items["links"].push({"source": system.id, "target": service.id, "type": "service"});
            })
          }

          // if (system.references){
          //   for (let x = 0; x < system.references.length; x++) {
          //     const reference = system.references[x];
          //     await fetch(process.env.REACT_APP_API + "/model/6c161cbe-0d86-413b-8ea1-0bbb2ba9e5d6/reference/" + reference.id)
          //     .then(res => res.json())
          //     .then((result) => {
          //       if (result.service !== undefined) {
          //         items["links"].push({"source": system.id, "target": result.service.id, "type": "reference"});
          //       }
          //     })
          //   }
          // }
        }

        resolve(items);

      }).then(function(items) {
        const width = window.innerWidth,
          height = window.innerHeight - 100;

        const zoom = d3.zoom()
                    .scaleExtent([0.5, 6])
                    .on("zoom", zoomed);

        var parent = d3.select(".forcegraph");
        parent.selectAll("*").remove();
        var svg = parent.append("svg")
            .attr("width", width)
            .attr("height", height)
            .call(zoom)
            .append("g")
            .attr("transform", "translate(0,0)");

          svg.append('defs').append('marker')
            .attr('id', 'service')
            .attr('viewBox', '-5 -5 20 20')
            .attr('refX', 8)
            .attr('refY', 0)
            .attr('orient', 'auto')
            .attr('markerWidth', 25)
            .attr('markerHeight', 25)
            .attr('xoverflow', 'visible')
            .append('svg:path')
            .attr('d', 'M -5 -2 L 0 0 L -5 2 z')
            .attr('fill', '#fff')
            .style('stroke','none');

        var simulation = d3.forceSimulation()
          .force("link", d3.forceLink().id(function(d,i) {
            return d.id;
          })
          .distance(function(d) {
            return d.type === "reference" ? 100 : 10;
          }))
          .force("charge", d3.forceManyBody().strength(-250 ))
          .force('gravity', d3.forceRadial(0, 0))
          .force('x', d3.forceX().strength(0.01))
          .force('y', d3.forceY().strength(0.01))
          .force('collide', d3.forceCollide(0))
          .force("center", d3.forceCenter(width / 2, height / 2));

        var links = svg.selectAll(".link")
          .data(items.links)
          .enter().append("line")
          .attr("class", "link");

        var nodes = svg.selectAll(".node")
          .data(items.nodes)
          .enter().append("g")
          .attr("class", "node")
          .call(d3.drag()
            .on("start", dragstarted)
            .on("drag", dragged)
            .on("end", dragended)
          );

        var circle = nodes.append("circle");

        var label = nodes.append("text")
          .attr("dy", ".35em")
          .attr("class", function(d) { return d.type; })
          .text(function(d) { return d.name; });

        simulation
          .nodes(items.nodes)
          .on("tick", tick);

        simulation
          .force("link")
          .links(items.links);

        function tick() {
          links
            .attr("x1", function (d) { return d.source.x; })
            .attr("y1", function (d) { return d.source.y; })
            .attr("x2", function (d) { return d.target.x; })
            .attr("y2", function (d) { return d.target.y; })
            .attr("marker-end", function(d) {
              return d.type === "reference" ? "url(#service)" : "";
            });

          circle
            .attr("id", function(d) { return "graph_"+d.id; })
            .attr("cx", function(d) { return d.x; })
            .attr("cy", function(d) { return d.y; })
            .attr("class", function(d) { return d.type; })
            .attr("r", weightRadius);

          label
            .attr("x", function(d) { return d.x; })
            .attr("y", function(d) { return d.y; });
        }

        function weightRadius(d) {
          var r = d.radius;
          return r;
        }

        function zoomed() {
            svg.attr('transform', 'translate(' + d3.event.transform.x + ',' + d3.event.transform.y + ') scale(' + d3.event.transform.k + ')');
        }

        function dragstarted(d) {
            if (!d3.event.active) simulation.alphaTarget(0.3).restart();
            d.fx = d.x;
            d.fy = d.y;
        }

        function dragged(d) {
            d.fx = d3.event.x;
            d.fy = d3.event.y;
        }

        function dragended(d) {
            if (!d3.event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
        }

      });

  }

  componentDidUpdate(){
    const { model, status } = this.props;    
    if(model.systems !== undefined){
      if(status !== null & status === "success"){
        this.buildForceGraph(model.systems)
      }else{
        this.buildForceGraph([]);
      }
    }else{
      this.buildForceGraph([]);
    }

    var activeSys = document.getElementById("graph_"+this.props.activeSystem)
    if(activeSys !== null){
      var cols = document.getElementsByClassName('system');
      for(var i = 0; i < cols.length; i++) {
        cols[i].style.fill = '';
      }
      activeSys.setAttribute("style", "fill:#e8455c");
    }
  }

  render() {
    return (
        <div className="forcegraph"></div>
    )
  }
}

function mapStateToProps(state){
  return{
    activeSystem: state.model.activeSystem,
    model: state.model.items,
    loading: state.model.loading,
    error: state.model.error,
    status: state.model.status,
  }
}

export default connect(mapStateToProps)(Graph);
