import React, {useState, useEffect, useContext} from 'react';
import LocalStorage from "../lib/utilities/storage/LocalStorage";
import fetchJSONDataFromURL from "../lib/utilities/fetchJSONDataFromURL";
import {DomainContext} from "./Domain";
const BrowserContext = React.createContext();
function getSystemAtLevel(systems = [], indexes) {
/*    console.log('systems', systems);
    console.log('indexes', indexes);
    console.log('systems', systems, 'indexes', indexes);*/
    let newIndexes = [...indexes];
    let newSystems = [...systems];

    let firstIndex = newIndexes.shift();
    let system = newSystems[firstIndex] || {};
    if (newIndexes.length === 0) {
        return {
            ...newSystems[firstIndex]
        };
    }
    let {subSystems = []} = system;
    if (!subSystems) {
        return {};
    }
    return getSystemAtLevel(subSystems, newIndexes);
}
function populateSystems(newData, systems, indexes) {
    /*  console.log('newData', newData);
      console.log('systems', systems);
      console.log('indexes', indexes);*/
    if (indexes.length === 0) {
        return newData;
    }
    let newIndexes = [...indexes];
    let newSystems = [...systems];
    let firstIndex = newIndexes.shift();
    newSystems[firstIndex].subSystems = populateSystems(newData, newSystems[firstIndex].subSystems, newIndexes);
    return newSystems;
}
function BrowserProvider(props) {
    let [browserData, setBrowserData] = useState([]);

    // filtered browser context should have browserdata indexes properly and always edit main data and not filtered data with these.
    // Filtered should ONLY be a filter and nothing more
    return <BrowserContext.Provider
        value={{
            state: browserData,
            setBrowserData: setBrowserData,
            populateSystems: (newData, indexes) => {
                let newBrowserData = populateSystems(newData, browserData, indexes);
                setBrowserData(newBrowserData);
            },
            getSystemAtLevel: indexes => getSystemAtLevel(browserData, indexes)
        }}
    >
        {props.children}
    </BrowserContext.Provider>;
}
export {BrowserContext, BrowserProvider};
